import React from 'react';
import styled, { keyframes } from 'styled-components';
import { FaShieldAlt, FaAward, FaThumbsUp, FaTools } from 'react-icons/fa';
import { useInView } from 'react-intersection-observer';

// Define trust factors and their icons
const trustItems = [
  {
    id: 1,
    icon: <FaShieldAlt />,
    title: 'Trusted Security',
    description: 'We implement the highest industry standards for secure smart home integration.',
  },
  {
    id: 2,
    icon: <FaAward />,
    title: 'Certified Experts',
    description: 'Our team consists of certified professionals with years of experience.',
  },
  {
    id: 3,
    icon: <FaThumbsUp />,
    title: 'Satisfaction Guaranteed',
    description: 'We stand behind our work with a 100% customer satisfaction guarantee.',
  },
  {
    id: 4,
    icon: <FaTools />,
    title: 'Premium Installation',
    description: 'We use only the highest quality materials and installation techniques.',
  },
];

// Animations
const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const shimmer = keyframes`
  0% {
    background-position: -200px 0;
  }
  100% {
    background-position: 200px 0;
  }
`;

const float = keyframes`
  0% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
  100% {
    transform: translateY(0px);
  }
`;

// Main section container
const TrustSection = styled.section`
  padding: 120px 0;
  background-color: #0a0a0a;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    width: 600px;
    height: 600px;
    background: radial-gradient(circle, rgba(203, 157, 118, 0.1) 0%, rgba(10, 10, 10, 0) 70%);
    bottom: -300px;
    left: -300px;
    z-index: 0;
  }
`;

const BackgroundPattern = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: 
    linear-gradient(90deg, rgba(203, 157, 118, 0.03) 1px, transparent 1px),
    linear-gradient(0deg, rgba(203, 157, 118, 0.03) 1px, transparent 1px);
  background-size: 40px 40px;
  z-index: 0;
  opacity: 0.2;
`;

// Container for content alignment
const Container = styled.div`
  max-width: 1300px;
  margin: 0 auto;
  padding: 0 50px;
  position: relative;
  z-index: 1;

  @media (max-width: 768px) {
    padding: 0 30px;
  }
`;

// Section heading with gradient and animation
const SectionTitle = styled.h2<{ inView: boolean }>`
  font-size: 0.9rem;
  text-transform: uppercase;
  letter-spacing: 3px;
  color: rgba(203, 157, 118, 0.9);
  margin-bottom: 15px;
  position: relative;
  display: inline-block;
  opacity: 0;
  transform: translateY(20px);
  transition: all 0.8s cubic-bezier(0.215, 0.61, 0.355, 1);

  ${props => props.inView && `
    opacity: 1;
    transform: translateY(0);
  `}

  &::after {
    content: '';
    position: absolute;
    bottom: -8px;
    left: 0;
    width: 40px;
    height: 2px;
    background: linear-gradient(135deg, rgba(203, 157, 118, 0.9), rgba(178, 132, 93, 0.9));
    transform-origin: left;
  }
`;

// Main heading with animation
const MainHeading = styled.h3<{ inView: boolean }>`
  font-size: clamp(2rem, 4vw, 2.5rem);
  margin-bottom: 60px;
  color: #fff;
  font-weight: 300;
  max-width: 650px;
  line-height: 1.3;
  opacity: 0;
  transform: translateY(20px);
  transition: all 0.8s cubic-bezier(0.215, 0.61, 0.355, 1);
  transition-delay: 0.2s;

  ${props => props.inView && `
    opacity: 1;
    transform: translateY(0);
  `}

  span {
    background: linear-gradient(135deg, rgba(203, 157, 118, 0.9), rgba(178, 132, 93, 0.9));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    font-weight: 400;
    position: relative;

    &::after {
      content: '';
      position: absolute;
      bottom: 0;
      left: 0;
      width: 100%;
      height: 6px;
      background-color: rgba(203, 157, 118, 0.1);
      z-index: -1;
      border-radius: 10px;
    }
  }
`;

// Grid for trust items
const TrustGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 30px;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

// Individual trust item card
const TrustItemCard = styled.div<{ index: number, inView: boolean }>`
  background: rgba(30, 30, 30, 0.4);
  border: 1px solid rgba(203, 157, 118, 0.1);
  backdrop-filter: blur(8px);
  padding: 40px 30px;
  border-radius: 12px;
  transition: all 0.4s ease;
  position: relative;
  overflow: hidden;
  opacity: 0;
  transform: translateY(30px);
  transition-delay: ${props => 0.2 + props.index * 0.1}s;
  transition-property: transform, opacity, box-shadow, border-color;

  ${props => props.inView && `
    opacity: 1;
    transform: translateY(0);
  `}

  &:hover {
    transform: translateY(-10px);
    border-color: rgba(203, 157, 118, 0.3);
    box-shadow: 
      0 20px 40px rgba(0, 0, 0, 0.2),
      0 0 30px rgba(203, 157, 118, 0.1);

    &::before {
      opacity: 0.1;
    }
  }

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, rgba(203, 157, 118, 0.05), transparent);
    opacity: 0;
    transition: opacity 0.4s ease;
  }
`;

// Icon container with glow effect
const IconContainer = styled.div`
  width: 60px;
  height: 60px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 20px;
  position: relative;
  background: linear-gradient(135deg, rgba(203, 157, 118, 0.2), rgba(0, 0, 0, 0));
  border: 1px solid rgba(203, 157, 118, 0.3);

  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 100%;
    border-radius: 50%;
    background: radial-gradient(circle, rgba(203, 157, 118, 0.15), transparent 70%);
    filter: blur(10px);
    z-index: -1;
  }

  svg {
    width: 24px;
    height: 24px;
    color: rgba(203, 157, 118, 1);
  }
`;

// Trust item title
const TrustTitle = styled.h4`
  font-size: 1.4rem;
  margin: 0 0 15px;
  color: #fff;
  font-weight: 500;
`;

// Trust item description
const TrustDescription = styled.p`
  font-size: 0.95rem;
  color: rgba(255, 255, 255, 0.7);
  line-height: 1.6;
  margin: 0;
`;

// Trust decoration element
const TrustDecoration = styled.div`
  position: absolute;
  top: 50px;
  right: 80px;
  width: 120px;
  height: 120px;
  z-index: 0;
  opacity: 0.7;

  &::before, &::after {
    content: '';
    position: absolute;
    border-radius: 50%;
  }

  &::before {
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border: 1px dashed rgba(203, 157, 118, 0.3);
    animation: ${float} 6s ease-in-out infinite;
  }

  &::after {
    top: 20px;
    left: 20px;
    width: 60%;
    height: 60%;
    border: 1px solid rgba(203, 157, 118, 0.2);
    animation: ${float} 4s ease-in-out infinite;
    animation-delay: 1s;
  }
`;

// TrustIcons component
const TrustIcons = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  return (
    <TrustSection id="trust" ref={ref}>
      <BackgroundPattern />
      <TrustDecoration />

      <Container>
        <SectionTitle inView={inView}>Why Choose Us</SectionTitle>
        <MainHeading inView={inView}>
          We deliver <span>reliable excellence</span> in every project
        </MainHeading>

        <TrustGrid>
          {trustItems.map((item, index) => (
            <TrustItemCard key={item.id} index={index} inView={inView}>
              <IconContainer>{item.icon}</IconContainer>
              <TrustTitle>{item.title}</TrustTitle>
              <TrustDescription>{item.description}</TrustDescription>
            </TrustItemCard>
          ))}
        </TrustGrid>
      </Container>
    </TrustSection>
  );
};

export default TrustIcons;
