import React, { useEffect, useState, useRef } from 'react';
import styled, { keyframes, createGlobalStyle } from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

const GlobalVideoStyle = createGlobalStyle`
  .hero-video {
    filter: contrast(1.05) saturate(1.1);
    will-change: transform;
  }
`;

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const slideIn = keyframes`
  from {
    opacity: 0;
    transform: translateX(50px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
`;

const HeroContainer = styled.section`
  position: relative;
  color: var(--white);
  display: flex;
  height: 100vh;
  width: 100%;
  background-color: #0a0a0a;

  @media (max-width: 768px) {
    flex-direction: column-reverse;
  }
`;

const VideoSection = styled.div`
  flex: 0.6;
  position: relative;
  overflow: hidden;
  height: 100vh;

  @media (max-width: 768px) {
    height: 50vh;
  }
`;

const VideoBackground = styled.div`
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 0;
  overflow: hidden;
  background-color: #080808;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(to left, rgba(0,0,0,0.1), rgba(0,0,0,0.4));
    z-index: 1;
  }

  video {
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
  }
`;

const ContentSection = styled.div`
  flex: 0.4;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0 50px;
  position: relative;
  z-index: 5;

  @media (max-width: 1200px) {
    padding: 0 30px;
  }

  @media (max-width: 768px) {
    flex: 1;
    padding: 30px 25px;
  }
`;

const ContentInner = styled.div`
  max-width: 440px;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  text-align: left;
`;

const Logo = styled.div`
  position: absolute;
  top: 40px;
  left: 50px;
  font-size: 24px;
  font-weight: 500;
  color: var(--white);
  z-index: 10;
  display: flex;
  align-items: center;
  letter-spacing: 1px;

  span {
    color: var(--primary);
  }

  @media (max-width: 768px) {
    left: 20px;
    font-size: 22px;
    top: 20px;
  }
`;

const MainHeading = styled.h1`
  font-size: clamp(2.4rem, 3.5vw, 2.8rem);
  font-weight: 300;
  line-height: 1.1;
  letter-spacing: 0.5px;
  margin-bottom: 20px;
  color: rgba(255, 255, 255, 0.95);
  animation: ${fadeIn} 1s ease-out forwards;

  strong {
    font-weight: 500;
    color: var(--primary);
    position: relative;
    display: inline-block;
    padding-bottom: 4px;

    &::after {
      content: '';
      position: absolute;
      bottom: 0;
      left: 0;
      width: 100%;
      height: 1px;
      background-color: var(--primary);
    }
  }
`;

const SubHeading = styled.p`
  font-size: 1rem;
  font-weight: 300;
  line-height: 1.5;
  margin: 0 0 30px 0;
  color: rgba(255, 255, 255, 0.8);
  animation: ${fadeIn} 1s ease-out 0.3s forwards;
  max-width: 95%;
`;

const LeadMagnet = styled.div`
  width: 100%;
  margin: 0 0 30px;
  animation: ${fadeIn} 1s ease-out 0.5s forwards;
`;

const ValueStatement = styled.p`
  font-size: 0.95rem;
  line-height: 1.5;
  margin-bottom: 20px;
  padding-left: 12px;
  border-left: 2px solid var(--primary);
  color: rgba(255, 255, 255, 0.85);
`;

const FormContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  gap: 8px;
  margin-bottom: 12px;
`;

const Input = styled.input`
  background: rgba(255, 255, 255, 0.07);
  border: 1px solid rgba(255, 255, 255, 0.15);
  border-radius: 3px;
  padding: 15px;
  color: var(--white);
  font-size: 0.95rem;
  width: 100%;
  transition: border-color 0.3s ease;

  &::placeholder {
    color: rgba(255, 255, 255, 0.4);
  }

  &:focus {
    outline: none;
    border-color: var(--primary);
  }
`;

const CheckboxContainer = styled.div`
  display: flex;
  align-items: flex-start;
  margin-bottom: 20px;
`;

const StyledCheckbox = styled.input`
  margin-right: 10px;
  margin-top: 4px;
  accent-color: var(--primary);
`;

const CheckboxLabel = styled.label`
  font-size: 0.85rem;
  color: rgba(255, 255, 255, 0.7);
  line-height: 1.4;
`;

const ExclusiveBadge = styled.div`
  display: inline-block;
  padding: 6px 12px;
  background-color: rgba(203, 157, 118, 0.15);
  border: 1px solid rgba(203, 157, 118, 0.3);
  border-radius: 3px;
  font-size: 0.8rem;
  text-transform: uppercase;
  letter-spacing: 1px;
  color: var(--primary);
  margin-bottom: 20px;
  font-weight: 500;
`;

const TestimonialOverlay = styled.div`
  position: absolute;
  bottom: 30px;
  right: 30px;
  max-width: 280px;
  background: rgba(10, 10, 10, 0.8);
  backdrop-filter: blur(10px);
  border-radius: 4px;
  padding: 20px;
  z-index: 5;
  border: 1px solid rgba(255, 255, 255, 0.1);

  @media (max-width: 1024px) {
    max-width: 240px;
    bottom: 20px;
    right: 20px;
  }

  @media (max-width: 768px) {
    display: none;
  }
`;

const TestimonialText = styled.p`
  font-size: 0.9rem;
  font-style: italic;
  color: rgba(255, 255, 255, 0.9);
  margin-bottom: 10px;
  line-height: 1.5;
`;

const TestimonialAuthor = styled.div`
  display: flex;
  align-items: center;
`;

const AuthorAvatar = styled.div`
  width: 35px;
  height: 35px;
  border-radius: 50%;
  background-color: var(--primary);
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-weight: 600;
  font-size: 0.9rem;
  margin-right: 10px;
`;

const AuthorInfo = styled.div`
  display: flex;
  flex-direction: column;
`;

const AuthorName = styled.span`
  font-size: 0.85rem;
  font-weight: 500;
  color: white;
`;

const AuthorRole = styled.span`
  font-size: 0.75rem;
  color: rgba(255, 255, 255, 0.7);
