import React from 'react';
import styled from 'styled-components';
import { useInView } from 'react-intersection-observer';

const addOns = [
  {
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <rect x="2" y="3" width="20" height="14" rx="2" ry="2"></rect>
        <line x1="8" y1="21" x2="16" y2="21"></line>
        <line x1="12" y1="17" x2="12" y2="21"></line>
      </svg>
    ),
    title: 'Motorized blinds',
    description: 'Automatic control of natural light',
  },
  {
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <rect x="4" y="4" width="16" height="16" rx="2" ry="2"></rect>
        <rect x="9" y="9" width="6" height="6"></rect>
        <line x1="9" y1="1" x2="9" y2="4"></line>
        <line x1="15" y1="1" x2="15" y2="4"></line>
        <line x1="9" y1="20" x2="9" y2="23"></line>
        <line x1="15" y1="20" x2="15" y2="23"></line>
        <line x1="20" y1="9" x2="23" y2="9"></line>
        <line x1="20" y1="14" x2="23" y2="14"></line>
        <line x1="1" y1="9" x2="4" y2="9"></line>
        <line x1="1" y1="14" x2="4" y2="14"></line>
      </svg>
    ),
    title: 'Touch panel upgrades',
    description: 'Premium control interfaces',
  },
  {
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <polygon points="12 2 15.09 8.26 22 9.27 17 14.14 18.18 21.02 12 17.77 5.82 21.02 7 14.14 2 9.27 8.91 8.26 12 2"></polygon>
      </svg>
    ),
    title: 'Outdoor audio systems',
    description: 'Extend your entertainment to the outdoors',
  },
  {
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <rect x="2" y="7" width="20" height="15" rx="2" ry="2"></rect>
        <polyline points="17 2 12 7 7 2"></polyline>
      </svg>
    ),
    title: 'Home theatre integration',
    description: 'Immersive entertainment experiences',
  },
  {
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <circle cx="12" cy="12" r="10"></circle>
        <path d="M8.56 2.75c4.37 6.03 6.02 9.42 8.03 17.72m2.54-15.38c-3.72 4.35-8.94 5.66-16.88 5.85m19.5 1.9c-3.5-.93-6.63-.82-8.94 0-2.58.92-5.01 2.86-7.44 6.32"></path>
      </svg>
    ),
    title: 'Voice assistant support',
    description: 'Alexa, Google & more',
  },
];

const AddOnsSection = styled.section`
  padding: 80px 0;
  background-color: var(--light-gray);
  position: relative;
  overflow: hidden;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const SectionHeader = styled.div`
  text-align: center;
  margin-bottom: 50px;

  h2 {
    color: var(--secondary);
    margin-bottom: 15px;
  }

  p {
    font-size: 1.1rem;
    max-width: 700px;
    margin: 0 auto;
    color: var(--secondary);
  }
`;

const AddOnsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  gap: 30px;

  @media (max-width: 992px) {
    grid-template-columns: repeat(3, 1fr);
  }

  @media (max-width: 768px) {
    grid-template-columns: repeat(2, 1fr);
  }

  @media (max-width: 480px) {
    grid-template-columns: 1fr;
  }
`;

const AddOnCard = styled.div`
  background-color: var(--white);
  border-radius: 10px;
  padding: 25px 20px;
  text-align: center;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
  transition: var(--transition);

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.1);

    svg {
      color: var(--primary);
      transform: scale(1.1);
    }
  }

  svg {
    width: 40px;
    height: 40px;
    margin-bottom: 15px;
    color: var(--secondary);
    transition: var(--transition);
  }

  h4 {
    margin-bottom: 10px;
    font-size: 1.1rem;
  }

  p {
    font-size: 0.9rem;
    color: var(--secondary);
    margin-bottom: 0;
  }
`;

const CTAText = styled.div`
  text-align: center;
  margin-top: 40px;
  font-size: 1.1rem;
  font-weight: 500;

  span {
    color: var(--primary);
  }
`;

const AddOns = () => {
  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  return (
    <AddOnsSection ref={ref}>
      <Container>
        <SectionHeader className={inView ? 'fade-in' : ''}>
          <h2>Enhance Your Smart Home Experience</h2>
          <p>
            Customize your system with these popular add-ons to create the 
            perfect smart home experience for your lifestyle.
          </p>
        </SectionHeader>

        <AddOnsGrid>
          {addOns.map((addon, index) => (
            <AddOnCard 
              key={index} 
              className={inView ? `slide-up delay-${index % 3}` : ''}
            >
              {addon.icon}
              <h4>{addon.title}</h4>
              <p>{addon.description}</p>
            </AddOnCard>
          ))}
        </AddOnsGrid>

        <CTAText className={inView ? 'fade-in delay-3' : ''}>
          <span>Just mention it in your consultation</span> and we'll include it in your custom quote.
        </CTAText>
      </Container>
    </AddOnsSection>
  );
};

export default AddOns;
