import React, { useState } from 'react';
import styled, { keyframes, css } from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

// Animations
const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const shimmer = keyframes`
  0% {
    background-position: -200% 0;
  }
  100% {
    background-position: 200% 0;
  }
`;

const float = keyframes`
  0% {
    transform: translateY(0) rotate(0deg);
  }
  50% {
    transform: translateY(-10px) rotate(1deg);
  }
  100% {
    transform: translateY(0) rotate(0deg);
  }
`;

const pulse = keyframes`
  0% {
    box-shadow: 0 0 0 0 rgba(255, 255, 255, 0.4);
  }
  70% {
    box-shadow: 0 0 0 15px rgba(255, 255, 255, 0);
  }
  100% {
    box-shadow: 0 0 0 0 rgba(255, 255, 255, 0);
  }
`;

// Styled Components
const Section = styled.section`
  padding: 120px 0;
  background: linear-gradient(135deg, #1e2a38 0%, #0f1620 100%);
  color: #fff;
  position: relative;
  overflow: hidden;
`;

const BackgroundElements = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 0;

  &::before, &::after {
    content: '';
    position: absolute;
    border-radius: 50%;
    background: radial-gradient(circle, rgba(255, 255, 255, 0.1) 0%, transparent 70%);
    opacity: 0.5;
  }

  &::before {
    width: 600px;
    height: 600px;
    top: -200px;
    right: -200px;
  }

  &::after {
    width: 400px;
    height: 400px;
    bottom: -100px;
    left: -100px;
  }
`;

const Grid = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: linear-gradient(rgba(255, 255, 255, 0.03) 1px, transparent 1px),
                    linear-gradient(90deg, rgba(255, 255, 255, 0.03) 1px, transparent 1px);
  background-size: 50px 50px;
  z-index: 0;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
  position: relative;
  z-index: 1;
`;

const SectionHeader = styled.div<{ inView: boolean }>`
  text-align: center;
  margin-bottom: 80px;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '30px'});
  transition: all 0.8s ease-out;
`;

const SectionTitle = styled.h2`
  font-size: 3.5rem;
  font-weight: 700;
  margin-bottom: 20px;
  color: #fff;

  span {
    color: #e1c34c;
    position: relative;
    display: inline-block;
  }
`;

const SectionDescription = styled.p`
  font-size: 1.2rem;
  max-width: 800px;
  margin: 0 auto;
  color: rgba(255, 255, 255, 0.8);
  line-height: 1.6;
`;

const FeaturesGrid = styled.div<{ inView: boolean }>`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 30px;
  margin-bottom: 80px;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '30px'});
  transition: all 0.8s ease-out 0.2s;

  @media (max-width: 991px) {
    grid-template-columns: repeat(2, 1fr);
  }

  @media (max-width: 767px) {
    grid-template-columns: 1fr;
  }
`;

const FeatureCard = styled.div<{ delay: number }>`
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(10px);
  border-radius: 15px;
  padding: 30px;
  transition: all 0.3s ease;
  border: 1px solid rgba(255, 255, 255, 0.1);
  animation: ${fadeIn} 0.8s ease-out forwards;
  animation-delay: ${props => 0.1 + props.delay * 0.1}s;
  opacity: 0;

  &:hover {
    transform: translateY(-10px);
    background: rgba(255, 255, 255, 0.08);
    border-color: rgba(255, 255, 255, 0.2);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.3);
  }
`;

const FeatureIcon = styled.div`
  width: 70px;
  height: 70px;
  border-radius: 20px;
  background: rgba(225, 195, 76, 0.2);
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 25px;

  svg {
    width: 35px;
    height: 35px;
    color: #e1c34c;
  }
`;

const FeatureTitle = styled.h3`
  font-size: 1.4rem;
  font-weight: 600;
  margin-bottom: 15px;
  color: #fff;
`;

const FeatureDescription = styled.p`
  font-size: 1rem;
  color: rgba(255, 255, 255, 0.7);
  line-height: 1.6;
`;

const ShowcaseSection = styled.div<{ inView: boolean }>`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 60px;
  align-items: center;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '30px'});
  transition: all 0.8s ease-out 0.4s;

  @media (max-width: 991px) {
    grid-template-columns: 1fr;
    gap: 40px;
  }
`;

const ShowcaseContent = styled.div`
  @media (max-width: 991px) {
    order: 1;
  }
`;

const ShowcaseTitle = styled.h3`
  font-size: 2.2rem;
  font-weight: 700;
  margin-bottom: 25px;
  color: #fff;
`;

const ShowcaseDescription = styled.p`
  font-size: 1.1rem;
  color: rgba(255, 255, 255, 0.8);
  line-height: 1.7;
  margin-bottom: 30px;
`;

const ShowcaseList = styled.ul`
  margin-bottom: 30px;
  padding-left: 0;
  list-style: none;
`;

const ShowcaseItem = styled.li`
  display: flex;
  align-items: flex-start;
  margin-bottom: 15px;

  svg {
    color: #e1c34c;
    min-width: 24px;
    margin-right: 15px;
    margin-top: 3px;
  }

  span {
    font-size: 1.05rem;
    color: rgba(255, 255, 255, 0.9);
    line-height: 1.5;
  }
`;

const BadgeRow = styled.div`
  display: flex;
  gap: 15px;
  margin-bottom: 30px;
`;

const Badge = styled.div`
  background: rgba(225, 195, 76, 0.15);
  color: #e1c34c;
  padding: 5px 15px;
  border-radius: 30px;
  font-size: 0.9rem;
  font-weight: 500;
  display: flex;
  align-items: center;

  svg {
    margin-right: 5px;
  }
`;

const ImageContainer = styled.div`
  position: relative;

  @media (max-width: 991px) {
    order: 2;
  }
`;

const ShowcaseImage = styled.div`
  position: relative;
  border-radius: 20px;
  overflow: hidden;
  box-shadow: 0 30px 60px rgba(0, 0, 0, 0.3);
  animation: ${float} 6s ease-in-out infinite;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, rgba(225, 195, 76, 0.2) 0%, transparent 80%);
    z-index: 1;
    pointer-events: none;
  }

  img {
    width: 100%;
    height: auto;
    display: block;
    transition: transform 0.5s ease;
  }

  &:hover img {
    transform: scale(1.05);
  }
`;

const PulseCircle = styled.div`
  position: absolute;
  width: 30px;
  height: 30px;
  background: rgba(225, 195, 76, 0.8);
  border-radius: 50%;
  z-index: 2;
  cursor: pointer;
  box-shadow: 0 0 0 rgba(225, 195, 76, 0.4);
  animation: ${pulse} 2s infinite;

  &::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 10px;
    height: 10px;
    background: #fff;
    border-radius: 50%;
  }
`;

const Hotspot1 = styled(PulseCircle)`
  top: 30%;
  left: 20%;
`;

const Hotspot2 = styled(PulseCircle)`
  bottom: 25%;
  right: 25%;
`;

const Tooltip = styled.div<{ visible: boolean }>`
  position: absolute;
  background: rgba(0, 0, 0, 0.8);
  color: #fff;
  padding: 15px;
  border-radius: 10px;
  min-width: 200px;
  z-index: 3;
  opacity: ${props => props.visible ? 1 : 0};
  transform: translateY(${props => props.visible ? 0 : '10px'});
  pointer-events: ${props => props.visible ? 'auto' : 'none'};
  transition: all 0.3s ease;

  h4 {
    font-size: 1rem;
    margin: 0 0 5px;
    color: #e1c34c;
  }

  p {
    font-size: 0.9rem;
    margin: 0;
    color: rgba(255, 255, 255, 0.9);
  }
`;

const Tooltip1 = styled(Tooltip)`
  top: calc(30% - 80px);
  left: calc(20% - 100px);

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 110px;
    border-width: 10px 10px 0;
    border-style: solid;
    border-color: rgba(0, 0, 0, 0.8) transparent transparent;
  }
`;

const Tooltip2 = styled(Tooltip)`
  bottom: calc(25% + 50px);
  right: calc(25% - 100px);

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    right: 110px;
    border-width: 10px 10px 0;
    border-style: solid;
    border-color: rgba(0, 0, 0, 0.8) transparent transparent;
  }
`;

const CtaSection = styled.div<{ inView: boolean }>`
  background: linear-gradient(135deg, rgba(225, 195, 76, 0.15) 0%, rgba(225, 195, 76, 0.05) 100%);
  border-radius: 20px;
  padding: 60px;
  margin-top: 100px;
  text-align: center;
  border: 1px solid rgba(225, 195, 76, 0.2);
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '30px'});
  transition: all 0.8s ease-out 0.6s;

  @media (max-width: 767px) {
    padding: 40px 20px;
  }
`;

const CtaTitle = styled.h3`
  font-size: 2.2rem;
  font-weight: 700;
  margin-bottom: 20px;
  color: #fff;
`;

const CtaDescription = styled.p`
  font-size: 1.1rem;
  color: rgba(255, 255, 255, 0.9);
  max-width: 700px;
  margin: 0 auto 30px;
  line-height: 1.6;
`;

interface CommercialProps {}

const Commercial: React.FC<CommercialProps> = () => {
  const [tooltip1Visible, setTooltip1Visible] = useState(false);
  const [tooltip2Visible, setTooltip2Visible] = useState(false);

  const [headerRef, headerInView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const [featuresRef, featuresInView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const [showcaseRef, showcaseInView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const [ctaRef, ctaInView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const features = [
    {
      icon: (
        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
        </svg>
      ),
      title: "Energy Management",
      description: "Optimize energy usage across your commercial space with AI-powered systems that adapt to occupancy and usage patterns."
    },
    {
