sourcesContent:
"use client"

import { useEffect, useState, useRef } from "react"
import { useInView } from "react-intersection-observer"
// Replace next/image with a regular img element approach
// import Image from "next/image"
// Replace lucide-react icons with simple div elements
// import { Check, Activity, Shield, Info } from "lucide-react"
// Use a local button import or HTML button
// import { Button } from "@/components/ui/button"
import type React from "react"
import styled, { keyframes, createGlobalStyle, css } from "styled-components"

// Create simple icon components to replace lucide-react
const CheckIcon = () => (
  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="mr-2 h-4 w-4 flex-shrink-0 text-[#cb9d76]">
    <polyline points="20 6 9 17 4 12"></polyline>
  </svg>
);

const ActivityIcon = () => (
  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="h-6 w-6 text-[#cb9d76]">
    <polyline points="22 12 18 12 15 21 9 3 6 12 2 12"></polyline>
  </svg>
);

const ShieldIcon = () => (
  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="h-6 w-6 text-[#cb9d76]">
    <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z"></path>
  </svg>
);

const InfoIcon = () => (
  <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="h-6 w-6 text-[#cb9d76]">
    <circle cx="12" cy="12" r="10"></circle>
    <line x1="12" y1="16" x2="12" y2="12"></line>
    <line x1="12" y1="8" x2="12.01" y2="8"></line>
  </svg>
);

// Create a simple Button component to replace @/components/ui/button
const Button = styled.button`
  display: inline-flex;
  align-items: center;
  justify-content: center;
  border-radius: 0.5rem;
  font-weight: 500;
  cursor: pointer;
  transition-property: all;
  transition-duration: 150ms;
  transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
`;

// Add new animations
const textReveal = keyframes`
  0% {
    clip-path: polygon(0 0, 0 0, 0 100%, 0 100%);
  }
  100% {
    clip-path: polygon(0 0, 100% 0, 100% 100%, 0 100%);
  }
`

const gradientMove = keyframes`
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
`

const buttonGlow = keyframes`
  0% {
    box-shadow: 0 0 5px rgba(203, 157, 118, 0.5), 0 0 10px rgba(203, 157, 118, 0);
  }
  50% {
    box-shadow: 0 0 10px rgba(203, 157, 118, 0.7), 0 0 20px rgba(203, 157, 118, 0.3);
  }
  100% {
    box-shadow: 0 0 5px rgba(203, 157, 118, 0.5), 0 0 10px rgba(203, 157, 118, 0);
  }
`

// Enhanced animations
const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`

const fadeInUp = keyframes`
  from {
    opacity: 0;
    transform: translateY(15px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`

const shimmer = keyframes`
  0% {
    background-position: -468px 0;
  }
  100% {
    background-position: 468px 0;
  }
`

const float = keyframes`
  0% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
  100% {
    transform: translateY(0px);
  }
`

const pulse = keyframes`
  0% {
    box-shadow: 0 0 0 0 rgba(203, 157, 118, 0.4);
  }
  70% {
    box-shadow: 0 0 0 15px rgba(203, 157, 118, 0);
  }
  100% {
    box-shadow: 0 0 0 0 rgba(203, 157, 118, 0);
  }
`

const slideInRight = keyframes`
  from {
    transform: translateX(50px);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
`

const scroll = keyframes`
  0% {
    transform: translateX(0);
  }
  100% {
    transform: translateX(-50%);
  }
`

// Global styles for enhanced visuals
const GlobalStyle = createGlobalStyle`
  .hero-video {
    will-change: transform;
    transition: all 1.5s ease-in-out;
  }

  .hero-video.in-view {
    transform: scale(1.05);
  }

  @keyframes gradientShift {
    0% {
      background-position: 0% 50%;
    }
    50% {
      background-position: 100% 50%;
    }
    100% {
      background-position: 0% 50%;
    }
  }
`

const HeroContainer = styled.section`
  position: relative;
  color: #fff;
  display: flex;
  height: 100vh;
  width: 100%;
  background-color: #0a0a0a;
  overflow: hidden;

  @media (max-width: 768px) {
    flex-direction: column-reverse;
  }
`

const VideoSection = styled.div`
  flex: 0.6;
  position: relative;
  overflow: hidden;
  height: 100vh;
  transform-style: preserve-3d;
  perspective: 1000px;

  @media (max-width: 768px) {
    height: 50vh;
  }
`

const VideoBackground = styled.div`
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 0;
  overflow: hidden;
  background-color: #0a0a0a;
  transform: translateZ(-50px) scale(1.15);

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(to left, rgba(10, 10, 10, 0.2), rgba(10, 10, 10, 0.4));
    z-index: 1;
  }

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle at 50% 50%, transparent 0%, rgba(10, 10, 10, 0.4) 100%);
    z-index: 2;
    opacity: 0.5;
  }

  video {
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
    opacity: 1;
  }
`

const ParticlesOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 2;
  pointer-events: none;
  overflow: hidden;
`

const Particle = styled.div<{ size: number; posX: number; posY: number; speed: number }>`
  position: absolute;
  width: ${(props) => props.size}px;
  height: ${(props) => props.size}px;
  background: rgba(203, 157, 118, 0.2);
  border-radius: 50%;
  top: ${(props) => props.posY}%;
  left: ${(props) => props.posX}%;
  animation: ${float} ${(props) => props.speed}s ease-in-out infinite;
  opacity: 0.2;
`

const ContentSection = styled.div`
  flex: 0.4;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  padding: 0 50px;
  position: relative;
  z-index: 10;
  background-color: #0a0a0a;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle at 30% 50%, rgba(203, 157, 118, 0.05) 0%, rgba(0, 0, 0, 0) 50%);
    z-index: -1;
    animation: ${shimmer} 10s linear infinite;
  }

  @media (max-width: 1200px) {
    padding: 0 30px;
  }

  @media (max-width: 768px) {
    flex: 1;
    padding: 30px 25px;
  }
`

const ContentInner = styled.div`
  max-width: 500px;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  text-align: left;
  position: relative;
  padding-top: 180px;
`

const Logo = styled.div`
  position: absolute;
  top: 40px;
  left: 50px;
  font-size: 24px;
  font-weight: 500;
  color: #fff;
  z-index: 20;
  display: flex;
  align-items: center;
  letter-spacing: 1px;

  span {
    color: rgba(203, 157, 118, 0.9);
    position: relative;

    &::after {
      content: '';
      position: absolute;
      bottom: 0;
      left: 0;
      width: 100%;
      height: 10px;
      background-color: rgba(203, 157, 118, 0.1);
      z-index: -1;
    }
  }

  svg {
    width: 20px;
    height: 20px;
    margin-right: 10px;
    fill: rgba(203, 157, 118, 0.9);
  }
`

const MainHeading = styled.h1<{ inView: boolean }>`
  font-size: clamp(2.5rem, 5vw, 3.5rem);
  font-weight: 800;
  line-height: 1.2;
  margin-bottom: 25px;
  opacity: 0;
  transform: translateY(30px);
  transition: all 0.8s cubic-bezier(0.215, 0.61, 0.355, 1);
  transition-delay: 0.2s;
  position: relative;
  letter-spacing: -0.5px;

  ${(props) =>
    props.inView &&
    css`
    opacity: 1;
    transform: translateY(0);
  `}

  .reveal-text {
    display: inline-block;
    position: relative;
    clip-path: polygon(0 0, 0 0, 0 100%, 0 100%);
    animation: ${textReveal} 0.8s cubic-bezier(0.77, 0, 0.175, 1) forwards;
    animation-delay: 0.5s;
  }

  .reveal-text:nth-child(2) {
    animation-delay: 0.7s;
  }

  .reveal-text:nth-child(3) {
    animation-delay: 0.9s;
  }

  span {
    background: linear-gradient(135deg, rgba(203, 157, 118, 0.9), rgba(178, 132, 93, 0.9));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    position: relative;
    display: inline-block;
    background-size: 200% auto;
    animation: ${gradientMove} 4s ease infinite;

    &::after {
      content: '';
      position: absolute;
      bottom: 5px;
      left: 0;
      width: 100%;
      height: 10px;
      background-color: rgba(203, 157, 118, 0.1);
      z-index: -1;
      border-radius: 10px;
    }
  }

  &::after {
    content: '';
    position: absolute;
    bottom: -15px;
    left: 0;
    width: 60px;
    height: 4px;
    background: linear-gradient(to right, rgba(203, 157, 118, 0.9), transparent);
    border-radius: 2px;
  }
`

const WordHighlight = styled.span`
  position: relative;
  color: rgba(255, 255, 255, 0.9);

  &::before {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 8px;
    background: rgba(203, 157, 118, 0.2);
    z-index: -1;
    transform: scaleX(0);
    transform-origin: left;
    transition: transform 0.4s ease-out;
    border-radius: 4px;
  }

  &:hover {
    &::before {
      transform: scaleX(1);
    }
  }
`

const Subtitle = styled.p<{ inView: boolean }>`
  font-size: 1.2rem;
  line-height: 1.8;
  margin-bottom: 30px;
  color: rgba(255, 255, 255, 0.9);
  opacity: 0;
  transform: translateY(30px);
  transition: all 0.8s cubic-bezier(0.215, 0.61, 0.355, 1);
  transition-delay: 0.4s;
  position: relative;
  max-width: 90%;

  ${(props) =>
    props.inView &&
    css`
    opacity: 1;
    transform: translateY(0);
  `}

  strong {
    color: rgba(203, 157, 118, 0.9);
    font-weight: 500;
    transition: all 0.3s ease;

    &:hover {
      color: #fff;
    }
  }

  &::before {
    content: '';
    position: absolute;
    top: -8px;
    left: 0;
    width: 30px;
    height: 2px;
    background-color: rgba(203, 157, 118, 0.4);
  }
`

const ButtonWrapper = styled.div<{ inView: boolean }>`
  display: flex;
  gap: 15px;
  opacity: 0;
  transform: translateY(30px);
  transition: all 0.8s cubic-bezier(0.215, 0.61, 0.355, 1);
  transition-delay: 0.6s;

  ${(props) =>
    props.inView &&
    css`
    opacity: 1;
    transform: translateY(0);
  `}

  @media (max-width: 480px) {
    flex-direction: column;
    width: 100%;
  }
`

const PrimaryButton = styled(Button)`
  padding: 14px 28px;
  background: linear-gradient(135deg, rgba(203, 157, 118, 0.9), rgba(178, 132, 93, 0.9));
  color: #fff;
  border-radius: 12px;
  border: none;
  font-weight: 600;
  letter-spacing: 0.5px;
  text-transform: uppercase;
  font-size: 0.9rem;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
  position: relative;
  overflow: hidden;
  animation: ${buttonGlow} 2s infinite;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(
      90deg,
      transparent 0%,
      rgba(255, 255, 255, 0.2) 50%,
      transparent 100%
    );
    transition: all 0.4s ease;
  }

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.2);

    &::before {
      left: 100%;
    }
  }

  @media (max-width: 480px) {
    width: 100%;
  }
`

const SecondaryButton = styled(Button)`
  padding: 14px 28px;
  background-color: transparent;
  color: #fff;
  border-radius: 12px;
  border: 1px solid rgba(203, 157, 118, 0.3);
  font-weight: 600;
  letter-spacing: 0.5px;
  text-transform: uppercase;
  font-size: 0.9rem;
  cursor: pointer;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
  z-index: 1;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, rgba(203, 157, 118, 0.1), rgba(178, 132, 93, 0.2));
    z-index: -1;
    transform: scaleX(0);
    transform-origin: right;
    transition: transform 0.4s ease;
  }

  &:hover {
    border-color: rgba(203, 157, 118, 0.6);
    transform: translateY(-5px);

    &::before {
      transform: scaleX(1);
      transform-origin: left;
    }
  }

  @media (max-width: 480px) {
    width: 100%;
  }
`

const FeaturesBar = styled.div<{ inView: boolean }>`
  position: absolute;
  bottom: 40px;
  right: 50px;
  display: flex;
  gap: 25px;
  opacity: 0;
  transform: translateY(30px);
  transition: all 0.8s cubic-bezier(0.215, 0.61, 0.355, 1);
  transition-delay: 0.8s;

  ${(props) =>
    props.inView &&
    css`
    opacity: 1;
    transform: translateY(0);
  `}

  @media (max-width: 1200px) {
    right: 30px;
  }

  @media (max-width: 768px) {
    display: none;
  }
`

const FeatureItem = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;

  svg {
    width: 24px;
    height: 24px;
    color: rgba(203, 157, 118, 0.9);
  }

  span {
    font-size: 0.9rem;
    font-weight: 500;
    color: rgba(255, 255, 255, 0.8);
  }
`

const ScrollCTA = styled.div<{ inView: boolean }>`
  position: absolute;
  bottom: 40px;
  left: 50px;
  display: flex;
  flex-direction: column;
  align-items: center;
  opacity: 0;
  transform: translateY(30px);
  transition: all 0.8s cubic-bezier(0.215, 0.61, 0.355, 1);
  transition-delay: 1s;

  ${(props) =>
    props.inView &&
    css`
    opacity: 1;
    transform: translateY(0);
  `}

  span {
    font-size: 0.85rem;
    margin-bottom: 10px;
    color: rgba(255, 255, 255, 0.6);
    transform: rotate(-90deg);
    letter-spacing: 2px;
    text-transform: uppercase;
  }

  .scroll-indicator {
    width: 1px;
    height: 60px;
    background: linear-gradient(to bottom, rgba(203, 157, 118, 0.9), transparent);
    position: relative;
    overflow: hidden;

    &::before {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      width: 100%;
      height: 15px;
      background-color: rgba(203, 157, 118, 0.9);
      animation: ${slideInRight} 1.5s cubic-bezier(0.25, 0.46, 0.45, 0.94) infinite;
    }
  }

  @media (max-width: 768px) {
    display: none;
  }
`

// Brand logos section
const BrandLogoSection = styled.div<{ inView: boolean }>`
  width: 100%;
  margin-bottom: 30px;
  padding: 15px 0;
  z-index: 10;
  opacity: 0;
  transform: translateY(20px);
  transition: all 0.8s ease;
  transition-delay: 1.2s;

  ${(props) =>
    props.inView &&
    css`
