        console.error("Video playback error:", err)
        setVideoError(true)
      })
    }

    // Timer countdown effect
    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timer)
          return 0
        }
        return prev - 1
      })
    }, 1000)

    return () => clearInterval(timer)
