import React from 'react';
import styled from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

const packages = [
  {
    id: 'bedroom1',
    title: '1 Bedroom Smart Package',
    price: '$17,000',
    image: '/1bed.png',
    features: [
      'Smart lighting (5 zones)',
      'Audio system (4 speakers)',
      '1 smart thermostat',
      '1 video doorbell',
      '1 smart touchscreen',
      '2 Wi-Fi access points',
      'Smart lock',
      'Full installation + network setup',
    ],
    color: '#f8d7bc',
  },
  {
    id: 'bedroom2',
    title: '2 Bedroom Smart Package',
    price: '$24,000',
    image: '/2bed.png',
    features: [
      'Smart lighting (7 zones)',
      'Audio (6 speakers)',
      '2 thermostats',
      'Video doorbell + touchscreen',
      'Smart lock',
      'Control4 automation hub',
      'Full install + tuning',
    ],
    featured: true,
    color: '#CB9D76',
  },
  {
    id: 'bedroom3',
    title: '3 Bedroom Smart Package',
    price: '$33,000',
    image: '/3bed.png',
    features: [
      'Smart lighting (10 zones)',
      'Audio (8–10 speakers)',
      '3 thermostats',
      'Touchscreen x2 + TV control',
      'Video doorbell + smart lock',
      'Full network + access points',
      'Intercom/gate options',
    ],
    color: '#b78a63',
  },
  {
    id: 'custom',
    title: 'Custom Luxury Homes',
    price: 'Starting at $50,000+',
    image: '/cusrombed.png',
    features: [
      'Full panelized lighting',
      'Multi-zone audio/video',
      'In-ceiling speakers',
      'Motorized shades',
      'Rack system & power conditioning',
      'Full-service engineering + install',
    ],
    color: '#8d6c4e',
  },
];

const PackagesSection = styled.section`
  padding: 100px 0;
  background-color: var(--white);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle at 90% 10%, var(--primary) 0%, transparent 30%);
    opacity: 0.03;
    z-index: 0;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
  position: relative;
  z-index: 1;
`;

const SectionHeader = styled.div`
  text-align: center;
  margin-bottom: 60px;

  h2 {
    color: var(--secondary);
    margin-bottom: 15px;
    font-size: 2.5rem;
    position: relative;
    display: inline-block;

    &::after {
      content: '';
      position: absolute;
      bottom: -10px;
      left: 50%;
      transform: translateX(-50%);
      width: 80px;
      height: 3px;
      background: var(--primary);
      border-radius: 3px;
    }
  }

  p {
    font-size: 1.1rem;
    max-width: 700px;
    margin: 0 auto;
    margin-top: 25px;
    color: var(--secondary);
  }
`;

const PackagesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 25px;

  @media (max-width: 1200px) {
    grid-template-columns: repeat(2, 1fr);
  }

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const PackageCard = styled.div<{ color: string; featured?: boolean }>`
  background-color: white;
  border-radius: 15px;
  overflow: hidden;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.05);
  transition: all 0.4s ease;
  height: 100%;
  display: flex;
  flex-direction: column;
  border: ${props => props.featured ? `2px solid ${props.color}` : '1px solid rgba(0, 0, 0, 0.05)'};
  position: relative;
  transform: ${props => props.featured ? 'scale(1.03)' : 'scale(1)'};
  z-index: ${props => props.featured ? '2' : '1'};

  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
  }

  @media (max-width: 1200px) {
    transform: scale(1);
  }
`;

const PackageImageContainer = styled.div`
  position: relative;
  width: 100%;
  height: 200px;
  overflow: hidden;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.5s ease;
  }
`;

const ImageOverlay = styled.div<{ color: string }>`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    to bottom,
    transparent 50%,
    ${props => props.color}20 100%
  );
`;

const PopularTag = styled.div`
  position: absolute;
  top: 15px;
  right: 15px;
  background-color: var(--primary);
  color: var(--white);
  padding: 6px 15px;
  font-size: 0.75rem;
  font-weight: 600;
  border-radius: 20px;
  box-shadow: 0 5px 15px rgba(203, 157, 118, 0.3);
  z-index: 3;
`;

const PackageContent = styled.div`
  padding: 25px;
  flex: 1;
  display: flex;
  flex-direction: column;
`;

const PackageHeader = styled.div`
  margin-bottom: 20px;
`;

const PackageTitle = styled.h3`
  color: var(--secondary);
  font-size: 1.5rem;
  margin-bottom: 10px;
`;

const PackagePrice = styled.div<{ color: string }>`
  font-size: 1.8rem;
  font-weight: 700;
  color: ${props => props.color};
`;

const FeaturesList = styled.ul`
  list-style: none;
  padding: 0;
  margin-bottom: 25px;
  flex: 1;

  li {
    padding: 8px 0;
    border-bottom: 1px solid rgba(0, 0, 0, 0.05);
    display: flex;
    align-items: center;
    font-size: 0.95rem;

    &:last-child {
      border-bottom: none;
    }

    &::before {
      content: '✓';
      color: var(--primary);
      margin-right: 10px;
      font-weight: bold;
    }
  }
`;

const Packages = () => {
  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  const scrollToSection = (sectionId: string) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <PackagesSection id="packages" ref={ref}>
      <Container>
        <SectionHeader className={inView ? 'fade-in' : ''}>
          <h2>Choose Your Smart Home Package</h2>
          <p>
            Select the perfect smart home solution for your space. Each package 
            includes professional design, installation, and programming. Custom 
            options available.
          </p>
        </SectionHeader>

        <PackagesGrid>
          {packages.map((pkg, index) => (
            <PackageCard 
              key={pkg.id} 
              color={pkg.color}
              featured={pkg.featured}
              className={inView ? `slide-up delay-${index}` : ''}
            >
              {pkg.featured && <PopularTag>Most Popular</PopularTag>}
              <PackageImageContainer>
                <img src={pkg.image} alt={pkg.title} />
                <ImageOverlay color={pkg.color} />
              </PackageImageContainer>

              <PackageContent>
                <PackageHeader>
                  <PackageTitle>{pkg.title}</PackageTitle>
                  <PackagePrice color={pkg.color}>{pkg.price}</PackagePrice>
                </PackageHeader>

                <FeaturesList>
                  {pkg.features.map((feature, i) => (
                    <li key={i}>{feature}</li>
                  ))}
                </FeaturesList>

                <Button 
                  onClick={() => scrollToSection('contact')}
                  fullWidth
                  style={{ 
                    backgroundColor: pkg.color, 
                    borderColor: pkg.color 
                  }}
                >
                  Select This Package
                </Button>
              </PackageContent>
            </PackageCard>
          ))}
        </PackagesGrid>
      </Container>
    </PackagesSection>
  );
};

export default Packages;
