import React from 'react';
import styled, { keyframes } from 'styled-components';
import { useInView } from 'react-intersection-observer';

// Use the same pattern as in the Hero component
const brandLogos = [
  { src: "/control4.png", alt: "Control4" },
  { src: "/lutron.png", alt: "Lutron" },
  { src: "/sonos.png", alt: "Sonos" },
  { src: "/sony.png", alt: "Sony" },
  { src: "/jvc.png", alt: "JVC" },
  { src: "/leon.png", alt: "Leon" },
  { src: "/senhizer.png", alt: "Sennheiser" },
  { src: "/seura.png", alt: "Seura" },
  { src: "/araknis.png", alt: "Araknis" },
  { src: "/ur.png", alt: "Araknis" }

];

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const LogosSection = styled.section`
  padding: 100px 0;
  background-color: #ffffff;
  position: relative;
  overflow: hidden;

  /* Remove the gradient overlay */
  &::before {
    display: none;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
  position: relative;
  z-index: 1;
`;

const SectionTitle = styled.h2<{ inView: boolean }>`
  font-size: 2.2rem;
  color: var(--secondary);
  margin-bottom: 50px;
  position: relative;
  display: inline-block;
  font-weight: 600;
  text-align: center;
  width: 100%;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '20px'});
  transition: all 0.5s ease-out;

  span {
    color: var(--primary);
    position: relative;

    &::after {
      content: '';
      position: absolute;
      bottom: 0;
      left: 0;
      width: 100%;
      height: 8px;
      background-color: rgba(203, 157, 118, 0.15);
      z-index: -1;
    }
  }
`;

const LogosGrid = styled.div<{ inView: boolean }>`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
  gap: 35px;
  align-items: center;
  justify-items: center;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '30px'});
  transition: all 0.7s ease-out;
  transition-delay: 0.2s;

  @media (max-width: 992px) {
    grid-template-columns: repeat(3, 1fr);
    gap: 30px;
  }

  @media (max-width: 576px) {
    grid-template-columns: repeat(2, 1fr);
    gap: 25px;
  }
`;

// Logo component with better contrast for white logos
const Logo = styled.div<{ delay: number }>`
  background: #1a1a1a;
  padding: 30px;
  border-radius: 8px;
  box-shadow: 0 6px 20px rgba(0, 0, 0, 0.1);
  transition: all 0.4s ease;
  opacity: 1;
  filter: none;
  border: 1px solid rgba(0, 0, 0, 0.2);
  width: 100%;
  height: 100px;
  display: flex;
  justify-content: center;
  align-items: center;
  animation: ${fadeIn} 0.6s ease-out forwards;
  animation-delay: ${props => props.delay * 0.1}s;
  position: relative;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.15);
    border-color: rgba(203, 157, 118, 0.3);
    background: #141414;
  }

  img {
    max-width: 85%;
    max-height: 70%;
    object-fit: contain;
    filter: brightness(1.5) contrast(1.2);
  }
`;

const Subtitle = styled.p<{ inView: boolean }>`
  font-size: 1.1rem;
  color: var(--text);
  text-align: center;
  max-width: 600px;
  margin: 0 auto 50px;
  line-height: 1.6;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '20px'});
  transition: all 0.5s ease-out;
  transition-delay: 0.1s;
`;

const BrandLogos = () => {
  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  // Handle image error
  const handleImageError = (e: React.SyntheticEvent<HTMLImageElement, Event>) => {
    const target = e.target as HTMLImageElement;
    target.style.display = 'none';
    target.parentElement!.innerHTML = `<div style="color: var(--primary); font-weight: 500; font-size: 12px;">Brand Logo</div>`;
  };

  return (
    <LogosSection ref={ref}>
      <Container>
        <SectionTitle inView={inView}>
          We Partner With <span>Industry Leaders</span>
        </SectionTitle>

        <Subtitle inView={inView}>
          Home Vision Solutions works with the most trusted brands in smart home technology
          to provide you with reliable, cutting-edge solutions.
        </Subtitle>

        <LogosGrid inView={inView}>
          {brandLogos.map((logo, index) => (
            <Logo 
              key={logo.alt}
              delay={index}
            >
              <img 
                src={logo.src} 
                alt={logo.alt} 
                onError={handleImageError}
              />
            </Logo>
          ))}
        </LogosGrid>
      </Container>
    </LogosSection>
  );
};

export default BrandLogos;
