import React, { useState } from 'react';
import styled from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';
import { motion } from 'framer-motion';
import { fadeIn } from '../utils/animations';

const packages = [
  {
    id: 'bedroom1',
    title: '1 Bedroom Smart Package',
    subtitle: 'Perfect for apartments and smaller spaces',
    price: '$17,000',
    image: '/1bed.png',
    features: [
      'Smart lighting (5 zones)',
      'Audio system (4 speakers)',
      '1 smart thermostat',
      '1 video doorbell',
      '1 smart touchscreen',
      '2 Wi-Fi access points',
      'Smart lock',
      'Full installation + network setup',
    ],
    color: 'var(--primary)',
    colorEnd: 'var(--primary-dark)',
  },
  {
    id: 'bedroom2',
    title: '2 Bedroom Smart Package',
    subtitle: 'Ideal for condos and townhomes',
    price: '$24,000',
    image: '/2bed.png',
    features: [
      'Smart lighting (7 zones)',
      'Audio (6 speakers)',
      '2 thermostats',
      'Video doorbell + touchscreen',
      'Smart lock',
      'Control4 automation hub',
      'Full install + tuning',
    ],
    featured: true,
    color: 'var(--primary)',
    colorEnd: 'var(--primary-dark)',
  },
  {
    id: 'bedroom3',
    title: '3 Bedroom Smart Package',
    subtitle: 'Comprehensive coverage for larger spaces',
    price: '$33,000',
    image: '/3bed.png',
    features: [
      'Smart lighting (10 zones)',
      'Audio (8–10 speakers)',
      '3 thermostats',
      'Touchscreen x2 + TV control',
      'Video doorbell + smart lock',
      'Full network + access points',
      'Intercom/gate options',
    ],
    color: 'var(--primary)',
    colorEnd: 'var(--primary-dark)',
  },
  {
    id: 'custom',
    title: 'Custom Luxury Homes',
    subtitle: 'Bespoke solutions for premium properties',
    price: 'Starting at $50,000+',
    image: '/cusrombed.png',
    features: [
      'Full panelized lighting',
      'Multi-zone audio/video',
      'In-ceiling speakers',
      'Motorized shades',
      'Rack system & power conditioning',
      'Full-service engineering + install',
    ],
    color: 'var(--primary)',
    colorEnd: 'var(--primary-dark)',
  },
];

const PackagesSection = styled.section`
  padding: 120px 0;
  background: var(--bg-light);
  position: relative;
  overflow: hidden;
`;

const Container = styled.div`
  max-width: var(--container-width);
  margin: 0 auto;
  padding: 0 var(--container-padding);
  position: relative;
  z-index: 2;
`;

const SectionHeader = styled.div`
  text-align: center;
  margin-bottom: 80px;
`;

const SectionTitle = styled.h2`
  color: var(--secondary);
  margin-bottom: 15px;
  font-size: var(--fs-h2);
  position: relative;
  display: inline-block;
  font-weight: 700;

  span {
    color: var(--primary);
    position: relative;

    &::after {
      content: '';
      position: absolute;
      bottom: 2px;
      left: 0;
      width: 100%;
      height: 10px;
      background-color: rgba(var(--primary-rgb), 0.15);
      z-index: -1;
    }
  }
`;

const SectionSubtitle = styled.p`
  font-size: var(--fs-lg);
  max-width: 750px;
  margin: 25px auto 0;
  color: var(--text);
  line-height: 1.7;
`;

const PackagesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 280px));
  gap: 25px;
  justify-content: center;
  max-width: 1200px;
  margin: 0 auto;

  @media (max-width: 1200px) {
    grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
    max-width: 900px;
  }

  @media (max-width: 768px) {
    grid-template-columns: minmax(240px, 450px);
  }
`;

const PackageCard = styled(motion.div)<{ color: string; colorEnd: string; featured?: boolean; active: boolean }>`
  background-color: white;
  border-radius: var(--radius-lg);
  overflow: hidden;
  box-shadow: var(--shadow-sm);
  height: 100%;
  display: flex;
  flex-direction: column;
  position: relative;
  transition: all 0.5s var(--transition);

  ${props => props.featured && `
    transform: translateY(${props.active ? '-20px' : '-10px'});
    box-shadow: var(--shadow-lg);
    border: 2px solid var(--primary);
    z-index: 2;
  `}

  ${props => props.active && !props.featured && `
    transform: translateY(-15px);
    box-shadow: var(--shadow-md);
  `}

  &:hover {
    transform: translateY(${props => props.featured ? '-20px' : '-15px'});
    box-shadow: var(--shadow-md);
  }

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 6px;
    background: linear-gradient(to right, ${props => props.color}, ${props => props.colorEnd});
    opacity: ${props => props.featured || props.active ? 1 : 0.5};
    transition: opacity 0.3s ease;
  }

  &:hover::before {
    opacity: 1;
  }
`;

const PackageImageContainer = styled.div`
  position: relative;
  width: 100%;
  padding-top: 60%;
  overflow: hidden;

  img {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: contain;
    transition: transform 0.7s var(--transition);
    background-color: var(--bg-light);
  }

  ${PackageCard}:hover & img {
    transform: scale(1.05);
  }
`;

const ImageOverlay = styled.div<{ color: string }>`
  position: absolute;
