import React, { useEffect, useState, useRef } from 'react';
import styled, { keyframes } from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const HeroContainer = styled.section`
  position: relative;
  color: var(--white);
  overflow: hidden;
  background-color: #000;
  width: 100%;
  height: 100vh;

  @media (max-width: 768px) {
    height: 100vh;
  }
`;

const VideoSection = styled.div`
  width: 100%;
  height: 100vh;
  position: relative;
  overflow: hidden;

  @media (max-width: 768px) {
    height: 100vh;
  }
`;

const VideoBackground = styled.div`
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 0;

  video {
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
    filter: blur(5px);
  }
`;

const ContentSection = styled.div`
  width: 100%;
  position: absolute;
  bottom: 0;
  left: 0;
  display: flex;
  align-items: flex-end;
  justify-content: center;
  z-index: 5;
  padding-bottom: 80px;

  @media (max-width: 768px) {
    padding-bottom: 40px;
  }
`;

const ContentContainer = styled.div`
  width: 90%;
  max-width: 1000px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  position: relative;
  z-index: 2;
  background-color: rgba(0, 0, 0, 0.7);
  padding: 40px;
  border-radius: 10px 10px 0 0;
  backdrop-filter: blur(10px);
`;

const Logo = styled.div`
  position: absolute;
  top: 40px;
  left: 50px;
  font-size: 28px;
  font-weight: 600;
  color: var(--white);
  z-index: 10;
  display: flex;
  align-items: center;
  letter-spacing: 1px;
  opacity: 0.9;

  span {
    color: var(--primary);
  }

  @media (max-width: 768px) {
    left: 20px;
    font-size: 24px;
    top: 25px;
  }
`;

const MainHeading = styled.h1`
  font-size: clamp(2.5rem, 6vw, 4.5rem);
  font-weight: 100;
  line-height: 1.05;
  letter-spacing: 1px;
  margin-bottom: 20px;
  color: rgba(255, 255, 255, 0.95);
  animation: ${fadeIn} 1s ease-out 0.2s backwards;

  span {
    display: block;
    font-weight: 500;
    color: var(--primary);
    margin-top: 8px;
  }
`;

const SubHeading = styled.p`
  font-size: clamp(1rem, 2vw, 1.2rem);
  font-weight: 300;
  line-height: 1.5;
  max-width: 650px;
  margin: 0 auto 30px auto;
  color: rgba(255, 255, 255, 0.85);
  animation: ${fadeIn} 1s ease-out 0.5s backwards;
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 20px;
  justify-content: center;
  animation: ${fadeIn} 1s ease-out 0.8s backwards;
  margin-bottom: 10px;

  @media (max-width: 480px) {
     flex-direction: column;
     width: 90%;
     max-width: 280px;
     gap: 15px;
     margin: 0 auto 10px;
  }
`;

const primaryButtonStyle: React.CSSProperties = {
  fontSize: '0.95rem',
  padding: '14px 35px',
  borderRadius: '50px',
  letterSpacing: '1.5px',
  textTransform: 'uppercase',
  fontWeight: '500',
  boxShadow: '0 8px 25px rgba(203, 157, 118, 0.2)',
  transition: 'transform 0.3s ease, box-shadow 0.3s ease',
};

const outlineButtonStyle: React.CSSProperties = {
  fontSize: '0.95rem',
  padding: '14px 35px',
  borderRadius: '50px',
  letterSpacing: '1.5px',
  textTransform: 'uppercase',
  fontWeight: '500',
  borderWidth: '1px',
  color: 'rgba(255, 255, 255, 0.9)',
  borderColor: 'rgba(255, 255, 255, 0.5)',
  backgroundColor: 'rgba(255, 255, 255, 0.05)',
  backdropFilter: 'blur(5px)',
  transition: 'background-color 0.3s ease, border-color 0.3s ease',
};

const Hero = () => {
  const [isVisible, setIsVisible] = useState(true);
  const videoRef = useRef<HTMLVideoElement>(null);

  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: false,
  });

  useEffect(() => {
    setIsVisible(inView);
  }, [inView]);

  // Play video when component mounts
  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.play().catch(error => {
        console.log("Video autoplay failed:", error);
      });
    }
  }, []);

  const scrollToSection = (sectionId: string) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <HeroContainer ref={ref}>
      <Logo>
        Home <span>Vision</span>
      </Logo>

      <VideoSection>
        <VideoBackground>
          <video 
            ref={videoRef}
            autoPlay
            muted
            loop
            playsInline
            poster="/hero-poster.jpg"
          >
            <source src="/video2.mp4" type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </VideoBackground>
      </VideoSection>

      <ContentSection>
        <ContentContainer>
          <MainHeading className={isVisible ? 'fade-in' : ''}>
            Effortless Home
            <span>Intuitive Control</span>
          </MainHeading>

          <SubHeading>
            Experience the pinnacle of luxury living with bespoke smart home 
            automation, seamlessly integrated for ultimate comfort and convenience.
          </SubHeading>

          <ButtonContainer>
            <Button 
              primary 
              onClick={() => scrollToSection('contact')}
              style={primaryButtonStyle}
            >
              Request Consultation
            </Button>
            <Button 
              outline 
              onClick={() => scrollToSection('packages')}
              style={outlineButtonStyle}
            >
              Explore Services
            </Button>
          </ButtonContainer>
        </ContentContainer>
      </ContentSection>
    </HeroContainer>
  );
};

export default Hero;
