const TrustIcons = () => {
  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  const [activeIndex, setActiveIndex] = useState<number | null>(null);

  const handleMouseEnter = (index: number) => {
    setActiveIndex(index);
  };

  const handleMouseLeave = () => {
    setActiveIndex(null);
  };

  return (
    <TrustSection ref={ref}>
      <BackgroundElement />
      <BackgroundElement />

      <Container>
        <SectionHeader>
          <SectionTitle inView={inView}>
            Why Clients <span>Trust Us</span>
          </SectionTitle>
          <SectionSubtitle inView={inView}>
            Our commitment to excellence, attention to detail, and customer-first approach
            has established us as Vancouver's premier smart home solution provider.
          </SectionSubtitle>
        </SectionHeader>

        <TrustGrid>
          {trustIcons.map((item, index) => (
            <TrustItem 
              key={index}
              delay={index}
              active={activeIndex === index}
              onMouseEnter={() => handleMouseEnter(index)}
              onMouseLeave={handleMouseLeave}
            >
              <TrustItemContent>
                <HighlightTag className="highlight">{item.highlight}</HighlightTag>
                <IconContainer className="icon-container">
                  {item.icon}
                </IconContainer>
                <TrustTitle>{item.title}</TrustTitle>
                <TrustText>{item.text}</TrustText>
              </TrustItemContent>
            </TrustItem>
          ))}
        </TrustGrid>
      </Container>
    </TrustSection>
  );
};

