import React from 'react';
import styled from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

// Placeholder image
const bonusImageUrl = 'https://images.unsplash.com/photo-1558002038-1055907df827?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1770&q=80';

const leadMagnets = [
  {
    title: 'Ultimate Smart Home Guide',
    description: 'Transform your home with our comprehensive guide to smart technology.',
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <path d="M4 19.5A2.5 2.5 0 0 1 6.5 17H20"></path>
        <path d="M6.5 2H20v20H6.5A2.5 2.5 0 0 1 4 19.5v-15A2.5 2.5 0 0 1 6.5 2z"></path>
      </svg>
    ),
  },
  {
    title: 'Smart Home Essentials Checklist',
    description: 'Your 10-point checklist for a fully automated home.',
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
        <polyline points="22 4 12 14.01 9 11.01"></polyline>
      </svg>
    ),
  },
  {
    title: 'Smart Home 101 Webinar',
    description: 'Access our exclusive video series on designing your dream automated home.',
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <polygon points="23 7 16 12 23 17 23 7"></polygon>
        <rect x="1" y="5" width="15" height="14" rx="2" ry="2"></rect>
      </svg>
    ),
  },
  {
    title: 'Home Automation Quiz',
    description: 'Is your home ready for automation? Take our 2-minute assessment.',
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <circle cx="12" cy="12" r="10"></circle>
        <path d="M9.09 9a3 3 0 0 1 5.83 1c0 2-3 3-3 3"></path>
        <line x1="12" y1="17" x2="12.01" y2="17"></line>
      </svg>
    ),
  },
  {
    title: 'Energy Savings Report',
    description: 'Discover your potential savings with smart home technology.',
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <path d="M18 3a3 3 0 0 0-3 3v12a3 3 0 0 0 3 3 3 3 0 0 0 3-3 3 3 0 0 0-3-3H6a3 3 0 0 0-3 3 3 3 0 0 0 3 3 3 3 0 0 0 3-3V6a3 3 0 0 0-3-3 3 3 0 0 0-3 3 3 3 0 0 0 3 3h12a3 3 0 0 0 3-3 3 3 0 0 0-3-3z"></path>
      </svg>
    ),
  },
];

const LeadMagnetsSection = styled.section`
  padding: 100px 0;
  background-color: var(--white);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    width: 500px;
    height: 500px;
    background: var(--primary);
    opacity: 0.03;
    border-radius: 50%;
    top: -250px;
    right: -250px;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
  position: relative;
  z-index: 1;
`;

const SectionHeader = styled.div`
  text-align: center;
  margin-bottom: 60px;

  h2 {
    color: var(--secondary);
    margin-bottom: 15px;
  }

  p {
    font-size: 1.1rem;
    max-width: 700px;
    margin: 0 auto;
    color: var(--secondary);
  }
`;

const ContentGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 60px;
  align-items: center;

  @media (max-width: 992px) {
    grid-template-columns: 1fr;
    gap: 40px;
  }
`;

const ImageSection = styled.div`
  position: relative;

  img {
    width: 100%;
    border-radius: 10px;
    box-shadow: var(--box-shadow);
  }

  &::before {
    content: '';
    position: absolute;
    top: -15px;
    right: -15px;
    width: 100%;
    height: 100%;
    border: 3px solid var(--primary);
    border-radius: 10px;
    z-index: -1;
  }

  &::after {
    content: 'Exclusive Bonuses';
    position: absolute;
    bottom: -20px;
    left: 20px;
    background: var(--primary);
    color: var(--white);
    padding: 10px 20px;
    border-radius: 5px;
    font-weight: 600;
    box-shadow: 0 5px 15px rgba(203, 157, 118, 0.3);
  }
`;

const LeadMagnetsList = styled.div`
`;

const LeadMagnetItem = styled.div`
  margin-bottom: 25px;
  display: flex;
  gap: 20px;
  align-items: flex-start;
  padding-bottom: 25px;
  border-bottom: 1px solid rgba(0, 0, 0, 0.05);

  &:last-child {
    margin-bottom: 0;
    padding-bottom: 0;
    border-bottom: none;
  }

  svg {
    min-width: 50px;
    width: 50px;
    height: 50px;
    color: var(--primary);
    padding: 10px;
    background: rgba(203, 157, 118, 0.1);
    border-radius: 8px;
  }
`;

const LeadMagnetContent = styled.div`
  h4 {
    margin-bottom: 5px;
    color: var(--secondary);
  }

  p {
    color: var(--secondary);
    margin-bottom: 0;
  }
`;

const CTAContainer = styled.div`
  margin-top: 40px;
  text-align: center;
`;

const LeadMagnets = () => {
  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  const scrollToSection = (sectionId: string) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <LeadMagnetsSection ref={ref}>
      <Container>
        <SectionHeader className={inView ? 'fade-in' : ''}>
          <h2>Exclusive Bonuses When You Book Today</h2>
          <p>
            Schedule your free consultation now and get instant access to these valuable resources 
            to help you plan and maximize your smart home investment.
          </p>
        </SectionHeader>

        <ContentGrid>
          <ImageSection className={inView ? 'slide-up' : ''}>
            <img src={bonusImageUrl} alt="Smart home bonuses" />
          </ImageSection>

          <LeadMagnetsList>
            {leadMagnets.map((item, index) => (
              <LeadMagnetItem 
                key={index} 
                className={inView ? `slide-up delay-${index % 3}` : ''}
              >
                {item.icon}
                <LeadMagnetContent>
                  <h4>{item.title}</h4>
                  <p>{item.description}</p>
                </LeadMagnetContent>
              </LeadMagnetItem>
            ))}

            <CTAContainer className={inView ? 'fade-in delay-3' : ''}>
              <Button 
                primary 
                onClick={() => scrollToSection('contact')}
              >
                Claim Your Free Consultation + Bonuses
              </Button>
            </CTAContainer>
          </LeadMagnetsList>
        </ContentGrid>
      </Container>
    </LeadMagnetsSection>
  );
};

export default LeadMagnets;
