import React, { useEffect, useState, useRef } from 'react';
import styled, { keyframes, createGlobalStyle } from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

const GlobalVideoStyle = createGlobalStyle`
  .hero-video {
    filter: contrast(1.05) saturate(1.1);
    will-change: transform;
  }
`;

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const slideIn = keyframes`
  from {
    opacity: 0;
    transform: translateX(50px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
`;

const HeroContainer = styled.section`
  position: relative;
  color: var(--white);
  display: flex;
  height: 100vh;
  width: 100%;

  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const VideoSection = styled.div`
  flex: 1;
  position: relative;
  overflow: hidden;

  @media (max-width: 768px) {
    height: 40vh;
  }
`;

const VideoBackground = styled.div`
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 0;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(to right, rgba(0,0,0,0.5), rgba(0,0,0,0.2));
    z-index: 1;
  }

  video {
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
    transform: scale(1.05);
  }
`;

const ContentSection = styled.div`
  flex: 1;
  background: linear-gradient(135deg, #0F0F0F 0%, #1A1A1A 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0 40px;
  position: relative;
  z-index: 5;

  @media (max-width: 768px) {
    padding: 40px 20px;
  }
`;

const ContentContainer = styled.div`
  max-width: 600px;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  text-align: left;

  @media (max-width: 768px) {
    text-align: center;
    align-items: center;
  }
`;

const Logo = styled.div`
  position: absolute;
  top: 40px;
  left: 50px;
  font-size: 28px;
  font-weight: 600;
  color: var(--white);
  z-index: 10;
  display: flex;
  align-items: center;
  letter-spacing: 1px;
  opacity: 0.9;

  span {
    color: var(--primary);
  }

  @media (max-width: 768px) {
    left: 20px;
    font-size: 24px;
    top: 25px;
  }
`;

const MainHeading = styled.h1`
  font-size: clamp(3rem, 4vw, 4rem);
  font-weight: 300;
  line-height: 1.1;
  letter-spacing: 1px;
  margin-bottom: 25px;
  color: rgba(255, 255, 255, 0.95);
  animation: ${fadeIn} 1s ease-out 0.2s backwards;

  span {
    display: block;
    font-weight: 600;
    color: var(--primary);
    margin-top: 10px;
  }
`;

const SubHeading = styled.p`
  font-size: clamp(1rem, 1.2vw, 1.2rem);
  font-weight: 300;
  line-height: 1.6;
  margin: 0 0 30px 0;
  color: rgba(255, 255, 255, 0.85);
  animation: ${fadeIn} 1s ease-out 0.5s backwards;
  max-width: 90%;

  @media (max-width: 768px) {
    max-width: 100%;
    margin: 0 auto 30px;
  }
`;

const LeadForm = styled.div`
  background: rgba(255, 255, 255, 0.05);
  border-radius: 8px;
  padding: 25px;
  width: 100%;
  margin: 20px 0 30px;
  backdrop-filter: blur(5px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  animation: ${slideIn} 1s ease-out 0.6s backwards;
`;

const FormTitle = styled.h3`
  font-size: 1.2rem;
  font-weight: 500;
  margin-bottom: 15px;
  color: var(--primary);
`;

const InputContainer = styled.div`
  display: flex;
  gap: 10px;
  margin-bottom: 15px;

  @media (max-width: 480px) {
    flex-direction: column;
  }
`;

const Input = styled.input`
  flex: 1;
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 4px;
  padding: 12px 15px;
  color: var(--white);
  font-size: 1rem;

  &::placeholder {
    color: rgba(255, 255, 255, 0.5);
  }

  &:focus {
    outline: none;
    border-color: var(--primary);
  }
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 20px;
  animation: ${fadeIn} 1s ease-out 0.8s backwards;
  margin-top: 10px;

  @media (max-width: 480px) {
    flex-direction: column;
    width: 100%;
    gap: 15px;
  }
`;

const primaryButtonStyle: React.CSSProperties = {
  fontSize: '0.95rem',
  padding: '14px 35px',
  borderRadius: '4px',
  letterSpacing: '1px',
  fontWeight: '500',
  width: '100%'
};

const outlineButtonStyle: React.CSSProperties = {
  fontSize: '0.95rem',
  padding: '14px 35px',
  borderRadius: '4px',
  letterSpacing: '1px',
  fontWeight: '500',
  borderWidth: '1px',
  color: 'rgba(255, 255, 255, 0.9)',
  borderColor: 'rgba(255, 255, 255, 0.5)',
  backgroundColor: 'rgba(255, 255, 255, 0.05)'
};

const Hero = () => {
  const [isVisible, setIsVisible] = useState(true);
  const videoRef = useRef<HTMLVideoElement>(null);

  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: false,
  });

  useEffect(() => {
    setIsVisible(inView);
  }, [inView]);

  // Play video when component mounts
  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.play().catch(error => {
        console.log("Video autoplay failed:", error);
      });
    }
  }, []);

  const scrollToSection = (sectionId: string) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <HeroContainer ref={ref}>
      <GlobalVideoStyle />
      <Logo>
        Home <span>Vision</span>
      </Logo>

      <VideoSection>
        <VideoBackground>
          <video 
            ref={videoRef}
            autoPlay
            muted
            loop
            playsInline
            preload="auto"
            poster="/hero-poster.jpg"
            className="hero-video"
          >
            <source src="/video4.mp4" type="video/mp4" />
            <source src="/video4.mp4" type="video/mp4" media="(max-width: 768px)" />
            Your browser does not support the video tag.
          </video>
        </VideoBackground>
      </VideoSection>

      <ContentSection>
        <ContentContainer>
          <MainHeading className={isVisible ? 'fade-in' : ''}>
            Effortless Home
            <span>Intuitive Control</span>
          </MainHeading>

          <SubHeading>
            Experience the pinnacle of luxury living with bespoke smart home 
            automation, seamlessly integrated for ultimate comfort and convenience.
          </SubHeading>

          <LeadForm>
            <FormTitle>Get Your Free Smart Home Consultation</FormTitle>
            <InputContainer>
              <Input type="text" placeholder="Your Name" />
              <Input type="email" placeholder="Your Email" />
            </InputContainer>
            <Button 
              primary 
              fullWidth
              style={primaryButtonStyle}
            >
              Request Consultation
            </Button>
          </LeadForm>

          <ButtonContainer>
            <Button 
              primary 
              onClick={() => scrollToSection('contact')}
              style={primaryButtonStyle}
            >
              Request Consultation
            </Button>
            <Button 
              outline 
              onClick={() => scrollToSection('packages')}
              style={outlineButtonStyle}
            >
              Explore Services
            </Button>
          </ButtonContainer>
        </ContentContainer>
      </ContentSection>
    </HeroContainer>
  );
};

export default Hero;
