import React, { useEffect, useState } from 'react';
import styled, { keyframes } from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

// Updated hero image URL with more specific home automation focus
const heroImageUrl = 'https://images.unsplash.com/photo-1558002038-1055e2dae2c6?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80';

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const pulse = keyframes`
  0% {
    box-shadow: 0 0 0 0 rgba(203, 157, 118, 0.6);
  }
  70% {
    box-shadow: 0 0 0 15px rgba(203, 157, 118, 0);
  }
  100% {
    box-shadow: 0 0 0 0 rgba(203, 157, 118, 0);
  }
`;

const HeroContainer = styled.section`
  height: 100vh;
  min-height: 700px;
  position: relative;
  display: flex;
  align-items: center;
  color: var(--white);
  overflow: hidden;

  @media (max-width: 768px) {
    min-height: 100vh;
    height: auto;
    padding: 100px 0 80px;
  }
`;

const BackgroundImage = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 0;
  background: url(${heroImageUrl}) no-repeat center center;
  background-size: cover;

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(
      to right,
      rgba(33, 37, 41, 0.85) 0%,
      rgba(33, 37, 41, 0.7) 50%,
      rgba(33, 37, 41, 0.6) 100%
    );
    z-index: 1;
  }
`;

const ContentContainer = styled.div`
  width: 100%;
  max-width: 1400px;
  margin: 0 auto;
  padding: 0 50px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  position: relative;
  z-index: 2;

  @media (max-width: 1024px) {
    flex-direction: column;
    text-align: center;
    padding: 0 30px;
  }

  @media (max-width: 768px) {
    padding: 0 25px;
  }
`;

const Logo = styled.div`
  position: absolute;
  top: 30px;
  left: 50px;
  font-size: 28px;
  font-weight: 700;
  color: var(--white);
  z-index: 10;
  display: flex;
  align-items: center;

  span {
    color: var(--primary);
  }

  @media (max-width: 768px) {
    left: 20px;
    font-size: 24px;
    top: 20px;
  }
`;

const HeroContent = styled.div`
  width: 50%;
  max-width: 650px;
  position: relative;
  z-index: 1;
  animation: ${fadeIn} 1s ease-out;

  @media (max-width: 1024px) {
    width: 100%;
    max-width: 600px;
    margin-bottom: 40px;
  }

  @media (max-width: 768px) {
    margin-top: 40px;
  }
`;

const VideoInfoSection = styled.div`
  width: 35%;
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(10px);
  border-radius: 20px;
  padding: 40px;
  border: 1px solid rgba(255, 255, 255, 0.1);

  @media (max-width: 1024px) {
    width: 100%;
    max-width: 500px;
    padding: 30px;
  }

  @media (max-width: 768px) {
    padding: 25px;
  }
`;

const MainHeading = styled.h1`
  margin-bottom: 20px;
  font-size: 4rem;
  font-weight: 800;
  line-height: 1.1;
  letter-spacing: -0.5px;

  span {
    display: block;
    color: var(--primary);
  }

  @media (max-width: 1200px) {
    font-size: 3.5rem;
  }

  @media (max-width: 1024px) {
    font-size: 3rem;
  }

  @media (max-width: 768px) {
    font-size: 2.5rem;
  }

  @media (max-width: 480px) {
    font-size: 2rem;
  }
`;

const SubHeading = styled.div`
  font-size: 1.3rem;
  font-weight: 400;
  margin: 30px 0;
  line-height: 1.6;

  @media (max-width: 768px) {
    font-size: 1.1rem;
    margin: 20px 0;
  }

  @media (max-width: 480px) {
    font-size: 1rem;
  }
`;

const InfoTitle = styled.h3`
  font-size: 1.5rem;
  margin-bottom: 20px;
  color: var(--white);

  span {
    color: var(--primary);
  }

  @media (max-width: 768px) {
    font-size: 1.3rem;
  }
`;

const InfoList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;

  li {
    padding: 12px 0;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    display: flex;
    align-items: center;

    &:last-child {
      border-bottom: none;
    }

    &:before {
      content: 'âœ¦';
      color: var(--primary);
      margin-right: 15px;
    }
  }

  @media (max-width: 1024px) {
    li {
      justify-content: center;
      text-align: left;

      &:before {
        margin-right: 10px;
      }
    }
  }
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 20px;
  margin: 40px 0 0;

  @media (max-width: 768px) {
    flex-direction: column;
    width: 100%;
    gap: 15px;
  }
`;

const UrgencyContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 15px;
  margin-top: 30px;
  animation: ${fadeIn} 1s ease-out 0.5s both;

  @media (max-width: 1024px) {
    justify-content: center;
  }

  @media (max-width: 480px) {
    gap: 10px;
    margin-top: 20px;
  }
`;

const PulseCircle = styled.div`
  width: 50px;
  height: 50px;
  border-radius: 50%;
  background: var(--primary);
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-weight: 700;
  animation: ${pulse} 2s infinite;

  @media (max-width: 480px) {
    width: 40px;
    height: 40px;
    font-size: 0.9rem;
  }
`;

const UrgencyText = styled.div`
  font-size: 0.95rem;

  span {
    color: var(--primary);
    font-weight: 700;
  }

  @media (max-width: 480px) {
    font-size: 0.85rem;
  }
`;

const ScrollDown = styled.div`
  position: absolute;
  bottom: 30px;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 8px;
  cursor: pointer;
  z-index: 5;

  p {
    font-size: 0.9rem;
    font-weight: 500;
    letter-spacing: 1px;
    text-transform: uppercase;
  }

  @media (max-width: 768px) {
    display: none;
  }
`;

