import React, { useEffect, useState, useRef } from 'react';
import styled, { keyframes, createGlobalStyle } from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

const GlobalVideoStyle = createGlobalStyle`
  .hero-video {
    filter: contrast(1.05) saturate(1.1);
    will-change: transform;
  }
`;

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const slideIn = keyframes`
  from {
    opacity: 0;
    transform: translateX(50px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
`;

const HeroContainer = styled.section`
  position: relative;
  color: var(--white);
  display: flex;
  height: 100vh;
  width: 100%;
  background-color: #0a0a0a;

  @media (max-width: 768px) {
    flex-direction: column-reverse;
  }
`;

const VideoSection = styled.div`
  flex: 0.45;
  position: relative;
  overflow: hidden;

  @media (max-width: 768px) {
    height: 45vh;
  }
`;

const VideoBackground = styled.div`
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 0;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(to left, rgba(0,0,0,0.1), rgba(0,0,0,0.4));
    z-index: 1;
  }

  video {
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
    transform: scale(1.05);
  }
`;

const ContentSection = styled.div`
  flex: 0.55;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0 60px;
  position: relative;
  z-index: 5;

  @media (max-width: 1200px) {
    padding: 0 40px;
  }

  @media (max-width: 768px) {
    flex: 1;
    padding: 40px 30px;
  }
`;

const ContentInner = styled.div`
  max-width: 520px;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  text-align: left;
`;

const Logo = styled.div`
  position: absolute;
  top: 40px;
  left: 50px;
  font-size: 24px;
  font-weight: 500;
  color: var(--white);
  z-index: 10;
  display: flex;
  align-items: center;
  letter-spacing: 1px;

  span {
    color: var(--primary);
  }

  @media (max-width: 768px) {
    left: 20px;
    font-size: 22px;
    top: 20px;
  }
`;

const MainHeading = styled.h1`
  font-size: clamp(2.8rem, 4vw, 3.2rem);
  font-weight: 300;
  line-height: 1.1;
  letter-spacing: 0.5px;
  margin-bottom: 25px;
  color: rgba(255, 255, 255, 0.95);
  animation: ${fadeIn} 1s ease-out forwards;

  strong {
    font-weight: 500;
    color: var(--primary);
    position: relative;
    display: inline-block;
    padding-bottom: 4px;

    &::after {
      content: '';
      position: absolute;
      bottom: 0;
      left: 0;
      width: 100%;
      height: 1px;
      background-color: var(--primary);
    }
  }
`;

const SubHeading = styled.p`
  font-size: 1.1rem;
  font-weight: 300;
  line-height: 1.6;
  margin: 0 0 40px 0;
  color: rgba(255, 255, 255, 0.8);
  animation: ${fadeIn} 1s ease-out 0.3s forwards;
  max-width: 95%;
`;

const LeadMagnet = styled.div`
  width: 100%;
  margin: 0 0 50px;
  animation: ${fadeIn} 1s ease-out 0.5s forwards;
`;

const ValueStatement = styled.p`
  font-size: 1rem;
  line-height: 1.5;
  margin-bottom: 25px;
  padding-left: 15px;
  border-left: 2px solid var(--primary);
  color: rgba(255, 255, 255, 0.85);
`;

const FormContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  gap: 10px;
  margin-bottom: 15px;
`;

const Input = styled.input`
  background: rgba(255, 255, 255, 0.07);
  border: 1px solid rgba(255, 255, 255, 0.15);
  border-radius: 3px;
  padding: 15px;
  color: var(--white);
  font-size: 0.95rem;
  width: 100%;
  transition: border-color 0.3s ease;

  &::placeholder {
    color: rgba(255, 255, 255, 0.4);
  }

  &:focus {
    outline: none;
    border-color: var(--primary);
  }
`;

const CheckboxContainer = styled.div`
  display: flex;
  align-items: flex-start;
  margin-bottom: 20px;
`;

const StyledCheckbox = styled.input`
  margin-right: 10px;
  margin-top: 4px;
  accent-color: var(--primary);
`;

const CheckboxLabel = styled.label`
  font-size: 0.85rem;
  color: rgba(255, 255, 255, 0.7);
  line-height: 1.4;
`;

const ExclusiveBadge = styled.div`
  display: inline-block;
  padding: 6px 12px;
  background-color: rgba(203, 157, 118, 0.15);
  border: 1px solid rgba(203, 157, 118, 0.3);
  border-radius: 3px;
  font-size: 0.8rem;
  text-transform: uppercase;
  letter-spacing: 1px;
  color: var(--primary);
  margin-bottom: 20px;
  font-weight: 500;
`;

const Hero = () => {
  const [isVisible, setIsVisible] = useState(true);
  const videoRef = useRef<HTMLVideoElement>(null);

  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: false,
  });

  useEffect(() => {
    setIsVisible(inView);
  }, [inView]);

  // Play video when component mounts
  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.play().catch(error => {
        console.log("Video autoplay failed:", error);
      });
    }
  }, []);

  const scrollToSection = (sectionId: string) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <HeroContainer ref={ref}>
      <GlobalVideoStyle />
      <Logo>
        Home <span>Vision</span>
      </Logo>

      <ContentSection>
        <ContentInner>
          <ExclusiveBadge>Exclusive Offer</ExclusiveBadge>

          <MainHeading>
            Transform your home with <strong>intelligent luxury</strong>
          </MainHeading>

          <SubHeading>
            Elevate your lifestyle with a curated smart home system designed for refined living and effortless control.
          </SubHeading>

          <LeadMagnet>
            <ValueStatement>
              Receive a complimentary Smart Home Design Consultation ($495 value) and personalized investment plan when you book today.
            </ValueStatement>

            <FormContainer>
              <Input type="text" placeholder="Full Name" />
              <Input type="email" placeholder="Email Address" />
              <Input type="tel" placeholder="Phone Number" />
            </FormContainer>

            <CheckboxContainer>
              <StyledCheckbox type="checkbox" id="subscribe" defaultChecked />
              <CheckboxLabel htmlFor="subscribe">
                I'd like to receive occasional updates on smart home innovations and exclusive offers. You can unsubscribe at any time.
              </CheckboxLabel>
            </CheckboxContainer>

            <Button 
              primary 
              fullWidth
              style={{
                fontSize: '0.95rem',
                padding: '16px',
                borderRadius: '3px',
                fontWeight: '500',
                letterSpacing: '0.5px',
              }}
              onClick={() => scrollToSection('contact')}
            >
              Reserve Your Consultation
            </Button>
          </LeadMagnet>
        </ContentInner>
      </ContentSection>

      <VideoSection>
        <VideoBackground>
          <video 
            ref={videoRef}
            autoPlay
            muted
            loop
            playsInline
            preload="auto"
            poster="/hero-poster.jpg"
            className="hero-video"
          >
            <source src="/video5.mp4" type="video/mp4" />
            <source src="/video5.mp4" type="video/mp4" media="(max-width: 768px)" />
            Your browser does not support the video tag.
          </video>
        </VideoBackground>
      </VideoSection>
    </HeroContainer>
  );
};

export default Hero;
