import React, { useEffect, useState, useRef } from 'react';
import styled, { keyframes, createGlobalStyle, css } from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

// Enhanced animations
const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const fadeInUp = keyframes`
  from {
    opacity: 0;
    transform: translateY(15px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const shimmer = keyframes`
  0% {
    background-position: -468px 0;
  }
  100% {
    background-position: 468px 0;
  }
`;

const float = keyframes`
  0% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
  100% {
    transform: translateY(0px);
  }
`;

const pulse = keyframes`
  0% {
    box-shadow: 0 0 0 0 rgba(203, 157, 118, 0.4);
  }
  70% {
    box-shadow: 0 0 0 15px rgba(203, 157, 118, 0);
  }
  100% {
    box-shadow: 0 0 0 0 rgba(203, 157, 118, 0);
  }
`;

const slideInRight = keyframes`
  from {
    transform: translateX(50px);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
`;

// Global styles for enhanced visuals
const GlobalStyle = createGlobalStyle`
  .hero-video {
    filter: contrast(1.1) brightness(0.7) saturate(0.9);
    will-change: transform;
    transition: all 1.5s ease-in-out;
  }

  .hero-video.in-view {
    filter: contrast(1.1) brightness(0.7) saturate(1);
    transform: scale(1.05);
  }

  @keyframes gradientShift {
    0% {
      background-position: 0% 50%;
    }
    50% {
      background-position: 100% 50%;
    }
    100% {
      background-position: 0% 50%;
    }
  }
`;

const HeroContainer = styled.section`
  position: relative;
  color: var(--white);
  display: flex;
  height: 100vh;
  width: 100%;
  background-color: #000000;
  overflow: hidden;

  @media (max-width: 768px) {
    flex-direction: column-reverse;
  }
`;

const VideoSection = styled.div`
  flex: 0.6;
  position: relative;
  overflow: hidden;
  height: 100vh;
  transform-style: preserve-3d;
  perspective: 1000px;

  @media (max-width: 768px) {
    height: 50vh;
  }
`;

const VideoBackground = styled.div`
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 0;
  overflow: hidden;
  background-color: #000000;
  transform: translateZ(-50px) scale(1.15);

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(to left, rgba(0,0,0,0.4), rgba(0,0,0,0.8));
    z-index: 1;
  }

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle at 50% 50%, transparent 0%, rgba(0,0,0,0.7) 100%);
    z-index: 2;
    opacity: 0.7;
  }

  video {
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
    opacity: 0.75;
  }
`;

const ParticlesOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 2;
  pointer-events: none;
  overflow: hidden;
`;

const Particle = styled.div<{ size: number, posX: number, posY: number, speed: number }>`
  position: absolute;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  background: rgba(255, 255, 255, 0.4);
  border-radius: 50%;
  top: ${props => props.posY}%;
  left: ${props => props.posX}%;
  animation: ${float} ${props => props.speed}s ease-in-out infinite;
  opacity: 0.2;
`;

const ContentSection = styled.div`
  flex: 0.4;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0 50px;
  position: relative;
  z-index: 10;
  background-color: #000000;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle at 30% 50%, rgba(203, 157, 118, 0.05) 0%, rgba(0, 0, 0, 0) 50%);
    z-index: -1;
    animation: ${shimmer} 10s linear infinite;
  }

  @media (max-width: 1200px) {
    padding: 0 30px;
  }

  @media (max-width: 768px) {
    flex: 1;
    padding: 30px 25px;
  }
`;

const ContentInner = styled.div`
  max-width: 440px;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  text-align: left;
  position: relative;
`;

const Logo = styled.div`
  position: absolute;
  top: 40px;
  left: 50px;
  font-size: 24px;
  font-weight: 500;
  color: var(--white);
  z-index: 20;
  display: flex;
  align-items: center;
  letter-spacing: 1px;

  span {
    color: var(--primary);
    position: relative;

    &::after {
      content: '';
      position: absolute;
      bottom: -3px;
      left: 0;
      width: 100%;
      height: 1px;
      background: linear-gradient(90deg, rgba(203, 157, 118, 0), rgba(203, 157, 118, 1), rgba(203, 157, 118, 0));
      animation: ${shimmer} 3s infinite linear;
    }
  }

  @media (max-width: 768px) {
    left: 20px;
    font-size: 22px;
    top: 20px;
  }
`;

const HeroHighlight = styled.span<{ delay: number }>`
  background: linear-gradient(120deg, rgba(203, 157, 118, 0), rgba(203, 157, 118, 0.2), rgba(203, 157, 118, 0));
  background-size: 200% 100%;
  animation: gradientShift 3s ease infinite;
  animation-delay: ${props => props.delay}s;
  padding: 0 5px;
  border-radius: 2px;
`;

const ExclusiveBadge = styled.div`
  display: inline-flex;
  align-items: center;
  padding: 6px 12px;
  background-color: rgba(203, 157, 118, 0.12);
  border: 1px solid rgba(203, 157, 118, 0.25);
  border-radius: 3px;
  font-size: 0.8rem;
  text-transform: uppercase;
  letter-spacing: 1px;
  color: var(--primary);
  margin-bottom: 20px;
  font-weight: 500;
  position: relative;
  overflow: hidden;
  animation: ${fadeIn} 1s ease-out forwards;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -150%;
    width: 40%;
    height: 100%;
    background: linear-gradient(
      to right,
      rgba(255, 255, 255, 0) 0%,
      rgba(255, 255, 255, 0.3) 50%,
      rgba(255, 255, 255, 0) 100%
    );
    transform: skewX(-20deg);
    animation: ${shimmer} 3s infinite;
  }

  &::after {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 10px;
    height: 10px;
    background: rgba(255, 255, 255, 0.8);
    border-radius: 50%;
    opacity: 0;
    filter: blur(5px);
    animation: ${pulse} 4s infinite;
  }
`;

const StarIcon = styled.span`
  margin-right: 6px;
  display: inline-block;
  animation: ${pulse} 2s infinite;
`;

const MainHeading = styled.h1`
  font-size: clamp(2.4rem, 3.5vw, 2.8rem);
  font-weight: 300;
  line-height: 1.1;
  letter-spacing: 0.5px;
  margin-bottom: 20px;
  color: rgba(255, 255, 255, 0.95);
  animation: ${fadeIn} 1s ease-out forwards;

  strong {
    font-weight: 500;
    color: var(--primary);
    position: relative;
    display: inline-block;
    padding-bottom: 4px;

    &::after {
      content: '';
      position: absolute;
      bottom: 0;
      left: 0;
      width: 100%;
      height: 1px;
      background-color: rgba(203, 157, 118, 0.7);
    }
  }
`;

const SubHeading = styled.p`
  font-size: 1rem;
  font-weight: 300;
  line-height: 1.5;
  margin: 0 0 30px 0;
  color: rgba(255, 255, 255, 0.8);
  animation: ${fadeIn} 1s ease-out 0.3s forwards;
  max-width: 95%;
`;

const LeadMagnet = styled.div`
  width: 100%;
  margin: 0 0 30px;
  animation: ${fadeIn} 1s ease-out 0.5s forwards;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: -10px;
    right: -10px;
    width: 40%;
    height: 40%;
    background: radial-gradient(circle, rgba(203, 157, 118, 0.1) 0%, rgba(0, 0, 0, 0) 70%);
    z-index: -1;
  }
`;

const ValueStatement = styled.p`
  font-size: 0.95rem;
  line-height: 1.5;
  margin-bottom: 20px;
  padding-left: 12px;
  border-left: 2px solid rgba(203, 157, 118, 0.7);
  color: rgba(255, 255, 255, 0.85);
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 0;
    width: 30px;
    height: 1px;
    background: linear-gradient(90deg, rgba(203, 157, 118, 0.7), rgba(0, 0, 0, 0));
  }
`;

const HighlightValue = styled.span`
  color: var(--primary);
  font-weight: 500;
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -2px;
    left: 0;
    width: 100%;
    height: 1px;
    background-color: rgba(203, 157, 118, 0.4);
  }
`;

const FormContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  gap: 8px;
  margin-bottom: 12px;
  position: relative;
`;

const Input = styled.input`
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.12);
  border-radius: 3px;
  padding: 15px;
  color: var(--white);
  font-size: 0.95rem;
  width: 100%;
  transition: all 0.3s ease;
  position: relative;

  &::placeholder {
    color: rgba(255, 255, 255, 0.4);
    transition: all 0.3s ease;
  }

  &:focus {
    outline: none;
    border-color: var(--primary);
    background: rgba(255, 255, 255, 0.07);

    &::placeholder {
      opacity: 0.7;
      transform: translateX(5px);
    }
  }
`;

const AnimatedButton = styled(Button)`
  position: relative;
  overflow: hidden;
  transition: all 0.3s ease;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(
      90deg,
      transparent,
      rgba(255, 255, 255, 0.2),
      transparent
    );
    transition: all 0.5s ease;
  }

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(203, 157, 118, 0.2);

    &::before {
      left: 100%;
    }
  }
`;

const BrandsSection = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  background-color: rgba(0, 0, 0, 0.7);
  backdrop-filter: blur(10px);
  padding: 25px 0;
  z-index: 10;

  @media (max-width: 768px) {
    position: relative;
    padding: 20px 0;
    background-color: #000000;
  }
`;

const BrandsContainer = styled.div`
  max-width: 1280px;
  margin: 0 auto;
  padding: 0;
`;

const BrandsTitle = styled.p`
  font-size: 0.75rem;
  text-transform: uppercase;
  letter-spacing: 1.5px;
  color: rgba(255, 255, 255, 0.45);
  text-align: center;
  margin-bottom: 20px;
  font-weight: 500;
  position: relative;
  display: inline-block;
  left: 50%;
  transform: translateX(-50%);

  &::before, &::after {
    content: '';
    position: absolute;
    top: 50%;
    width: 20px;
    height: 1px;
    background: linear-gradient(90deg, rgba(203, 157, 118, 0.3), rgba(203, 157, 118, 0));
  }

  &::before {
    right: -30px;
  }

  &::after {
    left: -30px;
    transform: rotate(180deg);
  }
`;

const BrandsGrid = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  gap: 40px;
  overflow-x: auto;
  padding: 0 50px;
  scrollbar-width: none; /* Firefox */
  -ms-overflow-style: none; /* IE and Edge */
  max-width: 1200px;
  margin: 0 auto;

  &::-webkit-scrollbar {
    display: none; /* Chrome, Safari, Opera */
  }

  @media (max-width: 992px) {
    justify-content: space-between;
    padding: 0 40px;
  }

  @media (max-width: 768px) {
    gap: 35px;
    padding: 0 20px;
    justify-content: flex-start;
  }
`;

const BrandLogo = styled.img<{ delay: number }>`
  height: 32px;
  width: auto;
  opacity: 0.65;
  transition: all 0.3s ease;
  filter: brightness(0) invert(1) drop-shadow(0 0 1px rgba(255, 255, 255, 0.1));
  animation: ${fadeInUp} 0.6s ease-out forwards;
  animation-delay: ${props => props.delay}s;
  flex-shrink: 0;
  transform: perspective(500px) translateZ(0);

  &:hover {
    opacity: 0.95;
    transform: perspective(500px) translateZ(20px);
  }

  @media (max-width: 768px) {
    height: 28px;
  }

  @media (max-width: 480px) {
    height: 24px;
  }
`;

// Generate random particles for background effect
const generateParticles = (count: number) => {
  const particles = [];
  for (let i = 0; i < count; i++) {
    particles.push({
      id: i,
      size: Math.random() * 3 + 1,
      posX: Math.random() * 100,
      posY: Math.random() * 100,
      speed: Math.random() * 8 + 3
    });
  }
  return particles;
};

const Hero = () => {
  const [isVisible, setIsVisible] = useState(true);
  const videoRef = useRef<HTMLVideoElement>(null);
  const particles = generateParticles(30);

  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: false,
  });

  useEffect(() => {
    setIsVisible(inView);

    if (videoRef.current) {
      if (inView) {
        videoRef.current.classList.add('in-view');
      } else {
        videoRef.current.classList.remove('in-view');
      }
    }
  }, [inView]);

  // Play video when component mounts
  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.play().catch(error => {
        console.log("Video autoplay failed:", error);
      });
    }
  }, []);

  const scrollToSection = (sectionId: string) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // Update the brand logos with a better selection for a balanced row
  const brandLogos = [
    { src: "/control4.png", alt: "Control4" },
    { src: "/lutron.png", alt: "Lutron" },
    { src: "/sonos.png", alt: "Sonos" },
    { src: "/sony.png", alt: "Sony" },
    { src: "/jvc.png", alt: "JVC" },
    { src: "/leon.png", alt: "Leon" },
    { src: "/senhizer.png", alt: "Sennheiser" },
    { src: "/seura.png", alt: "Seura" }
  ];

  return (
    <>
      <GlobalStyle />
      <HeroContainer ref={ref}>
        <Logo>
          Home <span>Vision</span>
        </Logo>

        <ContentSection>
          <ContentInner>
            <ExclusiveBadge>
              <StarIcon>â˜…</StarIcon>
              Exclusive Offer
            </ExclusiveBadge>

            <MainHeading>
              Transform with <strong>intelligent luxury</strong>
            </MainHeading>

            <SubHeading>
              Curated smart home systems designed for 
              <HeroHighlight delay={0}> refined living </HeroHighlight>
              and
              <HeroHighlight delay={1.5}> effortless control</HeroHighlight>.
            </SubHeading>

            <LeadMagnet>
              <ValueStatement>
                Complimentary Smart Home Design Consultation <HighlightValue>($495 value)</HighlightValue> with personalized investment plan.
              </ValueStatement>

              <FormContainer>
                <Input type="text" placeholder="Full Name" />
                <Input type="email" placeholder="Email Address" />
              </FormContainer>

              <AnimatedButton 
                primary 
                fullWidth
                style={{
                  fontSize: '0.9rem',
                  padding: '14px',
                  borderRadius: '3px',
                  fontWeight: '500',
                }}
                onClick={() => scrollToSection('contact')}
              >
                Reserve Consultation
              </AnimatedButton>
            </LeadMagnet>
          </ContentInner>
        </ContentSection>

        <VideoSection>
          <VideoBackground>
            <video 
              ref={videoRef}
              autoPlay
              muted
              loop
              playsInline
              preload="auto"
              poster="/hero-poster.jpg"
              className="hero-video"
            >
              <source src="/video5.mp4" type="video/mp4" />
              <source src="/video5.mp4" type="video/mp4" media="(max-width: 768px)" />
              Your browser does not support the video tag.
            </video>
          </VideoBackground>

          <ParticlesOverlay>
            {particles.map(particle => (
              <Particle 
                key={particle.id}
                size={particle.size}
                posX={particle.posX}
                posY={particle.posY}
                speed={particle.speed}
              />
            ))}
          </ParticlesOverlay>
        </VideoSection>

        <BrandsSection>
          <BrandsContainer>
            <BrandsTitle>Trusted Technology Partners</BrandsTitle>
            <BrandsGrid>
              {brandLogos.map((logo, index) => (
                <BrandLogo 
                  key={logo.alt}
                  src={logo.src}
                  alt={logo.alt}
                  delay={0.1 + (index * 0.05)}
                />
              ))}
            </BrandsGrid>
          </BrandsContainer>
        </BrandsSection>
      </HeroContainer>
    </>
  );
};

export default Hero;
