import React, { useEffect, useState, useRef } from 'react';
import styled, { keyframes } from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const HeroContainer = styled.section`
  height: 100vh;
  min-height: 700px;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--white);
  overflow: hidden;

  @media (max-width: 768px) {
    min-height: 100vh;
    height: auto;
  }
`;

const VideoBackground = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 0;

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(
      180deg, 
      rgba(0, 0, 0, 0.2) 0%, 
      rgba(0, 0, 0, 0.7) 100%
    );
    z-index: 1;
  }

  video {
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
  }
`;

const ContentContainer = styled.div`
  width: 90%;
  max-width: 900px;
  padding: 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  position: relative;
  z-index: 2;

  @media (max-width: 768px) {
    width: 85%;
    padding: 120px 0;
  }
`;

const Logo = styled.div`
  position: absolute;
  top: 40px;
  left: 50px;
  font-size: 28px;
  font-weight: 600;
  color: var(--white);
  z-index: 10;
  display: flex;
  align-items: center;
  letter-spacing: 1px;
  opacity: 0.9;

  span {
    color: var(--primary);
  }

  @media (max-width: 768px) {
    left: 20px;
    font-size: 24px;
    top: 25px;
  }
`;

const MainHeading = styled.h1`
  font-size: clamp(3rem, 8vw, 6.5rem);
  font-weight: 100;
  line-height: 1.05;
  letter-spacing: 1px;
  margin-bottom: 40px;
  color: rgba(255, 255, 255, 0.95);
  animation: ${fadeIn} 1s ease-out 0.2s backwards;

  span {
    display: block;
    font-weight: 500;
    color: var(--primary);
    margin-top: 8px;
  }
`;

const SubHeading = styled.p`
  font-size: clamp(1rem, 2.5vw, 1.4rem);
  font-weight: 300;
  line-height: 1.7;
  max-width: 650px;
  margin: 0 auto 50px auto;
  color: rgba(255, 255, 255, 0.85);
  animation: ${fadeIn} 1s ease-out 0.5s backwards;
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 25px;
  justify-content: center;
  animation: ${fadeIn} 1s ease-out 0.8s backwards;

  @media (max-width: 480px) {
     flex-direction: column;
     width: 90%;
     max-width: 300px;
     gap: 15px;
     margin: 0 auto;
  }
`;

const primaryButtonStyle: React.CSSProperties = {
  fontSize: '1rem',
  padding: '16px 40px',
  borderRadius: '50px',
  letterSpacing: '1.5px',
  textTransform: 'uppercase',
  fontWeight: '500',
  boxShadow: '0 8px 25px rgba(203, 157, 118, 0.2)',
  transition: 'transform 0.3s ease, box-shadow 0.3s ease',
};

const outlineButtonStyle: React.CSSProperties = {
  fontSize: '1rem',
  padding: '16px 40px',
  borderRadius: '50px',
  letterSpacing: '1.5px',
  textTransform: 'uppercase',
  fontWeight: '500',
  borderWidth: '1px',
  color: 'rgba(255, 255, 255, 0.9)',
  borderColor: 'rgba(255, 255, 255, 0.5)',
  backgroundColor: 'rgba(255, 255, 255, 0.05)',
  backdropFilter: 'blur(5px)',
  transition: 'background-color 0.3s ease, border-color 0.3s ease',
};

const ScrollDown = styled.div`
  position: absolute;
  bottom: 40px;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
  cursor: pointer;
  z-index: 5;
  font-size: 0.8rem;
  letter-spacing: 2px;
  text-transform: uppercase;
  opacity: 0.7;
  animation: ${fadeIn} 1.5s ease-out 1.2s backwards;

  @media (max-width: 768px) {
    display: none;
  }
`;

const ArrowDown = styled.div`
  width: 20px;
  height: 20px;
  display: flex;
  justify-content: center;

  &::after {
    content: '';
    width: 8px;
    height: 8px;
    border-right: 1.5px solid var(--white);
    border-bottom: 1.5px solid var(--white);
    transform: rotate(45deg);
    animation: bounce 2.5s infinite ease-in-out;
  }

  @keyframes bounce {
    0%, 100% {
      transform: translateY(0) rotate(45deg);
      opacity: 1;
    }
    50% {
      transform: translateY(8px) rotate(45deg);
      opacity: 0.5;
    }
  }
`;

const Hero = () => {
  const [isVisible, setIsVisible] = useState(true);
  const videoRef = useRef<HTMLVideoElement>(null);

  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: false,
  });

  useEffect(() => {
    setIsVisible(inView);
  }, [inView]);

  // Play video when component mounts
  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.play().catch(error => {
        console.log("Video autoplay failed:", error);
      });
    }
  }, []);

  const scrollToSection = (sectionId: string) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <HeroContainer ref={ref}>
      <Logo>
        Home <span>Vision</span>
      </Logo>

      <VideoBackground>
        <video 
          ref={videoRef}
          autoPlay
          muted
          loop
          playsInline
          poster="/path/to/poster-image.jpg"
        >
          <source src="/video2.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      </VideoBackground>

      <ContentContainer>
        <MainHeading className={isVisible ? 'fade-in' : ''}>
          Effortless Home
          <span>Intuitive Control</span>
        </MainHeading>

        <SubHeading>
          Experience the pinnacle of luxury living with bespoke smart home 
          automation, seamlessly integrated for ultimate comfort and convenience.
        </SubHeading>

        <ButtonContainer>
          <Button 
            primary 
            onClick={() => scrollToSection('contact')}
            style={primaryButtonStyle}
          >
            Request Consultation
          </Button>
          <Button 
            outline 
            onClick={() => scrollToSection('packages')}
            style={outlineButtonStyle}
          >
            Explore Services
          </Button>
        </ButtonContainer>
      </ContentContainer>

      <ScrollDown onClick={() => scrollToSection('about')}>
        Explore
        <ArrowDown />
      </ScrollDown>
    </HeroContainer>
  );
};

export default Hero;
