const Hero = () => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const [particles] = useState(() => generateParticles(30));
  const [videoError, setVideoError] = useState(false);
  const [formData, setFormData] = useState({ name: '', phone: '' });
  const [timeLeft, setTimeLeft] = useState(15 * 60); // 15 minutes in seconds
  const [validationErrors, setValidationErrors] = useState<{name?: boolean, phone?: boolean}>({});

  const [contentRef, contentInView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const [featuresRef, featuresInView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const [scrollRef, scrollInView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const [brandLogoRef, brandLogoInView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const [formRef, formInView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const [videoInView, setVideoInView] = useState(false);

  // Split text for animated reveal
  const titleWords = ["Transform", "Your", "Home"];

  useEffect(() => {
    setTimeout(() => {
      setVideoInView(true);
    }, 500);

    if (videoRef.current) {
      videoRef.current.play().catch((err) => {
        console.error("Video playback error:", err);
        setVideoError(true);
      });
    }

    // Timer countdown effect
    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timer);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs < 10 ? '0' : ''}${secs}`;
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    // Clear validation error when user types
    if (validationErrors[name as keyof typeof validationErrors]) {
      setValidationErrors(prev => ({ ...prev, [name]: false }));
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Simple validation
    const errors: {name?: boolean, phone?: boolean} = {};
    if (!formData.name.trim()) {
      errors.name = true;
    }
    if (!formData.phone.trim() || !/^[0-9]{10,}$/.test(formData.phone.replace(/[^0-9]/g, ''))) {
      errors.phone = true;
    }

    if (Object.keys(errors).length > 0) {
      setValidationErrors(errors);
      return;
    }

    // In a real app, you would send this data to your server
    console.log('Form submitted:', formData);

    // Mock successful submission
    alert('Thank you for your interest! Our team will contact you shortly.');
    setFormData({ name: '', phone: '' });
  };

  const scrollToSection = (sectionId: string) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section
      id="hero"
      className="relative flex h-screen w-full overflow-hidden bg-[#0a0a0a] text-white md:flex-row flex-col-reverse"
    >
      {/* Logo */}
      <div className="absolute left-4 top-4 z-20 flex items-center text-2xl font-medium tracking-wide text-white sm:left-12 sm:top-10">
        <svg viewBox="0 0 24 24" fill="currentColor" className="mr-2 h-5 w-5 fill-[#cb9d76]">
          <path d="M12 2L1 21h22L12 2zm0 4.2L20.1 19h-16L12 6.2z" />
        </svg>
        HOME
        <span className="relative text-[#cb9d76] after:absolute after:bottom-0 after:left-0 after:z-[-1] after:h-[10px] after:w-full after:rounded-md after:bg-[#cb9d76]/10">
          VISION
        </span>
      </div>

      {/* Content Section */}
      <div className="relative z-10 flex flex-[0.4] flex-col items-center justify-between overflow-hidden bg-[#0a0a0a] px-4 py-6 sm:px-12 md:px-[50px] md:py-0 lg:px-[50px]">
        <ContentInner ref={contentRef}>
          <MainHeading inView={contentInView}>
            {titleWords.map((word, i) => (
              <span key={i} className="reveal-text">{word} </span>
            ))}
            <span className="reveal-text">with <span>Smart Technology</span></span>
          </MainHeading>

          <Subtitle inView={contentInView}>
            Elevate your lifestyle with <strong>seamless, integrated</strong> home automation 
            systems designed for <WordHighlight>comfort</WordHighlight>, <WordHighlight>security</WordHighlight>, 
            and <WordHighlight>efficiency</WordHighlight>.
          </Subtitle>

          <LeadFormContainer ref={formRef} inView={formInView}>
            <FormTitle>Get Your <span>Free Consultation</span></FormTitle>

            <LimitedOffer>
              <OfferText>
                <strong>Limited Time:</strong> 20% off installation
              </OfferText>
              <Timer>{formatTime(timeLeft)}</Timer>
            </LimitedOffer>

            <form onSubmit={handleSubmit}>
              <InputRow>
                <InputGroup>
                  <FormLabel>Your Name</FormLabel>
                  <FormInput 
                    type="text" 
                    placeholder="Enter your name" 
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    className={validationErrors.name ? 'invalid' : ''}
                  />
                </InputGroup>

                <InputGroup>
                  <FormLabel>Phone Number</FormLabel>
                  <FormInput 
                    type="tel" 
                    placeholder="Enter your phone"
                    name="phone"
                    value={formData.phone}
                    onChange={handleInputChange}
                    className={validationErrors.phone ? 'invalid' : ''}
                  />
                </InputGroup>
              </InputRow>

              <BenefitsList>
                <BenefitItem>
                  <CheckIcon />
                  Free in-home assessment ($150 value)
                </BenefitItem>
                <BenefitItem>
                  <CheckIcon />
                  Personalized smart home plan
                </BenefitItem>
                <BenefitItem>
                  <CheckIcon />
                  No obligation quote
                </BenefitItem>
              </BenefitsList>

              <SubmitButton type="submit">Schedule Consultation</SubmitButton>
            </form>

            <SocialProof>
              <svg viewBox="0 0 24 24" fill="currentColor">
                <path d="M12 17.27L18.18 21l-1.64-7.03L22 9.24l-7.19-.61L12 2 9.19 8.63 2 9.24l5.46 4.73L5.82 21z" />
              </svg>
              <span>Join 500+ homeowners who upgraded this year</span>
            </SocialProof>
          </LeadFormContainer>
        </ContentInner>

        <BrandLogoSection ref={brandLogoRef} inView={brandLogoInView}>
          <BrandLabel>Trusted Technology Partners</BrandLabel>
          <BrandLogoContainer>
            <LogoRow>
              {[...brandLogos, ...brandLogos].map((logo, index) => (
                <BrandLogo key={`${logo.alt}-${index}`}>
                  <img src={logo.src} alt={logo.alt} />
                </BrandLogo>
              ))}
            </LogoRow>
          </BrandLogoContainer>
        </BrandLogoSection>
      </div>

      {/* Video Section */}
      <div className="relative flex-[0.6] overflow-hidden md:h-screen h-[50vh] perspective-1000 preserve-3d">
        <div className="absolute top-0 left-0 z-0 h-full w-full overflow-hidden bg-[#0a0a0a] scale-[1.15] -translate-z-[50px] before:absolute before:top-0 before:left-0 before:h-full before:w-full before:bg-gradient-to-l before:from-[#0a0a0a]/20 before:to-[#0a0a0a]/40 before:z-[1] after:absolute after:top-0 after:left-0 after:h-full after:w-full after:bg-radial after:from-transparent after:to-[#0a0a0a]/40 after:opacity-50 after:z-[2]">
          {!videoError ? (
            <video
              ref={videoRef}
              autoPlay
              muted
              loop
              playsInline
              className={`h-full w-full object-cover object-center transition-all duration-1500 ease-in-out ${videoInView ? "scale-105" : ""}`}
              onError={() => setVideoError(true)}
            >
              <source src="/video1.mp4" type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          ) : (
            <div className="flex h-full w-full items-center justify-center bg-gradient-to-br from-[#0a0a0a] to-[#1a1a1a]">
              <p className="text-white/50">Video could not be loaded</p>
            </div>
          )}
        </div>

        {/* Particles Overlay */}
        <div className="pointer-events-none absolute top-0 left-0 z-[2] h-full w-full overflow-hidden">
          {particles.map((particle, index) => (
            <div
              key={index}
              className="absolute rounded-full bg-[#cb9d76]/20 opacity-20"
              style={{
                width: `${particle.size}px`,
                height: `${particle.size}px`,
                top: `${particle.posY}%`,
                left: `${particle.posX}%`,
                animation: `float ${particle.speed}s ease-in-out infinite`,
              }}
            />
          ))}
        </div>

        {/* Features Bar */}
        <div
          ref={featuresRef}
          className={`absolute bottom-10 right-12 z-10 hidden gap-6 transition-all duration-800 delay-800 md:flex ${featuresInView ? "translate-y-0 opacity-100" : "translate-y-8 opacity-0"}`}
        >
          <div className="flex items-center gap-2.5">
            <ActivityIcon />
            <span className="text-sm font-medium text-white/80">Energy Efficient</span>
          </div>

          <div className="flex items-center gap-2.5">
            <ShieldIcon />
            <span className="text-sm font-medium text-white/80">Enhanced Security</span>
          </div>

          <div className="flex items-center gap-2.5">
            <InfoIcon />
            <span className="text-sm font-medium text-white/80">24/7 Support</span>
          </div>
        </div>

        {/* Scroll CTA */}
        <div
          ref={scrollRef}
          className={`absolute bottom-10 left-12 z-10 hidden flex-col items-center transition-all duration-800 delay-1000 md:flex ${scrollInView ? "translate-y-0 opacity-100" : "translate-y-8 opacity-0"}`}
        >
          <span className="mb-2.5 -rotate-90 text-xs uppercase tracking-[2px] text-white/60">Scroll</span>
          <div className="relative h-[60px] w-[1px] overflow-hidden bg-gradient-to-b from-[#cb9d76]/90 to-transparent before:absolute before:top-0 before:left-0 before:h-[15px] before:w-full before:animate-slideInRight before:bg-[#cb9d76]/90"></div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
