import React, { useState } from 'react';
import styled from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

// Placeholder image
const formBackgroundUrl = 'https://images.unsplash.com/photo-1585503418537-88331351ad99?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1674&q=80';

const ContactSection = styled.section`
  padding: 100px 0;
  background-color: var(--light-gray);
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.95) 0%, rgba(255, 255, 255, 0.8) 100%);
    z-index: 1;
  }
`;

const BackgroundImage = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: url(${formBackgroundUrl}) no-repeat center center/cover;
  z-index: 0;
  opacity: 0.3;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
  position: relative;
  z-index: 2;
`;

const FormGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 60px;
  align-items: center;

  @media (max-width: 992px) {
    grid-template-columns: 1fr;
    gap: 40px;
  }
`;

const FormContent = styled.div`
  h2 {
    color: var(--secondary);
    margin-bottom: 20px;
  }

  p {
    font-size: 1.1rem;
    margin-bottom: 30px;
    color: var(--secondary);
    line-height: 1.6;
  }
`;

const FeaturesList = styled.ul`
  list-style: none;
  padding: 0;
  margin-bottom: 30px;

  li {
    margin-bottom: 15px;
    display: flex;
    align-items: center;
    color: var(--secondary);

    svg {
      color: var(--primary);
      margin-right: 15px;
      min-width: 24px;
    }
  }
`;

const FormContainer = styled.div`
  background-color: var(--white);
  border-radius: 10px;
  padding: 40px;
  box-shadow: 0 5px 30px rgba(0, 0, 0, 0.08);

  h3 {
    color: var(--secondary);
    margin-bottom: 30px;
    text-align: center;
  }
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;

  label {
    margin-bottom: 8px;
    font-weight: 500;
    color: var(--secondary);
  }

  input,
  select,
  textarea {
    padding: 12px 15px;
    border: 1px solid rgba(0, 0, 0, 0.1);
