import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

// Hero background image placeholder - You'll need to replace with actual image
const heroBackgroundUrl = 'https://images.unsplash.com/photo-1591370874773-6702e8f12fd8?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1674&q=80';

const HeroContainer = styled.section`
  height: 100vh;
  min-height: 700px;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--white);
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(to right, rgba(0, 0, 0, 0.8), rgba(0, 0, 0, 0.4)), 
                url(${heroBackgroundUrl}) no-repeat center center/cover;
    z-index: -1;
  }

  @media (max-width: 768px) {
    height: auto;
    min-height: 100vh;
    padding: 100px 0;
  }
`;

const Logo = styled.div`
  position: absolute;
  top: 30px;
  left: 50px;
  font-size: 24px;
  font-weight: 700;
  color: var(--white);
  z-index: 10;
  display: flex;
  align-items: center;

  span {
    color: var(--primary);
  }

  @media (max-width: 768px) {
    left: 20px;
    font-size: 20px;
  }
`;

const HeroContent = styled.div`
  max-width: 900px;
  text-align: center;
  padding: 0 20px;
  z-index: 1;
`;

const MainHeading = styled.h1`
  margin-bottom: 20px;

  span {
    color: var(--primary);
  }
`;

const SubHeading = styled.h2`
  font-size: 1.5rem;
  font-weight: 400;
  margin-bottom: 40px;
  font-family: 'Montserrat', sans-serif;
  max-width: 800px;
  margin-left: auto;
  margin-right: auto;

  span {
    margin: 0 8px;
  }
`;

const CTAContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 15px;
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 20px;
  margin-bottom: 40px;

  @media (max-width: 768px) {
    flex-direction: column;
    width: 100%;
    gap: 15px;
  }
`;

const UrgencyText = styled.div`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(5px);
  padding: 15px 25px;
  border-radius: 30px;
  font-size: 14px;
  font-weight: 500;
  margin-top: 30px;
  border: 1px solid rgba(255, 255, 255, 0.2);

  span {
    color: var(--primary);
    font-weight: 700;
  }
`;

const OverlayText = styled.div`
  font-size: 20px;
  margin-bottom: 40px;
  line-height: 1.6;
  max-width: 700px;
  margin-left: auto;
  margin-right: auto;

  @media (max-width: 768px) {
    font-size: 18px;
  }
`;

const ScrollIndicator = styled.div`
  position: absolute;
  bottom: 30px;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  flex-direction: column;
  align-items: center;
  cursor: pointer;

  &::before {
    content: '';
    width: 30px;
    height: 50px;
    border: 2px solid var(--white);
    border-radius: 50px;
    margin-bottom: 8px;
    position: relative;
  }

  &::after {
    content: '';
    width: 8px;
    height: 8px;
    background: var(--white);
    border-radius: 50%;
    position: absolute;
    top: 15px;
    animation: scroll 1.5s infinite;
  }

  @keyframes scroll {
    0% {
      transform: translateY(0);
      opacity: 1;
    }
    100% {
      transform: translateY(20px);
      opacity: 0;
    }
  }
`;

const Hero = () => {
  const [isVisible, setIsVisible] = useState(true);
  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: false,
  });

  useEffect(() => {
    setIsVisible(inView);
  }, [inView]);

  const scrollToSection = (sectionId: string) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <HeroContainer ref={ref}>
      <Logo>
        Home <span>Vision</span> Solutions
      </Logo>

      <HeroContent className={isVisible ? 'fade-in' : ''}>
        <MainHeading>
          Smart Home Automation <span>Installed in Just 14 Days</span>
        </MainHeading>

        <SubHeading>
          <span>✅ Full-Service</span> <span>✅ Luxury Living</span> <span>✅ Starting at $17,000</span>
          <br />
          Designed + Installed by Pros. No DIY. No Guesswork.
        </SubHeading>

        <OverlayText>
          Lights. Shades. Music. Security. Climate. All in sync. All in your control. 
          You just live. We handle the rest.
        </OverlayText>

        <CTAContainer>
          <ButtonContainer>
            <Button 
              primary 
              onClick={() => scrollToSection('contact')}
            >
              Get My Custom Quote Now
            </Button>
            <Button 
              outline 
              onClick={() => scrollToSection('packages')}
            >
              View Packages
            </Button>
          </ButtonContainer>

          <UrgencyText>
            <span>Limited Time:</span> Only 5 spots left for April installation!
          </UrgencyText>
        </CTAContainer>
      </HeroContent>

      <ScrollIndicator onClick={() => scrollToSection('about')} />
    </HeroContainer>
  );
};

export default Hero;
