import React, { useState } from 'react';
import styled, { keyframes, css } from 'styled-components';
import { useInView } from 'react-intersection-observer';

const addOns = [
  {
    id: 'blinds',
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <rect x="2" y="3" width="20" height="14" rx="2" ry="2"></rect>
        <line x1="8" y1="21" x2="16" y2="21"></line>
        <line x1="12" y1="17" x2="12" y2="21"></line>
      </svg>
    ),
    title: 'Motorized Blinds',
    description: 'Automatic control of natural light with scheduling and remote access',
    color: '#CB9D76',
    colorEnd: '#bb8d66',
  },
  {
    id: 'touchpanels',
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <rect x="4" y="4" width="16" height="16" rx="2" ry="2"></rect>
        <rect x="9" y="9" width="6" height="6"></rect>
        <line x1="9" y1="1" x2="9" y2="4"></line>
        <line x1="15" y1="1" x2="15" y2="4"></line>
        <line x1="9" y1="20" x2="9" y2="23"></line>
        <line x1="15" y1="20" x2="15" y2="23"></line>
        <line x1="20" y1="9" x2="23" y2="9"></line>
        <line x1="20" y1="14" x2="23" y2="14"></line>
        <line x1="1" y1="9" x2="4" y2="9"></line>
        <line x1="1" y1="14" x2="4" y2="14"></line>
      </svg>
    ),
    title: 'Touch Panel Upgrades',
    description: 'Premium control interfaces with sleek, intuitive designs',
    color: '#b78a63',
    colorEnd: '#a77a53',
  },
  {
    id: 'outdoor',
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <polygon points="12 2 15.09 8.26 22 9.27 17 14.14 18.18 21.02 12 17.77 5.82 21.02 7 14.14 2 9.27 8.91 8.26 12 2"></polygon>
      </svg>
    ),
    title: 'Outdoor Audio Systems',
    description: 'Extend your entertainment experiences to patios and gardens',
    color: '#8d6c4e',
    colorEnd: '#7d5c3e',
  },
  {
    id: 'theatre',
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <rect x="2" y="7" width="20" height="15" rx="2" ry="2"></rect>
        <polyline points="17 2 12 7 7 2"></polyline>
      </svg>
    ),
    title: 'Home Theatre Integration',
    description: 'Immersive cinematic experiences with professional calibration',
    color: '#f8d7bc',
    colorEnd: '#eac7ab',
  },
  {
    id: 'voice',
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <circle cx="12" cy="12" r="10"></circle>
        <path d="M8.56 2.75c4.37 6.03 6.02 9.42 8.03 17.72m2.54-15.38c-3.72 4.35-8.94 5.66-16.88 5.85m19.5 1.9c-3.5-.93-6.63-.82-8.94 0-2.58.92-5.01 2.86-7.44 6.32"></path>
      </svg>
    ),
    title: 'Voice Assistant Support',
    description: 'Seamless integration with Alexa, Google Assistant, and more',
    color: '#CB9D76',
    colorEnd: '#bb8d66',
  },
];

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const pulse = keyframes`
  0% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.05);
  }
  100% {
    transform: scale(1);
  }
`;

const float = keyframes`
  0% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-15px);
  }
  100% {
    transform: translateY(0px);
  }
`;

const rotate = keyframes`
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
`;

const AddOnsSection = styled.section`
  padding: 120px 0 140px;
  background: linear-gradient(135deg, #f8f8f8 0%, #ffffff 100%);
  position: relative;
  overflow: hidden;
`;

const BackgroundElement = styled.div`
  position: absolute;
  z-index: 0;

  &.circle1 {
    width: 400px;
    height: 400px;
    border-radius: 50%;
    background: radial-gradient(circle, rgba(203, 157, 118, 0.05) 0%, rgba(255, 255, 255, 0) 70%);
    top: -100px;
    left: -100px;
  }

  &.circle2 {
    width: 500px;
    height: 500px;
    border-radius: 50%;
    background: radial-gradient(circle, rgba(203, 157, 118, 0.03) 0%, rgba(255, 255, 255, 0) 70%);
    bottom: -200px;
    right: -200px;
  }
`;

const Container = styled.div`
  max-width: 1400px;
  margin: 0 auto;
  padding: 0 30px;
  position: relative;
  z-index: 2;
`;

const SectionHeader = styled.div<{ inView: boolean }>`
  text-align: center;
  margin-bottom: 80px;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '30px'});
  transition: all 0.7s cubic-bezier(0.175, 0.885, 0.32, 1.275);
`;

const SectionTitle = styled.h2`
  color: var(--secondary);
  margin-bottom: 15px;
  font-size: 2.6rem;
  position: relative;
  display: inline-block;
  font-weight: 700;

  span {
    color: var(--primary);
    position: relative;

    &::after {
      content: '';
      position: absolute;
      bottom: 2px;
      left: 0;
      width: 100%;
      height: 10px;
      background-color: rgba(203, 157, 118, 0.15);
      z-index: -1;
    }
  }
`;

const SectionSubtitle = styled.p`
  font-size: 1.2rem;
  max-width: 750px;
  margin: 25px auto 0;
  color: var(--text);
  line-height: 1.7;
`;

const AddOnsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 30px;

  @media (max-width: 1200px) {
    grid-template-columns: repeat(2, 1fr);
  }

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    max-width: 450px;
    margin: 0 auto;
  }
`;

const AddOnCard = styled.div<{ color: string; colorEnd: string; delay: number; active: boolean }>`
  background-color: white;
  border-radius: 16px;
  padding: 40px 30px;
  text-align: center;
  box-shadow: 0 15px 40px rgba(0, 0, 0, 0.05);
  transition: all 0.5s cubic-bezier(0.165, 0.84, 0.44, 1);
  position: relative;
  opacity: 0;
  transform: translateY(30px);
  animation: ${fadeIn} 0.8s cubic-bezier(0.175, 0.885, 0.32, 1.275) forwards;
  animation-delay: ${props => 0.15 + props.delay * 0.15}s;

  ${props => props.active && css`
    transform: translateY(-15px);
    box-shadow: 0 25px 50px rgba(0, 0, 0, 0.1);
  `}

  &:hover {
    transform: translateY(-15px);
    box-shadow: 0 25px 50px rgba(0, 0, 0, 0.1);

    &::before {
      opacity: 1;
    }

    .icon-wrapper {
      background: linear-gradient(135deg, ${props => props.color} 0%, ${props => props.colorEnd} 100%);
      transform: scale(1.05);

      svg {
        color: white;
        transform: scale(1.1);
      }
    }
  }

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 5px;
    background: linear-gradient(to right, ${props => props.color}, ${props => props.colorEnd});
    opacity: 0.5;
    transition: opacity 0.3s ease;
    border-radius: 16px 16px 0 0;
  }
`;

const IconWrapper = styled.div<{ color: string }>`
  width: 80px;
  height: 80px;
  border-radius: 50%;
  background: ${props => props.color}15;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto 25px;
  transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
  position: relative;

  svg {
    width: 40px;
    height: 40px;
    color: ${props => props.color};
    transition: all 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
  }

  &::after {
    content: '';
    position: absolute;
    top: -8px;
    left: -8px;
    right: -8px;
    bottom: -8px;
    border: 2px dashed ${props => props.color}30;
    border-radius: 50%;
    opacity: 0.5;
    transition: all 0.4s ease;
  }

  ${AddOnCard}:hover &::after {
    opacity: 0;
    transform: scale(1.2);
  }
`;

const AddOnTitle = styled.h3`
  color: var(--secondary);
  font-size: 1.5rem;
  margin-bottom: 15px;
  font-weight: 700;
`;

const AddOnDescription = styled.p`
  font-size: 1rem;
  color: var(--text);
  line-height: 1.6;
  margin-bottom: 0;
`;

const FloatingIcon = styled.div`
  position: absolute;
  width: 150px;
  height: 150px;
  opacity: 0.03;
  z-index: 0;
  animation: ${float} 6s ease-in-out infinite;

  &.icon1 {
    top: 15%;
    right: 10%;
    animation-delay: 0s;
  }

  &.icon2 {
    bottom: 10%;
    left: 5%;
    animation-delay: 2s;
  }

  svg {
    width: 100%;
    height: 100%;
    color: var(--primary);
  }
`;

const CTAContainer = styled.div<{ inView: boolean }>`
  margin-top: 70px;
  text-align: center;
  background: linear-gradient(135deg, #ffffff 0%, #f9f9f9 100%);
  border-radius: 20px;
  padding: 40px;
  box-shadow: 0 15px 40px rgba(0, 0, 0, 0.05);
  position: relative;
  overflow: hidden;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '30px'});
  transition: all 0.7s ease 0.5s;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 5px;
    background: linear-gradient(to right, var(--primary), #cb9d76);
  }
`;

const CTATitle = styled.h3`
  font-size: 1.6rem;
  color: var(--secondary);
  margin-bottom: 15px;
  font-weight: 700;
`;

const CTAText = styled.p`
  font-size: 1.1rem;
  max-width: 700px;
  margin: 0 auto;
  color: var(--text);
  line-height: 1.7;

  span {
    position: relative;
    color: var(--primary);
    font-weight: 600;

    &::after {
      content: '';
      position: absolute;
      bottom: 2px;
      left: 0;
      width: 100%;
      height: 6px;
      background-color: rgba(203, 157, 118, 0.15);
      z-index: -1;
    }
  }
`;

const AddOns = () => {
  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  const [activeAddOn, setActiveAddOn] = useState<string | null>(null);

  const handleMouseEnter = (addonId: string) => {
    setActiveAddOn(addonId);
  };

  const handleMouseLeave = () => {
    setActiveAddOn(null);
  };

  return (
    <AddOnsSection ref={ref} id="addons">
      <BackgroundElement className="circle1" />
      <BackgroundElement className="circle2" />

      <FloatingIcon className="icon1">
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1" strokeLinecap="round" strokeLinejoin="round">
          <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
          <polyline points="22 4 12 14.01 9 11.01"></polyline>
        </svg>
      </FloatingIcon>

      <FloatingIcon className="icon2">
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="1" strokeLinecap="round" strokeLinejoin="round">
          <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z"></path>
        </svg>
      </FloatingIcon>

      <Container>
        <SectionHeader inView={inView}>
          <SectionTitle>Enhance Your <span>Smart Home</span> Experience</SectionTitle>
          <SectionSubtitle>
            Customize your system with these premium add-ons designed to elevate
            your lifestyle and create the perfect smart home experience.
          </SectionSubtitle>
        </SectionHeader>

        <AddOnsGrid>
          {addOns.map((addon, index) => (
            <AddOnCard 
              key={addon.id} 
              color={addon.color}
              colorEnd={addon.colorEnd}
              delay={index}
              active={activeAddOn === addon.id}
              onMouseEnter={() => handleMouseEnter(addon.id)}
              onMouseLeave={handleMouseLeave}
            >
              <IconWrapper className="icon-wrapper" color={addon.color}>
                {addon.icon}
              </IconWrapper>
              <AddOnTitle>{addon.title}</AddOnTitle>
              <AddOnDescription>{addon.description}</AddOnDescription>
            </AddOnCard>
          ))}
        </AddOnsGrid>

        <CTAContainer inView={inView}>
          <CTATitle>Interested in Add-Ons?</CTATitle>
          <CTAText>
            <span>Just mention it in your consultation</span> and our specialists will
            include it in your personalized quote, ensuring a seamless integration
            with your smart home system.
          </CTAText>
        </CTAContainer>
      </Container>
    </AddOnsSection>
  );
};

export default AddOns;
