import React, { useState } from 'react';
import styled, { keyframes, css } from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

// Sample lighting scenes
const lightingScenes = [
  {
    id: 'scene1',
    title: 'Ambient Mood',
    description: 'Create the perfect atmosphere with subtle gradient lighting that transforms your space.',
    color: '#e94ca1',
    colorEnd: '#c746b8',
    backgroundGradient: 'linear-gradient(135deg, #e94ca1, #c746b8)'
  },
  {
    id: 'scene2',
    title: 'Entertainment Mode',
    description: "Enhance your media experience with synchronized lighting that responds to what's on screen.",
    color: '#f5b454',
    colorEnd: '#f8973f',
    backgroundGradient: 'linear-gradient(135deg, #f5b454, #f8973f)'
  },
  {
    id: 'scene3',
    title: 'Focus & Productivity',
    description: 'Optimize your environment for work with lighting scientifically designed to improve concentration.',
    color: '#42c6ff',
    colorEnd: '#5a8afb',
    backgroundGradient: 'linear-gradient(135deg, #42c6ff, #5a8afb)'
  },
];

// Features of the lighting system
const features = [
  {
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <path d="M12 2v8M12 18v4M4.93 10.93l1.41 1.41M17.66 10.93l-1.41 1.41M2 18h2M20 18h2M19.437 3.406l-1.612 1.035M5.563 3.406l1.612 1.035M19.437 20.594l-1.612-1.035M5.563 20.594l1.612-1.035"></path>
        <circle cx="12" cy="14" r="4"></circle>
      </svg>
    ),
    title: 'Smart Scenes',
    description: 'Pre-program custom lighting scenes that activate with a single touch or voice command.'
  },
  {
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <path d="M2 12h10M12 2v10M12 12l8 8M22 2L12 12"></path>
      </svg>
    ),
    title: 'Intuitive Controls',
    description: 'Control your lighting through elegant wall panels, mobile app, or voice assistants.'
  },
  {
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <circle cx="12" cy="12" r="10"></circle>
        <path d="M12 2a14.5 14.5 0 0 0 0 20 14.5 14.5 0 0 0 0-20"></path>
        <path d="M2 12h20"></path>
      </svg>
    ),
    title: 'Whole Home Integration',
    description: 'Seamlessly integrate with other smart home systems for comprehensive automation.'
  },
  {
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <path d="M18 8h1a4 4 0 1 1 0 8h-1"></path>
        <path d="M2 8h16v9a4 4 0 0 1-4 4H6a4 4 0 0 1-4-4V8z"></path>
        <line x1="6" y1="1" x2="6" y2="4"></line>
        <line x1="10" y1="1" x2="10" y2="4"></line>
        <line x1="14" y1="1" x2="14" y2="4"></line>
      </svg>
    ),
    title: 'Energy Efficient',
    description: 'Reduce electricity consumption with smart scheduling and motion-based activation.'
  },
];

// Animations
const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const glow = keyframes`
  0% {
    box-shadow: 0 0 15px rgba(255, 238, 150, 0.5);
  }
  50% {
    box-shadow: 0 0 25px rgba(255, 238, 150, 0.8);
  }
  100% {
    box-shadow: 0 0 15px rgba(255, 238, 150, 0.5);
  }
`;

const shimmer = keyframes`
  0% {
    background-position: -200px 0;
  }
  100% {
    background-position: 200px 0;
  }
`;

const float = keyframes`
  0% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-15px);
  }
  100% {
    transform: translateY(0px);
  }
`;

const gradientShift = keyframes`
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
`;

const rotate = keyframes`
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
`;

// Styled Components
const LightingSection = styled.section`
  padding: 140px 0;
  background: linear-gradient(135deg, #232526 0%, #414345 100%);
  color: #fff;
  position: relative;
  overflow: hidden;
`;

const BackgroundEffect = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: radial-gradient(circle at 80% 20%, rgba(233, 76, 161, 0.15) 0%, transparent 45%),
              radial-gradient(circle at 20% 80%, rgba(90, 138, 251, 0.15) 0%, transparent 45%);
  background-image: radial-gradient(rgba(255, 255, 255, 0.1) 1px, transparent 1px);
  background-size: 30px 30px;
  opacity: 0.3;
  z-index: 0;
`;

const Container = styled.div`
  max-width: 1400px;
  margin: 0 auto;
  padding: 0 30px;
  position: relative;
  z-index: 2;
`;

const SectionHeader = styled.div<{ inView: boolean }>`
  text-align: center;
  margin-bottom: 80px;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '30px'});
  transition: all 0.7s cubic-bezier(0.175, 0.885, 0.32, 1.275);
`;

const SectionTitle = styled.h2`
  color: white;
  margin-bottom: 15px;
  font-size: 3.4rem;
  position: relative;
  display: inline-block;
  font-weight: 700;
  letter-spacing: -0.5px;

  span {
    background: linear-gradient(90deg, #e94ca1, #f5b454, #42c6ff);
    background-size: 200% 200%;
    animation: ${gradientShift} 7s ease infinite;
    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
    position: relative;
  }
`;

const SectionSubtitle = styled.p`
  font-size: 1.2rem;
  max-width: 750px;
  margin: 25px auto 0;
  color: rgba(255, 255, 255, 0.8);
  line-height: 1.7;
`;

// First section - Showcase and scenes
const ShowcaseGrid = styled.div`
  display: grid;
  grid-template-columns: 2fr 1fr;
  gap: 40px;
  margin-bottom: 120px;

  @media (max-width: 992px) {
    grid-template-columns: 1fr;
    gap: 60px;
  }
`;

const MainImageDisplay = styled.div<{ inView: boolean; gradient: string }>`
  position: relative;
  border-radius: 24px;
  overflow: hidden;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '40px'});
  transition: all 1s cubic-bezier(0.175, 0.885, 0.32, 1.275) 0.2s;
  box-shadow: 0 30px 70px rgba(0, 0, 0, 0.4);
  background: ${props => props.gradient};
  height: 400px;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
`;

const SceneIcon = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -80%);
  font-size: 6rem;
  opacity: 0.8;
`;

const OverlayText = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  padding: 50px 40px 40px;
  background: linear-gradient(to top, rgba(0, 0, 0, 0.8) 0%, transparent 100%);

  h2 {
    font-size: 2.5rem;
    font-weight: 700;
    margin-bottom: 10px;
    letter-spacing: -0.5px;
  }

  p {
    font-size: 1.1rem;
    opacity: 0.9;
    max-width: 80%;
    line-height: 1.6;
  }
`;

const ControlBadge = styled.div`
  position: absolute;
  top: 30px;
  right: 30px;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border-radius: 50px;
  padding: 12px 24px;
  display: flex;
  align-items: center;
  border: 1px solid rgba(255, 255, 255, 0.2);

  span {
    margin-left: 8px;
    font-size: 0.9rem;
    font-weight: 500;
    letter-spacing: 0.5px;
  }

  svg {
    width: 18px;
    height: 18px;
    color: rgba(255, 238, 150, 0.9);
  }
`;

const SceneSelector = styled.div<{ inView: boolean }>`
  display: flex;
  flex-direction: column;
  gap: 20px;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateX(${props => props.inView ? 0 : '30px'});
  transition: all 1s cubic-bezier(0.175, 0.885, 0.32, 1.275) 0.4s;
`;

const SceneCard = styled.div<{ active: boolean; color: string; colorEnd: string }>`
  background: ${props => props.active ? 
    `linear-gradient(135deg, ${props.color}, ${props.colorEnd})` : 
    'rgba(255, 255, 255, 0.05)'};
  border-radius: 16px;
  padding: 25px;
  cursor: pointer;
  transition: all 0.3s ease;
  border: 1px solid ${props => props.active ? 
    'rgba(255, 255, 255, 0.3)' : 
    'rgba(255, 255, 255, 0.05)'};
  box-shadow: ${props => props.active ? 
    `0 15px 30px rgba(0, 0, 0, 0.2), 0 0 15px ${props.color}40` : 
    '0 8px 20px rgba(0, 0, 0, 0.1)'};

  &:hover {
    transform: translateY(-5px);
    box-shadow: ${props => props.active ? 
      `0 20px 40px rgba(0, 0, 0, 0.25), 0 0 20px ${props.color}50` : 
      '0 12px 25px rgba(0, 0, 0, 0.15)'};
  }
`;

const SceneTitle = styled.h3`
  font-size: 1.4rem;
  font-weight: 600;
  margin-bottom: 8px;
`;

const SceneDescription = styled.p`
  font-size: 1rem;
  opacity: 0.8;
  line-height: 1.5;
`;

// Second section - Interactive light control
const ContentWrapper = styled.div<{ inView: boolean }>`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 80px;
  align-items: center;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '40px'});
  transition: all 1s cubic-bezier(0.165, 0.84, 0.44, 1);

  @media (max-width: 992px) {
    grid-template-columns: 1fr;
    gap: 60px;
  }
`;

const ContentContainer = styled.div`
  @media (max-width: 992px) {
    order: 1;
  }
`;

const LightingDemo = styled.div`
  position: relative;

