const SmartLighting = () => {
  const [headerRef, headerInView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const [showcaseRef, showcaseInView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const [scenesRef, scenesInView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const [interactiveRef, interactiveInView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const [featuresRef, featuresInView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  // State for scene selector
  const [activeScene, setActiveScene] = useState(lightingScenes[0].id);

  // State for interactive lighting controls
  const [brightness, setBrightness] = useState(70);
  const [color, setColor] = useState('#ffee96');

  const colors = [
    '#ffee96', // Warm white
    '#ff5f5f', // Red
    '#5ea7ff', // Blue
    '#7dff7d', // Green
    '#ff7dff', // Purple
    '#ffaa5e', // Orange
  ];

  const getActiveScene = () => {
    return lightingScenes.find(scene => scene.id === activeScene) || lightingScenes[0];
  };

  const scrollToSection = (sectionId: string) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <LightingSection id="smart-lighting">
      <BackgroundEffect />
      <Container>
        <SectionHeader ref={headerRef} inView={headerInView}>
          <SectionTitle>Smart <span>Lighting</span> Control</SectionTitle>
          <SectionSubtitle>
            Transform any space with intelligent lighting that adapts to your needs, mood, and activities.
            Experience the perfect ambience with just a tap.
          </SectionSubtitle>
        </SectionHeader>

        {/* First section - Scene showcase */}
        <ShowcaseGrid>
          <MainImageDisplay 
            ref={showcaseRef} 
            inView={showcaseInView} 
            gradient={getActiveScene().backgroundGradient}
          >
            <OverlayText>
              <h2>{getActiveScene().title}</h2>
              <p>{getActiveScene().description}</p>
            </OverlayText>
            <ControlBadge>
              <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z"></path>
              </svg>
              <span>Scene Control</span>
            </ControlBadge>
          </MainImageDisplay>

          <SceneSelector ref={scenesRef} inView={scenesInView}>
            {lightingScenes.map(scene => (
              <SceneCard 
                key={scene.id} 
                active={activeScene === scene.id}
                color={scene.color}
                colorEnd={scene.colorEnd}
                onClick={() => setActiveScene(scene.id)}
              >
                <SceneTitle>{scene.title}</SceneTitle>
                <SceneDescription>{scene.description}</SceneDescription>
              </SceneCard>
            ))}
          </SceneSelector>
        </ShowcaseGrid>

        {/* Second section - Interactive control demo */}
        <ContentWrapper ref={interactiveRef} inView={interactiveInView}>
          <ContentContainer>
            <Subtitle>Smart Control</Subtitle>
            <Title>Complete <span>Control</span> at Your Fingertips</Title>
            <Description>
              Adjust brightness, color, and mood with intuitive controls through our app,
              voice commands, or elegant wall panels. Create custom scenes for any occasion.
            </Description>

            <FeatureList ref={featuresRef}>
              {features.map((feature, index) => (
                <FeatureItem 
                  key={index} 
                  inView={featuresInView} 
                  delay={index + 1}
                >
                  <FeatureIcon>{feature.icon}</FeatureIcon>
                  <FeatureContent>
                    <h4>{feature.title}</h4>
                    <p>{feature.description}</p>
                  </FeatureContent>
                </FeatureItem>
              ))}
            </FeatureList>

            <ActionButton>Explore Lighting Solutions</ActionButton>
          </ContentContainer>

          <LightingDemo>
            <RoomImage>
              <img src="/lightingControl.jpg" alt="Smart Lighting Demo" />
              <LightOverlay color={color} brightness={brightness} />
            </RoomImage>

            <LightingControls>
              <ControlsTitle>
                <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                  <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z"></path>
                </svg>
                Live Lighting Control
              </ControlsTitle>

              <ControlRow>
                <ControlLabel>
                  <span>Brightness</span>
                  <strong>{brightness}%</strong>
                </ControlLabel>
                <RangeSlider 
                  min="10" 
                  max="100" 
                  value={brightness} 
                  onChange={(e) => setBrightness(parseInt(e.target.value))} 
                />
              </ControlRow>

              <ControlRow>
                <ControlLabel>
                  <span>Color</span>
                </ControlLabel>
                <ColorPicker>
                  {colors.map((clr, index) => (
                    <ColorButton 
                      key={index}
                      color={clr}
                      active={color === clr}
                      onClick={() => setColor(clr)}
                    />
                  ))}
                </ColorPicker>
              </ControlRow>
            </LightingControls>
          </LightingDemo>
        </ContentWrapper>
      </Container>
    </LightingSection>
  );
};

export default SmartLighting;
