import React, { useState } from 'react';
import styled, { keyframes, css } from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

// Animations
const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const glow = keyframes`
  0% {
    box-shadow: 0 0 15px rgba(255, 238, 150, 0.5);
  }
  50% {
    box-shadow: 0 0 25px rgba(255, 238, 150, 0.8);
  }
  100% {
    box-shadow: 0 0 15px rgba(255, 238, 150, 0.5);
  }
`;

const rotate = keyframes`
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
`;

// Styled Components
const Section = styled.section`
  padding: 140px 0;
  background: linear-gradient(135deg, #232526 0%, #414345 100%);
  color: #fff;
  position: relative;
  overflow: hidden;
`;

const BackgroundPattern = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: radial-gradient(rgba(255, 255, 255, 0.1) 1px, transparent 1px);
  background-size: 30px 30px;
  opacity: 0.3;
  z-index: 0;
`;

const Container = styled.div`
  max-width: 1400px;
  margin: 0 auto;
  padding: 0 30px;
  position: relative;
  z-index: 1;
`;

const ContentWrapper = styled.div<{ inView: boolean }>`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 80px;
  align-items: center;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '40px'});
  transition: all 1s cubic-bezier(0.165, 0.84, 0.44, 1);

  @media (max-width: 992px) {
    grid-template-columns: 1fr;
    gap: 60px;
  }
`;

const ContentContainer = styled.div`
  @media (max-width: 992px) {
    order: 1;
  }
`;

const LightingDemo = styled.div`
  position: relative;

  @media (max-width: 992px) {
    order: 2;
  }
`;

const RoomImage = styled.div`
  position: relative;
  border-radius: 20px;
  overflow: hidden;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);

  img {
    width: 100%;
    height: auto;
    display: block;
    transition: all 0.5s ease;
  }
`;

const LightOverlay = styled.div<{ color: string; brightness: number }>`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: ${props => props.color};
  opacity: ${props => props.brightness / 100 * 0.5};
  mix-blend-mode: soft-light;
  transition: all 0.5s ease;
`;

const LightingControls = styled.div`
  position: relative;
  margin-top: -50px;
  z-index: 10;
  background: rgba(30, 30, 32, 0.8);
  backdrop-filter: blur(10px);
  padding: 30px;
  border-radius: 20px;
  border: 1px solid rgba(255, 255, 255, 0.1);
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
  margin-left: 40px;
  margin-right: 40px;
`;

const ControlsTitle = styled.h4`
  font-size: 1.1rem;
  margin-bottom: 20px;
  display: flex;
  align-items: center;
  color: rgba(255, 255, 255, 0.9);

  svg {
    margin-right: 10px;
    color: rgba(255, 238, 150, 0.9);
  }
`;

const ControlRow = styled.div`
  margin-bottom: 20px;

  &:last-child {
    margin-bottom: 0;
  }
`;

const ControlLabel = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 10px;

  span {
    font-size: 0.85rem;
    color: rgba(255, 255, 255, 0.7);
  }

  strong {
    font-size: 0.85rem;
    color: rgba(255, 255, 255, 0.9);
  }
`;

const RangeSlider = styled.input.attrs({ type: 'range' })`
  width: 100%;
  height: 6px;
  -webkit-appearance: none;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 3px;
  outline: none;

  &::-webkit-slider-thumb {
    -webkit-appearance: none;
    width: 18px;
    height: 18px;
    border-radius: 50%;
    background: rgba(255, 238, 150, 0.9);
    cursor: pointer;
    transition: all 0.2s ease;
    box-shadow: 0 0 10px rgba(255, 238, 150, 0.5);
  }

  &::-webkit-slider-thumb:hover {
    transform: scale(1.1);
    animation: ${glow} 2s infinite;
  }

  &::-moz-range-thumb {
    width: 18px;
    height: 18px;
    border: none;
    border-radius: 50%;
    background: rgba(255, 238, 150, 0.9);
    cursor: pointer;
    transition: all 0.2s ease;
    box-shadow: 0 0 10px rgba(255, 238, 150, 0.5);
  }

  &::-moz-range-thumb:hover {
    transform: scale(1.1);
  }
`;

const ColorPicker = styled.div`
  display: flex;
  gap: 10px;
  justify-content: space-between;
  margin-top: 15px;
`;

interface ColorButtonProps {
  color: string;
  active: boolean;
}

const ColorButton = styled.button<ColorButtonProps>`
  width: 36px;
  height: 36px;
  border-radius: 50%;
  background-color: ${props => props.color};
  border: 3px solid ${props => props.active ? 'rgba(255, 255, 255, 0.9)' : 'rgba(255, 255, 255, 0.2)'};
  cursor: pointer;
  transition: all 0.2s ease;
  box-shadow: ${props => props.active ? `0 0 15px ${props.color}` : 'none'};

  &:hover {
    transform: scale(1.1);
  }
`;

const Subtitle = styled.div`
  font-size: 1.1rem;
  text-transform: uppercase;
  letter-spacing: 3px;
  margin-bottom: 15px;
  color: rgba(255, 238, 150, 0.9);
  font-weight: 500;
`;

const Title = styled.h2`
  font-size: 3.5rem;
  line-height: 1.1;
  margin-bottom: 25px;
  font-weight: 700;

  @media (max-width: 768px) {
    font-size: 2.8rem;
  }
`;

const Description = styled.p`
  font-size: 1.1rem;
  line-height: 1.7;
  color: rgba(255, 255, 255, 0.8);
  margin-bottom: 40px;
`;

const FeaturesList = styled.div`
  margin-bottom: 40px;
`;

const Feature = styled.div<{ delay: number }>`
  margin-bottom: 20px;
  display: flex;
  opacity: 0;
  transform: translateY(20px);
  animation: ${fadeIn} 0.6s forwards;
  animation-delay: ${props => 0.2 + props.delay * 0.15}s;
`;

const FeatureIcon = styled.div`
  margin-right: 16px;
  width: 50px;
  height: 50px;
  background: rgba(255, 238, 150, 0.1);
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;

  svg {
    width: 24px;
    height: 24px;
    color: rgba(255, 238, 150, 0.9);
  }
`;

const FeatureContent = styled.div``;

const FeatureTitle = styled.h4`
  font-size: 1.1rem;
  margin-bottom: 8px;
  font-weight: 600;
`;

const FeatureDescription = styled.p`
  font-size: 0.95rem;
  color: rgba(255, 255, 255, 0.7);
  line-height: 1.5;
`;

const GlowCircle = styled.div`
  position: absolute;
  width: 250px;
  height: 250px;
  border-radius: 50%;
  background: radial-gradient(circle, rgba(255, 238, 150, 0.2) 0%, rgba(255, 238, 150, 0) 70%);
  top: -50px;
  right: -80px;
  z-index: -1;
`;

const CTAButton = styled(Button)`
  background: linear-gradient(to right, rgba(255, 238, 150, 0.9), rgba(255, 209, 82, 0.9));
  border: none;
  color: #333;
  font-size: 1rem;
  font-weight: 600;
  padding: 15px 35px;
  border-radius: 50px;
  box-shadow: 0 10px 30px rgba(255, 238, 150, 0.3);
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 15px 40px rgba(255, 238, 150, 0.4);
  }
`;

const IconCircle = styled.div`
  position: absolute;
  top: -100px;
  right: -100px;
  width: 200px;
  height: 200px;
  border-radius: 50%;
  opacity: 0.05;
  z-index: 0;

  svg {
    width: 100%;
    height: 100%;
    animation: ${rotate} 20s linear infinite;
  }
`;

const SmartLighting = () => {
  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  const [brightness, setBrightness] = useState(70);
  const [selectedColor, setSelectedColor] = useState('#FFE278');

  const colorOptions = [
    { id: 'warm', color: '#FFE278' },
    { id: 'cool', color: '#A5D6FF' },
    { id: 'purple', color: '#D4A5FF' },
    { id: 'red', color: '#FF8F8F' },
    { id: 'green', color: '#A5FFBC' },
  ];

  const features = [
    {
      icon: (
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
          <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z"></path>
        </svg>
      ),
      title: 'Smart Mood Lighting',
      description: 'Create the perfect ambiance for any occasion with customizable lighting scenes that adapt to your needs.'
    },
    {
      icon: (
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
          <rect x="3" y="11" width="18" height="11" rx="2" ry="2"></rect>
          <path d="M7 11V7a5 5 0 0 1 10 0v4"></path>
        </svg>
      ),
      title: 'Mobile Control',
      description: 'Control all your lights from anywhere using our intuitive mobile app with preset scenes and schedules.'
    },
    {
      icon: (
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
          <path d="M12 2a10 10 0 1 0 0 20 10 10 0 0 0 0-20z"></path>
          <path d="M12 8v8"></path>
          <path d="M8 12h8"></path>
        </svg>
      ),
      title: 'Energy Efficient',
      description: 'Reduce your energy consumption with automated lighting that adjusts based on occupancy and natural light.'
    }
  ];

  const scrollToSection = (sectionId: string) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <Section id="smart-lighting" ref={ref}>
      <BackgroundPattern />
      <IconCircle>
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="0.5" strokeLinecap="round" strokeLinejoin="round">
          <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4"></path>
          <polyline points="16 17 21 12 16 7"></polyline>
          <line x1="21" y1="12" x2="9" y2="12"></line>
        </svg>
      </IconCircle>

      <Container>
        <ContentWrapper inView={inView}>
          <ContentContainer>
            <Subtitle>Smart Lighting Solutions</Subtitle>
            <Title>Transform Your Space</Title>
            <Description>
              Experience the ultimate in lighting control with our smart lighting solutions.
              Create the perfect ambiance for any moment, save energy, and enhance security
              with intelligent, responsive lights that adapt to your lifestyle.
            </Description>

            <FeaturesList>
              {features.map((feature, index) => (
                <Feature key={index} delay={index}>
                  <FeatureIcon>{feature.icon}</FeatureIcon>
                  <FeatureContent>
                    <FeatureTitle>{feature.title}</FeatureTitle>
                    <FeatureDescription>{feature.description}</FeatureDescription>
                  </FeatureContent>
                </Feature>
              ))}
            </FeaturesList>

            <CTAButton onClick={() => scrollToSection('contact')}>
              Explore Lighting Options
            </CTAButton>
            <GlowCircle />
          </ContentContainer>

          <LightingDemo>
            <RoomImage>
              <img src="/lightingControl.jpg" alt="Smart Lighting Demo" />
              <LightOverlay color={selectedColor} brightness={brightness} />
            </RoomImage>

            <LightingControls>
              <ControlsTitle>
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                  <circle cx="12" cy="12" r="5"></circle>
                  <line x1="12" y1="1" x2="12" y2="3"></line>
                  <line x1="12" y1="21" x2="12" y2="23"></line>
                  <line x1="4.22" y1="4.22" x2="5.64" y2="5.64"></line>
                  <line x1="18.36" y1="18.36" x2="19.78" y2="19.78"></line>
                  <line x1="1" y1="12" x2="3" y2="12"></line>
                  <line x1="21" y1="12" x2="23" y2="12"></line>
                  <line x1="4.22" y1="19.78" x2="5.64" y2="18.36"></line>
                  <line x1="18.36" y1="5.64" x2="19.78" y2="4.22"></line>
                </svg>
                Lighting Controls
              </ControlsTitle>

              <ControlRow>
                <ControlLabel>
                  <span>Brightness</span>
                  <strong>{brightness}%</strong>
                </ControlLabel>
                <RangeSlider
                  min="0"
                  max="100"
                  value={brightness}
                  onChange={(e) => setBrightness(parseInt(e.target.value))}
                />
              </ControlRow>

              <ControlRow>
                <ControlLabel>
                  <span>Light Color</span>
                </ControlLabel>
                <ColorPicker>
                  {colorOptions.map((option) => (
                    <ColorButton
                      key={option.id}
                      color={option.color}
                      active={selectedColor === option.color}
                      onClick={() => setSelectedColor(option.color)}
                    />
                  ))}
                </ColorPicker>
              </ControlRow>
            </LightingControls>
          </LightingDemo>
        </ContentWrapper>
      </Container>
    </Section>
  );
};

export default SmartLighting;
