import React from 'react';
import styled, { keyframes } from 'styled-components';
import { useInView } from 'react-intersection-observer';

// Use brand logo images from the public folder - same pattern as video sources in Hero component
const logoUrls = [
  '/control4.png',
  '/leon.png',
  '/sonos.png',
  '/lutron.png',
  '/araknis.png',
  '/sony.png',
  '/smart-clare.png',
  '/seura.png',
  '/luma.png',
];

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const LogosSection = styled.section`
  padding: 100px 0;
  background-color: var(--white);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    width: 600px;
    height: 600px;
    background: radial-gradient(circle, rgba(203, 157, 118, 0.05) 0%, rgba(255, 255, 255, 0) 70%);
    top: -300px;
    right: -300px;
    z-index: 0;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
  position: relative;
  z-index: 1;
`;

const SectionTitle = styled.h2<{ inView: boolean }>`
  font-size: 2.2rem;
  color: var(--secondary);
  margin-bottom: 50px;
  position: relative;
  display: inline-block;
  font-weight: 600;
  text-align: center;
  width: 100%;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '20px'});
  transition: all 0.5s ease-out;

  span {
    color: var(--primary);
    position: relative;

    &::after {
      content: '';
      position: absolute;
      bottom: 0;
      left: 0;
      width: 100%;
      height: 8px;
      background-color: rgba(203, 157, 118, 0.15);
      z-index: -1;
    }
  }
`;

const LogosGrid = styled.div<{ inView: boolean }>`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(180px, 1fr));
  gap: 30px;
  align-items: center;
  justify-items: center;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '30px'});
  transition: all 0.7s ease-out;
  transition-delay: 0.2s;

  @media (max-width: 992px) {
    grid-template-columns: repeat(3, 1fr);
    gap: 30px;
  }

  @media (max-width: 576px) {
    grid-template-columns: repeat(2, 1fr);
    gap: 20px;
  }
`;

const Logo = styled.div<{ delay: number }>`
  background-color: white;
  padding: 30px;
  border-radius: 8px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.03);
  transition: all 0.4s ease;
  filter: grayscale(100%);
  opacity: 0.7;
  border: 1px solid rgba(0, 0, 0, 0.03);
  width: 100%;
  height: 100px;
  display: flex;
  justify-content: center;
  align-items: center;
  animation: ${fadeIn} 0.6s ease-out forwards;
  animation-delay: ${props => props.delay * 0.1}s;

  &:hover {
    filter: grayscale(0);
    opacity: 1;
    transform: translateY(-5px);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.08);
    border-color: rgba(203, 157, 118, 0.2);
  }

  img {
    max-width: 85%;
    max-height: 70%;
    object-fit: contain;
  }
`;

const Subtitle = styled.p<{ inView: boolean }>`
  font-size: 1.1rem;
  color: var(--text);
  text-align: center;
  max-width: 600px;
  margin: 0 auto 50px;
  line-height: 1.6;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '20px'});
  transition: all 0.5s ease-out;
  transition-delay: 0.1s;
`;

const BrandLogos = () => {
  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  return (
    <LogosSection ref={ref}>
      <Container>
        <SectionTitle inView={inView}>
          We Partner With <span>Industry Leaders</span>
        </SectionTitle>

        <Subtitle inView={inView}>
          Home Vision Solutions works with the most trusted brands in smart home technology
          to provide you with reliable, cutting-edge solutions.
        </Subtitle>

        <LogosGrid inView={inView}>
          {logoUrls.map((url, index) => (
            <Logo 
              key={index}
              delay={index}
            >
              <img src={url} alt={`Partner brand ${index + 1}`} />
            </Logo>
          ))}
        </LogosGrid>
      </Container>
    </LogosSection>
  );
};

export default BrandLogos;
