import React, { useState } from 'react';
import styled, { keyframes } from 'styled-components';
import Button from './Button';

// Animations
const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
`;

const float = keyframes`
  0% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
  100% { transform: translateY(0px); }
`;

const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.05); }
  100% { transform: scale(1); }
`;

const glow = keyframes`
  0% { box-shadow: 0 0 5px rgba(255, 255, 255, 0.5); }
  50% { box-shadow: 0 0 15px rgba(255, 255, 255, 0.8); }
  100% { box-shadow: 0 0 5px rgba(255, 255, 255, 0.5); }
`;

// Styled Components
const Section = styled.section`
  padding: 120px 0;
  background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
  position: relative;
  overflow: hidden;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
  position: relative;
  z-index: 2;
`;

const SectionHeader = styled.div`
  text-align: center;
  margin-bottom: 60px;
`;

const SectionTitle = styled.h2`
  font-size: 2.8rem;
  font-weight: 700;
  color: var(--primary);
  margin-bottom: 20px;

  span {
    color: var(--accent);
    position: relative;

    &::after {
      content: '';
      position: absolute;
      bottom: -5px;
      left: 0;
      width: 100%;
      height: 3px;
      background: var(--accent);
      border-radius: 3px;
    }
  }
`;

const SectionDescription = styled.p`
  font-size: 1.2rem;
  color: var(--text);
  max-width: 700px;
  margin: 0 auto;
  line-height: 1.6;
`;

const GameWrapper = styled.div`
  margin-top: 40px;
`;

const GameHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 30px;
  background: white;
  padding: 20px;
  border-radius: 15px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
`;

const GameInfo = styled.div`
  h3 {
    font-size: 1.5rem;
    font-weight: 600;
    color: var(--primary);
    margin-bottom: 10px;
  }

  p {
    color: var(--text);
    margin: 0;
  }
`;

const ScoreDisplay = styled.div`
  text-align: center;
  background: linear-gradient(135deg, var(--primary) 0%, var(--accent) 100%);
  color: white;
  padding: 15px 25px;
  border-radius: 15px;
  min-width: 150px;

  h4 {
    font-size: 1rem;
    font-weight: 500;
    margin: 0 0 5px;
  }

  span {
    font-size: 2rem;
    font-weight: 700;
  }
`;

const GameContainer = styled.div`
  position: relative;
  overflow: hidden;
  border-radius: 20px;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
  margin-bottom: 40px;
`;

const MainImage = styled.div`
  height: 500px;
  background: url('/home-automations.jpg') center/cover no-repeat;
  border-radius: 20px;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle at 20% 30%, rgba(255, 255, 255, 0.1) 0%, rgba(0, 0, 0, 0) 60%),
                radial-gradient(circle at 80% 70%, rgba(255, 255, 255, 0.1) 0%, rgba(0, 0, 0, 0) 50%);
    z-index: 1;
  }
`;

const Hotspot = styled.div`
  position: absolute;
  width: 30px;
  height: 30px;
  background: rgba(65, 154, 255, 0.2);
  border: 3px solid white;
  border-radius: 50%;
  transform: translate(-50%, -50%);
  cursor: pointer;
  z-index: 5;
  animation: ${pulse} 2s infinite;

  &::after {
    content: '+';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    color: white;
    font-size: 20px;
    font-weight: bold;
  }

  &:hover {
    animation: ${glow} 1.5s infinite;
  }
`;

const DiscoveredHotspot = styled(Hotspot)`
  background: rgba(76, 217, 100, 0.4);
  border-color: rgba(76, 217, 100, 0.8);

  &::after {
    content: 'âœ“';
  }

  animation: none;
`;

const Tooltip = styled.div`
  position: absolute;
  background: white;
  padding: 15px;
  border-radius: 10px;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
  max-width: 250px;
  z-index: 10;
  transform: translateY(-10px);
  opacity: 0;
  visibility: hidden;
  transition: all 0.3s ease;

  &.visible {
    transform: translateY(0);
    opacity: 1;
    visibility: visible;
  }

  h4 {
    font-size: 1.1rem;
    font-weight: 600;
    color: var(--primary);
    margin-bottom: 8px;
  }

