import React, { useState } from 'react';
import styled, { keyframes, css } from 'styled-components';
import { useInView } from 'react-intersection-observer';

// Animations
const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
`;

const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.05); }
  100% { transform: scale(1); }
`;

const float = keyframes`
  0% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
  100% { transform: translateY(0px); }
`;

const rotate = keyframes`
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
`;

// Styled Components
const CommercialSection = styled.section`
  padding: 100px 0;
  background: linear-gradient(135deg, #0c1633 0%, #1a2846 100%);
  position: relative;
  overflow: hidden;
  color: #ffffff;

  &::before {
    content: '';
    position: absolute;
    width: 300px;
    height: 300px;
    background: radial-gradient(circle, rgba(0,112,243,0.1) 0%, rgba(0,112,243,0) 70%);
    top: -100px;
    right: -100px;
    border-radius: 50%;
    z-index: 1;
  }

  &::after {
    content: '';
    position: absolute;
    width: 200px;
    height: 200px;
    background: radial-gradient(circle, rgba(255,65,84,0.1) 0%, rgba(255,65,84,0) 70%);
    bottom: -50px;
    left: -50px;
    border-radius: 50%;
    z-index: 1;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
  position: relative;
  z-index: 2;
`;

const SectionHeader = styled.div`
  text-align: center;
  margin-bottom: 60px;

  animation: ${props => props.inView ? css`${fadeIn} 1s forwards` : 'none'};
  opacity: ${props => props.inView ? 1 : 0};
`;

const Title = styled.h2`
  font-size: 38px;
  font-weight: 700;
  margin-bottom: 16px;
  color: #ffffff;

  span {
    color: #00a3ff;
  }
`;

const Subtitle = styled.p`
  font-size: 18px;
  color: #b3c5ef;
  max-width: 700px;
  margin: 0 auto;
  line-height: 1.6;
`;

const CommercialShowcase = styled.div`
  display: flex;
  align-items: center;
  gap: 50px;
  margin-top: 40px;

  @media (max-width: 1024px) {
    flex-direction: column;
  }
`;

const ImageContainer = styled.div`
  flex: 1;
  position: relative;
  border-radius: 16px;
  overflow: hidden;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
  height: 500px;

  animation: ${props => props.inView ? css`${fadeIn} 1s forwards` : 'none'};
  opacity: ${props => props.inView ? 1 : 0};

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.5s ease;
  }

  &:hover img {
    transform: scale(1.02);
  }

  @media (max-width: 1024px) {
    width: 100%;
    height: 350px;
  }
`;

const Hotspot = styled.div`
  position: absolute;
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background-color: rgba(0, 163, 255, 0.3);
  border: 2px solid #00a3ff;
  transform: translate(-50%, -50%);
  z-index: 3;
  cursor: pointer;
  transition: all 0.3s ease;

  &::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 10px;
    height: 10px;
    background-color: #00a3ff;
    border-radius: 50%;
  }

  &::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 100%;
    height: 100%;
    border-radius: 50%;
    background-color: rgba(0, 163, 255, 0.3);
    animation: ${pulse} 2s infinite;
