import React from 'react';
import styled from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

const UrgencyContainer = styled.section`
  background-color: var(--primary);
  color: var(--white);
  padding: 80px 0;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(45deg, rgba(0, 0, 0, 0.2) 0%, rgba(0, 0, 0, 0) 100%);
    z-index: 1;
  }
`;

const Container = styled.div`
  max-width: 1000px;
  margin: 0 auto;
  padding: 0 20px;
  position: relative;
  z-index: 2;
  text-align: center;
`;

const UrgencyTitle = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 20px;

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const UrgencySubtitle = styled.p`
  font-size: 1.2rem;
  max-width: 800px;
  margin: 0 auto 40px;
  line-height: 1.6;
`;

const OffersGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 50px;
  margin-bottom: 50px;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 30px;
  }
`;

const OfferColumn = styled.div`
  text-align: left;
`;

const OfferTitle = styled.h3`
  font-size: 1.5rem;
  margin-bottom: 20px;
  position: relative;
  display: inline-block;

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 0;
    width: 50px;
    height: 3px;
    background-color: var(--white);
  }
`;

const OfferList = styled.ul`
  list-style: none;
  padding: 0;

  li {
    margin-bottom: 15px;
    display: flex;
    align-items: center;

    &::before {
      content: 'âœ“';
      margin-right: 10px;
      font-weight: bold;
    }
  }
`;

const CountdownContainer = styled.div`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(5px);
  padding: 20px;
  border-radius: 10px;
  margin: 0 auto 40px;
  max-width: 500px;
`;

const CountdownTitle = styled.h4`
  margin-bottom: 15px;
  font-size: 1.2rem;
`;

const Countdown = styled.div`
  display: flex;
  justify-content: center;
  gap: 30px;

  @media (max-width: 576px) {
    gap: 15px;
  }
`;

const TimeUnit = styled.div`
  .number {
    font-size: 2.5rem;
    font-weight: 700;
    background: rgba(255, 255, 255, 0.15);
    width: 70px;
    height: 70px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 8px;
    margin-bottom: 5px;

    @media (max-width: 576px) {
      font-size: 1.8rem;
      width: 50px;
      height: 50px;
    }
  }

  .label {
    font-size: 0.8rem;
    text-transform: uppercase;
    font-weight: 500;
  }
`;

const SpotsLeft = styled.div`
  display: inline-block;
  background-color: rgba(255, 255, 255, 0.2);
  padding: 8px 20px;
  border-radius: 30px;
  font-weight: 600;
  margin-bottom: 30px;
`;

const UrgencySection = () => {
  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  const scrollToSection = (sectionId: string) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <UrgencyContainer ref={ref}>
      <Container>
        <UrgencyTitle className={inView ? 'fade-in' : ''}>
          Limited Time Offer
        </UrgencyTitle>
        <UrgencySubtitle className={inView ? 'fade-in delay-1' : ''}>
          Schedule your free consultation today and receive these exclusive bonuses 
          that will enhance your smart home experience.
        </UrgencySubtitle>

        <OffersGrid>
          <OfferColumn className={inView ? 'slide-up' : ''}>
            <OfferTitle>This Month Only</OfferTitle>
            <OfferList>
              <li>Free Consultation ($300 Value)</li>
              <li>10% OFF Your Installation</li>
              <li>Priority Installation Slot</li>
              <li>Smart Home Planning Guide</li>
            </OfferList>
          </OfferColumn>

          <OfferColumn className={inView ? 'slide-up delay-1' : ''}>
            <OfferTitle>Bonus Resources</OfferTitle>
            <OfferList>
              <li>Smart Home Technology Guide</li>
              <li>Access to Exclusive Webinar</li>
              <li>Personal Energy Savings Report</li>
              <li>Lifetime Phone Support</li>
            </OfferList>
          </OfferColumn>
        </OffersGrid>

        <CountdownContainer className={inView ? 'fade-in delay-2' : ''}>
          <CountdownTitle>Limited Availability:</CountdownTitle>

          <Countdown>
            <TimeUnit>
              <div className="number">14</div>
              <div className="label">Days</div>
            </TimeUnit>
            <TimeUnit>
              <div className="number">23</div>
              <div className="label">Hours</div>
            </TimeUnit>
            <TimeUnit>
              <div className="number">59</div>
              <div className="label">Minutes</div>
            </TimeUnit>
            <TimeUnit>
              <div className="number">43</div>
              <div className="label">Seconds</div>
            </TimeUnit>
          </Countdown>
        </CountdownContainer>

        <SpotsLeft className={inView ? 'fade-in delay-3' : ''}>
          ðŸ•’ Only 5 spots left for April installation!
        </SpotsLeft>

        <Button 
          onClick={() => scrollToSection('contact')}
          className={inView ? 'slide-up delay-3' : ''}
          style={{ 
            backgroundColor: 'white', 
            color: 'var(--primary)',
            padding: '15px 35px',
            fontSize: '1.1rem'
          }}
        >
          Claim Yours Now
        </Button>
      </Container>
    </UrgencyContainer>
  );
};

export default UrgencySection;
