import React, { useState } from 'react';
import styled, { keyframes, css } from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

// Animations
const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const pulse = keyframes`
  0% {
    box-shadow: 0 0 0 0 rgba(126, 182, 224, 0.4);
  }
  70% {
    box-shadow: 0 0 0 20px rgba(126, 182, 224, 0);
  }
  100% {
    box-shadow: 0 0 0 0 rgba(126, 182, 224, 0);
  }
`;

const float = keyframes`
  0% {
    transform: translateY(0px) rotate(0deg);
  }
  50% {
    transform: translateY(-10px) rotate(1deg);
  }
  100% {
    transform: translateY(0px) rotate(0deg);
  }
`;

const rotate = keyframes`
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
`;

const shimmer = keyframes`
  0% {
    background-position: -200px 0;
  }
  100% {
    background-position: 200px 0;
  }
`;

// Styled components
const Section = styled.section`
  padding: 120px 0;
  background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
  position: relative;
  overflow: hidden;
`;

const BackgroundCircles = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 0;

  &::before, &::after {
    content: '';
    position: absolute;
    border-radius: 50%;
    opacity: 0.1;
  }

  &::before {
    width: 500px;
    height: 500px;
    background: radial-gradient(circle, #7eb6e0 0%, transparent 70%);
    top: -200px;
    left: -200px;
  }

  &::after {
    width: 600px;
    height: 600px;
    background: radial-gradient(circle, #7eb6e0 0%, transparent 70%);
    bottom: -300px;
    right: -200px;
  }
`;

const Container = styled.div`
  max-width: 1400px;
  margin: 0 auto;
  padding: 0 30px;
  position: relative;
  z-index: 1;
`;

const SectionHeader = styled.div<{ inView: boolean }>`
  text-align: center;
  margin-bottom: 80px;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '30px'});
  transition: all 0.8s ease-out;
`;

const Title = styled.h2`
  font-size: 3rem;
  margin-bottom: 20px;
  color: #333;

  span {
    color: #7eb6e0;
    position: relative;

    &::after {
      content: '';
      position: absolute;
      bottom: 5px;
      left: 0;
      width: 100%;
      height: 8px;
      background-color: rgba(126, 182, 224, 0.2);
      z-index: -1;
    }
  }
`;

const Subtitle = styled.p`
  font-size: 1.2rem;
  color: #666;
  max-width: 700px;
  margin: 0 auto;
  line-height: 1.6;
`;

const ContentWrapper = styled.div<{ inView: boolean }>`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 80px;
  align-items: center;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '40px'});
  transition: all 1s cubic-bezier(0.165, 0.84, 0.44, 1);

  @media (max-width: 992px) {
    grid-template-columns: 1fr;
    gap: 60px;
  }
`;

const LeftColumn = styled.div`
  @media (max-width: 992px) {
    order: 2;
  }
`;

const RightColumn = styled.div`
  position: relative;

  @media (max-width: 992px) {
    order: 1;
  }
`;

const ThermostatWrapper = styled.div`
  position: relative;
`;

const ThermostatDisplay = styled.div`
  width: 100%;
  max-width: 350px;
  height: 350px;
  margin: 0 auto;
  background: white;
  border-radius: 50%;
  box-shadow: 0 20px 50px rgba(0, 0, 0, 0.1);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    width: 250px;
    height: 250px;
    border-radius: 50%;
    border: 2px solid rgba(126, 182, 224, 0.2);
  }

  &::after {
    content: '';
    position: absolute;
    width: 200px;
    height: 200px;
    border-radius: 50%;
    border: 2px solid rgba(126, 182, 224, 0.1);
  }
`;

const Temperature = styled.div`
  font-size: 5rem;
  font-weight: 700;
  color: #333;
  line-height: 1;
  position: relative;

  span {
    font-size: 2.5rem;
    position: absolute;
    top: 15px;
    right: -30px;
    color: #7eb6e0;
  }
`;

const Status = styled.div`
  margin-top: 10px;
  font-size: 1.2rem;
  color: #666;

  span {
    display: inline-block;
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background: #7eb6e0;
    margin-right: 8px;
    position: relative;

    &::after {
      content: '';
      position: absolute;
      top: -5px;
      left: -5px;
      right: -5px;
      bottom: -5px;
      border-radius: 50%;
      background: rgba(126, 182, 224, 0.3);
      animation: ${pulse} 2s infinite;
    }
  }
`;

const ControlButtons = styled.div`
  display: flex;
  gap: 30px;
  margin-top: 40px;
  justify-content: center;
`;

const ControlButton = styled.button<{ isActive?: boolean }>`
  width: 60px;
  height: 60px;
  border-radius: 50%;
  border: none;
  background: ${props => props.isActive ? '#7eb6e0' : 'white'};
  color: ${props => props.isActive ? 'white' : '#333'};
  font-size: 1.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.15);
  }

  svg {
    width: 24px;
    height: 24px;
  }
`;

const FeaturesList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 30px;
`;

const FeatureItem = styled.div<{ delay: number, inView: boolean }>`
  display: flex;
  gap: 25px;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateX(${props => props.inView ? 0 : '30px'});
  transition: all 0.6s ease-out;
  transition-delay: ${props => props.delay * 0.1}s;
`;

const IconContainer = styled.div`
  flex-shrink: 0;
  width: 60px;
  height: 60px;
  background-color: rgba(126, 182, 224, 0.15);
  border-radius: 15px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #7eb6e0;
  font-size: 1.5rem;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: -10px;
    left: -10px;
    right: -10px;
    bottom: -10px;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.4), transparent);
    animation: ${shimmer} 2s infinite;
    transform: skewX(-20deg);
  }

  svg {
    width: 30px;
    height: 30px;
    stroke-width: 2;
  }
`;

const FeatureContent = styled.div`
  h4 {
    font-size: 1.3rem;
    margin-bottom: 10px;
    color: #333;
  }

  p {
    color: #666;
    line-height: 1.6;
    margin: 0;
  }
`;

const FloatingBadge = styled.div`
  position: absolute;
  top: -20px;
  right: 50px;
  width: 120px;
  height: 120px;
  background: white;
  border-radius: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  box-shadow: 0 15px 30px rgba(0, 0, 0, 0.1);
  animation: ${float} 5s ease-in-out infinite;
  z-index: 2;

  svg {
    color: #7eb6e0;
    width: 40px;
    height: 40px;
    margin-bottom: 10px;
  }

  p {
    margin: 0;
    font-size: 0.9rem;
    font-weight: 600;
    color: #333;
  }
`;

const CtaContainer = styled.div<{ inView: boolean }>`
  margin-top: 50px;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '20px'});
  transition: all 0.8s ease-out;
  transition-delay: 0.6s;
`;

// Thermostat features
const thermostatFeatures = [
  {
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" d="M3.75 13.5l10.5-11.25L12 10.5h8.25L9.75 21.75 12 13.5H3.75z" />
      </svg>
    ),
    title: "Energy Efficient",
    description: "Save up to 23% annually on heating and cooling costs with intelligent temperature management."
  },
  {
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" d="M9 17.25v1.007a3 3 0 01-.879 2.122L7.5 21h9l-.621-.621A3 3 0 0115 18.257V17.25m6-12V15a2.25 2.25 0 01-2.25 2.25H5.25A2.25 2.25 0 013 15V5.25m18 0A2.25 2.25 0 0018.75 3H5.25A2.25 2.25 0 003 5.25m18 0V12a2.25 2.25 0 01-2.25 2.25H5.25A2.25 2.25 0 013 12V5.25" />
      </svg>
    ),
    title: "Smart Learning",
    description: "Automatically adjusts to your schedule and preferences, learning from your behavior patterns."
  },
  {
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" d="M10.5 1.5H8.25A2.25 2.25 0 006 3.75v16.5a2.25 2.25 0 002.25 2.25h7.5A2.25 2.25 0 0018 20.25V3.75a2.25 2.25 0 00-2.25-2.25H13.5m-3 0V3h3V1.5m-3 0h3m-3 18.75h3" />
      </svg>
    ),
    title: "Mobile Control",
    description: "Control your home's temperature from anywhere using our intuitive smartphone app."
  },
  {
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path strokeLinecap="round" strokeLinejoin="round" d="M12 18v-5.25m0 0a6.01 6.01 0 001.5-.189m-1.5.189a6.01 6.01 0 01-1.5-.189m3.75 7.478a12.06 12.06 0 01-4.5 0m3.75 2.383a14.406 14.406 0 01-3 0M14.25 18v-.192c0-.983.658-1.823 1.508-2.316a7.5 7.5 0 10-7.517 0c.85.493 1.509 1.333 1.509 2.316V18" />
      </svg>
    ),
    title: "Smart Zoning",
    description: "Control temperature by room with multiple sensors for customized comfort throughout your home."
  }
];

const SmartThermostat = () => {
  const [temperature, setTemperature] = useState(72);
  const [mode, setMode] = useState<'cooling' | 'heating' | 'off'>('off');

  const [headerRef, headerInView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const [contentRef, contentInView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const [ctaRef, ctaInView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const increaseTemp = () => {
    if (temperature < 85) {
      setTemperature(prev => prev + 1);
      setMode('heating');
    }
  };

  const decreaseTemp = () => {
    if (temperature > 65) {
      setTemperature(prev => prev - 1);
      setMode('cooling');
    }
  };

  const togglePower = () => {
    if (mode === 'off') {
      setMode('heating');
    } else {
      setMode('off');
    }
  };

  return (
    <Section id="smart-thermostat">
      <BackgroundCircles />
      <Container>
        <SectionHeader ref={headerRef} inView={headerInView}>
          <Title>Smart <span>Thermostat</span></Title>
          <Subtitle>
            Intelligent climate control that learns your preferences, saves energy, and provides personalized comfort.
          </Subtitle>
        </SectionHeader>

        <ContentWrapper ref={contentRef} inView={contentInView}>
          <LeftColumn>
            <FeaturesList>
              {thermostatFeatures.map((feature, index) => (
                <FeatureItem 
                  key={index} 
                  delay={index + 1} 
                  inView={contentInView}
                >
                  <IconContainer>
                    {feature.icon}
                  </IconContainer>
                  <FeatureContent>
                    <h4>{feature.title}</h4>
                    <p>{feature.description}</p>
                  </FeatureContent>
                </FeatureItem>
              ))}
            </FeaturesList>

            <CtaContainer ref={ctaRef} inView={ctaInView}>
              <Button 
                text="Explore Features"
                style={{ 
                  background: "#7eb6e0",
                  color: "#fff",
                  padding: "15px 35px",
                  fontSize: "1.1rem",
                  borderRadius: "12px",
                  fontWeight: "600"
                }}
              />
            </CtaContainer>
          </LeftColumn>

          <RightColumn>
            <ThermostatWrapper>
              <FloatingBadge>
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" d="M12 3v2.25m6.364.386l-1.591 1.591M21 12h-2.25m-.386 6.364l-1.591-1.591M12 18.75V21m-4.773-4.227l-1.591 1.591M5.25 12H3m4.227-4.773L5.636 5.636M15.75 12a3.75 3.75 0 11-7.5 0 3.75 3.75 0 017.5 0z" />
                </svg>
                <p>Eco-Saving</p>
              </FloatingBadge>

              <ThermostatDisplay>
                <Temperature>
                  {temperature}<span>Â°F</span>
                </Temperature>
                {mode !== 'off' && (
                  <Status>
                    <span></span>
                    {mode === 'cooling' ? 'Cooling' : 'Heating'}
                  </Status>
                )}
                {mode === 'off' && (
                  <Status>System Off</Status>
                )}
              </ThermostatDisplay>

              <ControlButtons>
                <ControlButton onClick={decreaseTemp} disabled={mode === 'off'}>
                  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" d="M19.5 12h-15" />
                  </svg>
                </ControlButton>

                <ControlButton onClick={togglePower} isActive={mode !== 'off'}>
                  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" d="M5.636 5.636a9 9 0 1012.728 0M12 3v9" />
                  </svg>
                </ControlButton>

                <ControlButton onClick={increaseTemp} disabled={mode === 'off'}>
                  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" d="M12 4.5v15m7.5-7.5h-15" />
                  </svg>
                </ControlButton>
              </ControlButtons>
            </ThermostatWrapper>
          </RightColumn>
        </ContentWrapper>
      </Container>
    </Section>
  );
};

export default SmartThermostat;
