import React from 'react';
import styled, { keyframes } from 'styled-components';
import Button from './Button';

// Animations
const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
`;

const float = keyframes`
  0% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
  100% { transform: translateY(0px); }
`;

const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.05); }
  100% { transform: scale(1); }
`;

// Styled Components
const Section = styled.section`
  padding: 120px 0;
  background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
  position: relative;
  overflow: hidden;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
  position: relative;
  z-index: 2;
`;

const SectionHeader = styled.div`
  text-align: center;
  margin-bottom: 60px;
`;

const SectionTitle = styled.h2`
  font-size: 2.8rem;
  font-weight: 700;
  color: var(--primary);
  margin-bottom: 20px;

  span {
    color: var(--accent);
    position: relative;

    &::after {
      content: '';
      position: absolute;
      bottom: -5px;
      left: 0;
      width: 100%;
      height: 3px;
      background: var(--accent);
      border-radius: 3px;
    }
  }
`;

const SectionDescription = styled.p`
  font-size: 1.2rem;
  color: var(--text);
  max-width: 700px;
  margin: 0 auto;
  line-height: 1.6;
`;

const ContentWrapper = styled.div`
  display: flex;
  flex-direction: column;
  gap: 60px;
  margin-top: 40px;

  @media (min-width: 992px) {
    flex-direction: row;
    align-items: center;
  }
`;

const ImageContainer = styled.div`
  flex: 1;
  position: relative;

  @media (min-width: 992px) {
    flex: 1.2;
  }
`;

const MainImage = styled.div`
  height: 500px;
  background: url('/home-automationS.jpg') center/cover no-repeat;
  border-radius: 20px;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: radial-gradient(circle at 20% 30%, rgba(255, 255, 255, 0.2) 0%, rgba(255, 255, 255, 0) 60%),
                      radial-gradient(circle at 80% 70%, rgba(255, 255, 255, 0.15) 0%, rgba(255, 255, 255, 0) 50%);
    z-index: 1;
  }

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: url("data:image/svg+xml,%3Csvg width='100' height='100' viewBox='0 0 100 100' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M11 18c3.866 0 7-3.134 7-7s-3.134-7-7-7-7 3.134-7 7 3.134 7 7 7zm48 25c3.866 0 7-3.134 7-7s-3.134-7-7-7-7 3.134-7 7 3.134 7 7 7zm-43-7c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zm63 31c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zM34 90c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zm56-76c1.657 0 3-1.343 3-3s-1.343-3-3-3-3 1.343-3 3 1.343 3 3 3zM12 86c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm28-65c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm23-11c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zm-6 60c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm29 22c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zM32 63c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zm57-13c2.76 0 5-2.24 5-5s-2.24-5-5-5-5 2.24-5 5 2.24 5 5 5zm-9-21c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2zM60 91c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2zM35 41c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2zM12 60c1.105 0 2-.895 2-2s-.895-2-2-2-2 .895-2 2 .895 2 2 2z' fill='%23ffffff' fill-opacity='0.1' fill-rule='evenodd'/%3E%3C/svg%3E");
    opacity: 0.5;
    z-index: 1;
  }

  &:hover {
    transform: translateY(-5px);
    transition: transform 0.3s ease;
  }
`;

const FloatingBadge = styled.div`
  position: absolute;
  bottom: 30px;
  right: 30px;
  background: linear-gradient(135deg, var(--primary) 0%, var(--accent) 100%);
  color: white;
  padding: 15px 25px;
  border-radius: 12px;
  font-weight: 600;
  font-size: 1.1rem;
  z-index: 2;
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.15);
  animation: ${float} 4s ease-in-out infinite;
`;

const ContentContainer = styled.div`
  flex: 1;
`;

const FeaturesList = styled.div`
  margin-bottom: 30px;
`;

const FeatureItem = styled.div<{ delay?: string }>`
  display: flex;
  align-items: flex-start;
  margin-bottom: 25px;
  animation: ${fadeIn} 0.5s forwards;
  animation-delay: ${props => props.delay || '0s'};
  opacity: 0;

  &:last-child {
    margin-bottom: 0;
  }
`;

const FeatureIcon = styled.div`
  width: 50px;
  height: 50px;
  background: rgba(65, 154, 255, 0.1);
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 20px;
  flex-shrink: 0;
  font-size: 1.5rem;
  color: var(--primary);
`;

const FeatureContent = styled.div`
  h4 {
    font-size: 1.25rem;
    font-weight: 600;
    margin: 0 0 10px;
    color: var(--primary);
  }

  p {
    font-size: 1rem;
    color: var(--text);
    line-height: 1.6;
    margin: 0;
  }
`;

const CTAButton = styled(Button)`
  margin-top: 20px;
  padding: 12px 30px;
  font-size: 1.1rem;
`;

const HomeAutomation = () => {
  const features = [
    {
      icon: 'üè†',
      title: 'Whole Home Integration',
      description: 'Connect all your smart devices into a seamless ecosystem that works together to create a more comfortable and efficient home.'
    },
    {
      icon: 'üì±',
      title: 'Mobile Control',
      description: 'Control all aspects of your home from anywhere using our intuitive mobile app, with customizable dashboards and scenes.'
    },
    {
      icon: 'üîê',
      title: 'Enhanced Security',
      description: 'Protect your home with integrated security features including smart locks, cameras, and motion sensors with instant alerts.'
    },
    {
      icon: 'üí°',
      title: 'Energy Efficiency',
      description: 'Reduce energy consumption with automated lighting, smart thermostats, and intelligent scheduling that adapts to your lifestyle.'
    },
    {
      icon: 'üó£Ô∏è',
      title: 'Voice Commands',
      description: 'Control your entire home with simple voice commands through integration with all major voice assistants.'
    }
  ];

  return (
    <Section id="home-automation">
      <Container>
        <SectionHeader>
          <SectionTitle>Smart Home <span>Automation</span></SectionTitle>
          <SectionDescription>
            Transform your home with intelligent automation that enhances comfort, convenience, and security.
            Our system integrates all your smart devices for a truly connected living experience.
          </SectionDescription>
        </SectionHeader>

        <ContentWrapper>
          <ImageContainer>
            <MainImage />
            <FloatingBadge>Intelligent Living</FloatingBadge>
          </ImageContainer>

          <ContentContainer>
            <FeaturesList>
              {features.map((feature, index) => (
                <FeatureItem key={index} delay={`${0.2 * index}s`}>
                  <FeatureIcon>{feature.icon}</FeatureIcon>
                  <FeatureContent>
                    <h4>{feature.title}</h4>
                    <p>{feature.description}</p>
                  </FeatureContent>
                </FeatureItem>
              ))}
            </FeaturesList>

            <CTAButton>Explore Automation Solutions</CTAButton>
          </ContentContainer>
        </ContentWrapper>
      </Container>
    </Section>
  );
};

export default HomeAutomation;
