const generateParticles = (count: number) => {
  const particles = [];

  for (let i = 0; i < count; i++) {
    particles.push({
      size: Math.random() * 3 + 1,
      posX: Math.random() * 100,
      posY: Math.random() * 100,
      speed: Math.random() * 10 + 10
    });
  }

  return particles;
};

