import React, { useEffect, useState, useRef } from 'react';
import styled, { keyframes, createGlobalStyle, css } from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

// Essential animations
const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
`;

const shimmer = keyframes`
  0% { background-position: -468px 0; }
  100% { background-position: 468px 0; }
`;

const float = keyframes`
  0% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
  100% { transform: translateY(0px); }
`;

const pulse = keyframes`
  0% { box-shadow: 0 0 0 0 rgba(203, 157, 118, 0.4); }
  70% { box-shadow: 0 0 0 15px rgba(203, 157, 118, 0); }
  100% { box-shadow: 0 0 0 0 rgba(203, 157, 118, 0); }
`;

const gradientMove = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

const typewriter = keyframes`
  from { width: 0; }
  to { width: 100%; }
`;

const blinkCursor = keyframes`
  0%, 100% { border-right-color: rgba(255, 255, 255, 0.7); }
  50% { border-right-color: transparent; }
`;

const rotateIn = keyframes`
  from { transform: rotateX(30deg); opacity: 0; }
  to { transform: rotateX(0); opacity: 1; }
`;

const slideFromLeft = keyframes`
  from { transform: translateX(-100px); opacity: 0; }
  to { transform: translateX(0); opacity: 1; }
`;

const particleAnimation = keyframes`
  0% { transform: translate(0, 0); opacity: 0; }
  50% { opacity: 0.5; }
  100% { transform: translate(20px, -20px); opacity: 0; }
`;

const shake = keyframes`
  0%, 100% { transform: translateX(0); }
  10%, 30%, 50%, 70%, 90% { transform: translateX(-5px); }
  20%, 40%, 60%, 80% { transform: translateX(5px); }
`;

// Global styles
const GlobalStyle = createGlobalStyle`
  .hero-video {
    will-change: transform;
    transition: all 1.5s ease-in-out;
  }

  .hero-video.in-view {
    transform: scale(1.05);
  }
`;

// Main components
const HeroContainer = styled.section`
  position: relative;
  color: #fff;
  height: 100vh;
  width: 100%;
  background: #0a0a0a;
  overflow: hidden;
  display: grid;
  grid-template-columns: 1fr 1fr;

  @media (max-width: 992px) {
    grid-template-columns: 1fr;
    grid-template-rows: auto 1fr;
  }
`;

const LeftSection = styled.div`
  position: relative;
  z-index: 10;
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding: 0 5vw 0 10vw;
  background: linear-gradient(135deg, #0a0a0a 0%, #151515 100%);
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    width: 100px;
    height: 100%;
    background: linear-gradient(to left, #0a0a0a, transparent);
    z-index: 3;
  }

  @media (max-width: 992px) {
    padding: 10vh 5vw;
    grid-row: 2;
    &::before {
      display: none;
    }
  }
`;

const RightSection = styled.div`
  position: relative;
  overflow: hidden;
  height: 100vh;
  background: #0a0a0a;

  @media (max-width: 992px) {
    height: 50vh;
    grid-row: 1;
  }
`;

const ContentWrapper = styled.div<{ inView: boolean }>`
  max-width: 600px;
  opacity: 0;
  transform: translateY(30px);
  transition: opacity 0.8s ease-out, transform 1s ease-out;

  ${props => props.inView && css`
    opacity: 1;
    transform: translateY(0);
  `}

  @media (max-width: 992px) {
    max-width: 100%;
  }
`;

const HeroHeading = styled.h1`
  font-size: clamp(3rem, 5vw, 4.5rem);
  font-weight: 900;
  line-height: 1.1;
  margin: 0 0 30px;
  position: relative;

  .main-title {
    display: block;
    letter-spacing: -1px;
    color: #fff;
    transform-style: preserve-3d;
    perspective: 1000px;
    margin-bottom: 10px;
    animation: ${rotateIn} 1s cubic-bezier(0.23, 1, 0.32, 1) forwards;
  }

  .accent {
    display: block;
    background: linear-gradient(to right, #CB9D76, #d3b595, #CB9D76);
    background-size: 200% auto;
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    animation: ${gradientMove} 5s ease infinite;
    font-style: italic;
    margin-bottom: 8px;
  }

  &::after {
    content: '';
    position: absolute;
    bottom: -15px;
    left: 0;
    width: 80px;
    height: 5px;
    background: #CB9D76;
    border-radius: 10px;
  }
`;

const TypewriterText = styled.span`
  display: inline-block;
  color: rgba(255, 255, 255, 0.8);
  font-size: clamp(1rem, 1.5vw, 1.5rem);
  font-weight: 400;
  letter-spacing: 1px;
  margin-bottom: 30px;
  position: relative;
  overflow: hidden;
  white-space: nowrap;
  width: 0;
  border-right: 2px solid rgba(255, 255, 255, 0.7);
  animation: 
    ${typewriter} 3s steps(40) 1s forwards,
    ${blinkCursor} 0.75s step-end infinite;
`;

const BrandBar = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 40px;
`;

const BrandLabel = styled.span`
  font-size: 0.8rem;
  text-transform: uppercase;
  letter-spacing: 1px;
  color: rgba(255, 255, 255, 0.5);
  margin-right: 15px;
  padding-right: 15px;
  border-right: 1px solid rgba(255, 255, 255, 0.2);
`;

const BrandLogos = styled.div`
  display: flex;
  gap: 15px;
  align-items: center;
  overflow: hidden;
`;

const BrandLogo = styled.div`
  height: 25px;
  opacity: 0.6;
  transition: opacity 0.3s ease;

  &:hover {
    opacity: 1;
  }

  img {
    height: 100%;
    filter: brightness(0) invert(1);
  }
`;

const FeatureList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0 0 40px;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 15px;

  @media (max-width: 480px) {
    grid-template-columns: 1fr;
  }
`;

const FeatureItem = styled.li`
  display: flex;
  align-items: center;
  font-size: 1rem;
  color: rgba(255, 255, 255, 0.8);

  svg {
    width: 18px;
    height: 18px;
    color: #CB9D76;
    margin-right: 10px;
    flex-shrink: 0;
  }

  span {
    position: relative;

    &::after {
      content: '';
      position: absolute;
      bottom: -2px;
      left: 0;
      width: 100%;
      height: 1px;
      background: linear-gradient(to right, #CB9D76, transparent);
      transform: scaleX(0);
      transform-origin: left;
      transition: transform 0.3s ease;
    }

    &:hover::after {
      transform: scaleX(1);
    }
  }
`;

const FormContainer = styled.div<{ inView: boolean }>`
  background: rgba(30, 30, 30, 0.7);
  backdrop-filter: blur(10px);
  border-radius: 16px;
  border: 1px solid rgba(203, 157, 118, 0.2);
  padding: 30px;
  width: 100%;
  max-width: 500px;
  position: relative;
  overflow: hidden;
  opacity: 0;
  transform: translateY(30px);
  transition: all 0.8s ease;
  transition-delay: 0.3s;

  ${props => props.inView && css`
    opacity: 1;
    transform: translateY(0);
  `}

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 3px;
    background: linear-gradient(to right, #CB9D76, transparent);
  }
`;

const FormTitle = styled.h3`
  font-size: 1.5rem;
  margin: 0 0 5px;
  display: flex;
  align-items: center;

  svg {
    width: 20px;
    height: 20px;
    margin-right: 10px;
    color: #CB9D76;
  }
`;

const FormSubtitle = styled.p`
  font-size: 0.9rem;
  margin: 0 0 25px;
  color: rgba(255, 255, 255, 0.7);
`;

const CountdownTimer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  background: rgba(203, 157, 118, 0.1);
  border: 1px dashed rgba(203, 157, 118, 0.3);
  border-radius: 8px;
  padding: 8px 15px;
  margin-bottom: 20px;
`;

const TimeValue = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 0 10px;
`;

const TimerDigit = styled.span`
  font-size: 1.5rem;
  font-weight: 700;
  color: #CB9D76;
`;

const TimerLabel = styled.span`
  font-size: 0.7rem;
  text-transform: uppercase;
  color: rgba(255, 255, 255, 0.6);
  letter-spacing: 1px;
`;

const TimerSeparator = styled.span`
  font-size: 1.5rem;
  font-weight: 700;
  color: rgba(255, 255, 255, 0.3);
`;

const InputWrapper = styled.div`
  margin-bottom: 15px;
`;

const Label = styled.label`
  display: block;
  font-size: 0.85rem;
  margin-bottom: 5px;
  color: rgba(255, 255, 255, 0.8);
`;

const Input = styled.input`
  width: 100%;
  padding: 14px;
  border-radius: 8px;
  border: 1px solid rgba(203, 157, 118, 0.3);
  background: rgba(0, 0, 0, 0.2);
  color: #fff;
  font-size: 1rem;
  transition: all 0.3s ease;

  &:focus {
    outline: none;
    border-color: rgba(203, 157, 118, 0.8);
    box-shadow: 0 0 0 3px rgba(203, 157, 118, 0.1);
  }

  &::placeholder {
    color: rgba(255, 255, 255, 0.3);
  }

  &.shake {
    animation: ${shake} 0.5s ease;
    border-color: #e74c3c;
  }
`;

const SubmitButton = styled.button`
  width: 100%;
  padding: 16px;
  background: linear-gradient(135deg, #CB9D76, #a67c5b);
  border: none;
  border-radius: 8px;
  color: #fff;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(to right, transparent, rgba(255, 255, 255, 0.2), transparent);
    transform: translateX(-100%);
    transition: transform 0.6s ease;
  }

  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2);

    &::before {
      transform: translateX(100%);
    }
  }
`;

const PrivacyNote = styled.p`
  font-size: 0.75rem;
  color: rgba(255, 255, 255, 0.5);
  text-align: center;
  margin-top: 15px;
`;

const LogoSection = styled.div`
  position: absolute;
  top: 40px;
  left: 10vw;
  z-index: 100;
  display: flex;
  align-items: center;
  font-size: 1.5rem;
  font-weight: 700;

  @media (max-width: 992px) {
    top: 20px;
  }
`;

const VideoContainer = styled.div`
  width: 100%;
  height: 100%;
  position: relative;
  overflow: hidden;
  transform-style: preserve-3d;
  perspective: 1000px;

  &::before {
    content: '';
    position: absolute;
    inset: 0;
    background: linear-gradient(to right, rgba(10, 10, 10, 0.7), rgba(10, 10, 10, 0.3));
    z-index: 2;
  }

  &::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 30%;
    background: linear-gradient(to top, #0a0a0a, transparent);
    z-index: 1;
  }
`;

const VideoOverlayPattern = styled.div`
  position: absolute;
  inset: 0;
  z-index: 3;
  background-image: 
    linear-gradient(rgba(10, 10, 10, 0.3) 1px, transparent 1px),
    linear-gradient(90deg, rgba(10, 10, 10, 0.3) 1px, transparent 1px);
  background-size: 20px 20px;
  opacity: 0.3;
`;

const Video = styled.video`
  width: 100%;
  height: 100%;
  object-fit: cover;
  transform: scale(1.1);
  transition: all 1s ease;
`;

const FloatingShape = styled.div<{ size: number, top: string, left: string, delay: number }>`
  position: absolute;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  border-radius: 50%;
  background: radial-gradient(circle at 30% 30%, rgba(203, 157, 118, 0.8), rgba(203, 157, 118, 0.1));
  top: ${props => props.top};
  left: ${props => props.left};
  filter: blur(30px);
  opacity: 0.3;
  z-index: 1;
  animation: ${float} 15s ease-in-out infinite;
  animation-delay: ${props => props.delay}s;
`;

const ParticlesContainer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  z-index: 3;
  pointer-events: none;
`;

const Particle = styled.div<{ size: number, posX: string, posY: string, delay: number }>`
  position: absolute;
  top: ${props => props.posY};
  left: ${props => props.posX};
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  background: radial-gradient(circle at center, rgba(203, 157, 118, 0.8), rgba(203, 157, 118, 0.1));
  border-radius: 50%;
  opacity: 0;
  animation: ${particleAnimation} 10s ease-out infinite;
  animation-delay: ${props => props.delay}s;
`;

// Generate particles
const generateParticles = (count: number) => {
  const particles = [];
  for (let i = 0; i < count; i++) {
    particles.push({
      size: Math.random() * 8 + 2,
      posX: `${Math.random() * 100}%`,
      posY: `${Math.random() * 100}%`,
      delay: Math.random() * 5
    });
  }
  return particles;
};

// Main component
const Hero = () => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const [videoError, setVideoError] = useState(false);
  const [formData, setFormData] = useState({ name: '', phone: '' });
  const [validationErrors, setValidationErrors] = useState<{name?: boolean, phone?: boolean}>({});
  const [timeLeft, setTimeLeft] = useState({
    hours: 12,
    minutes: 0,
    seconds: 0
  });
  const [particles] = useState(() => generateParticles(20));

  const [contentRef, contentInView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const [formRef, formInView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  // Logo brands
  const brands = [
    { src: '/control4.png', alt: 'Control4' },
    { src: '/lutron.png', alt: 'Lutron' },
    { src: '/sonos.png', alt: 'Sonos' },
    { src: '/sony.png', alt: 'Sony' }
  ];

  // Features for the smart home
  const features = [
    'Voice-Controlled Systems',
    'Energy Monitoring',
    'Remote Access',
    'Automated Lighting'
  ];

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.play().catch(err => {
        console.error('Video playback error:', err);
        setVideoError(true);
      });
    }

    // Countdown timer
    const timer = setInterval(() => {
      setTimeLeft(prev => {
        if (prev.hours === 0 && prev.minutes === 0 && prev.seconds === 0) {
          clearInterval(timer);
          return prev;
        }

        let newHours = prev.hours;
        let newMinutes = prev.minutes;
        let newSeconds = prev.seconds - 1;

        if (newSeconds < 0) {
          newSeconds = 59;
          newMinutes--;
        }

        if (newMinutes < 0) {
          newMinutes = 59;
          newHours--;
        }

        return { hours: newHours, minutes: newMinutes, seconds: newSeconds };
      });
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    // Clear validation error when user types
    if (validationErrors[name as keyof typeof validationErrors]) {
      setValidationErrors(prev => ({ ...prev, [name]: false }));
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Simple validation
    const errors: {name?: boolean, phone?: boolean} = {};
    if (!formData.name.trim()) {
      errors.name = true;
    }
    if (!formData.phone.trim() || !/^[0-9]{10,}$/.test(formData.phone.replace(/[^0-9]/g, ''))) {
      errors.phone = true;
    }

    if (Object.keys(errors).length > 0) {
      setValidationErrors(errors);
      return;
    }

    // In a real app, you would send this data to your server
    console.log('Form submitted:', formData);

    // Mock successful submission
    alert('Thank you for your interest! Our team will contact you shortly.');
    setFormData({ name: '', phone: '' });
  };

  const formatTime = (value: number) => {
    return value < 10 ? `0${value}` : value;
  };

  return (
    <HeroContainer id="hero">
      <GlobalStyle />

      <LogoSection>
        <svg viewBox="0 0 24 24" fill="currentColor" width="32" height="32" style={{ marginRight: '10px', color: '#CB9D76' }}>
          <path d="M12 2L1 21h22L12 2zm0 4.2L20.1 19h-16L12 6.2z" />
        </svg>
        HOME<span style={{ color: '#CB9D76' }}>VISION</span>
      </LogoSection>

      <LeftSection>
        <ContentWrapper ref={contentRef} inView={contentInView}>
          <HeroHeading>
            <span className="main-title">Transform Your Home</span>
            <span className="accent">with Smart Technology</span>
          </HeroHeading>

          <TypewriterText>Experience the future of home automation today</TypewriterText>

          <BrandBar>
            <BrandLabel>Trusted By</BrandLabel>
            <BrandLogos>
              {brands.map((brand, index) => (
                <BrandLogo key={index}>
                  <img src={brand.src} alt={brand.alt} />
                </BrandLogo>
              ))}
            </BrandLogos>
          </BrandBar>

          <FeatureList>
            {features.map((feature, index) => (
              <FeatureItem key={index}>
                <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                  <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
                  <polyline points="22 4 12 14.01 9 11.01"></polyline>
                </svg>
                <span>{feature}</span>
              </FeatureItem>
            ))}
          </FeatureList>

          <FormContainer ref={formRef} inView={formInView}>
            <FormTitle>
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z"></path>
              </svg>
              Exclusive Consultation
            </FormTitle>
            <FormSubtitle>Get a personalized smart home plan at 20% off standard rates</FormSubtitle>

            <CountdownTimer>
              <TimeValue>
                <TimerDigit>{formatTime(timeLeft.hours)}</TimerDigit>
                <TimerLabel>Hours</TimerLabel>
              </TimeValue>
              <TimerSeparator>:</TimerSeparator>
              <TimeValue>
                <TimerDigit>{formatTime(timeLeft.minutes)}</TimerDigit>
                <TimerLabel>Minutes</TimerLabel>
              </TimeValue>
              <TimerSeparator>:</TimerSeparator>
              <TimeValue>
                <TimerDigit>{formatTime(timeLeft.seconds)}</TimerDigit>
                <TimerLabel>Seconds</TimerLabel>
              </TimeValue>
            </CountdownTimer>

            <form onSubmit={handleSubmit}>
              <InputWrapper>
                <Label htmlFor="name">Full Name</Label>
                <Input 
                  id="name"
                  name="name"
                  placeholder="Enter your name"
                  value={formData.name}
                  onChange={handleInputChange}
                  className={validationErrors.name ? 'shake' : ''}
                />
              </InputWrapper>

              <InputWrapper>
                <Label htmlFor="phone">Phone Number</Label>
                <Input 
                  id="phone"
                  name="phone"
                  placeholder="Enter your phone number"
                  value={formData.phone}
                  onChange={handleInputChange}
                  className={validationErrors.phone ? 'shake' : ''}
                />
              </InputWrapper>

              <SubmitButton type="submit">Request Your Free Quote</SubmitButton>
            </form>

            <PrivacyNote>Your information is secure and will never be shared with third parties.</PrivacyNote>
          </FormContainer>
        </ContentWrapper>

        <FloatingShape size={200} top="10%" left="-100px" delay={0} />
        <FloatingShape size={150} top="70%" left="-50px" delay={2} />
      </LeftSection>

      <RightSection>
        <VideoContainer>
          <Video 
            ref={videoRef}
            autoPlay 
            muted 
            loop 
            playsInline
            onError={() => setVideoError(true)}
          >
            <source src="/video5.mp4" type="video/mp4" />
            Your browser does not support the video tag.
          </Video>
          <VideoOverlayPattern />
        </VideoContainer>

        <ParticlesContainer>
          {particles.map((particle, index) => (
            <Particle 
              key={index}
              size={particle.size}
              posX={particle.posX}
              posY={particle.posY}
              delay={particle.delay}
            />
          ))}
        </ParticlesContainer>
      </RightSection>
    </HeroContainer>
  );
};

export default Hero;
