import React, { useEffect, useState, useRef } from 'react';
import styled, { keyframes } from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

// Replace placeholder with actual images from public
const imageUrls = [
  '/b206-1723539512-1440x838.png',
  '/1bed.png',
  '/2bed.png',
  '/3bed.png',
  '/cusrombed.png'
];

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const slideRight = keyframes`
  from {
    transform: translateX(-100%);
  }
  to {
    transform: translateX(0);
  }
`;

const slideUp = keyframes`
  from {
    transform: translateY(100%);
  }
  to {
    transform: translateY(0);
  }
`;

const pulse = keyframes`
  0% {
    box-shadow: 0 0 0 0 rgba(203, 157, 118, 0.6);
  }
  70% {
    box-shadow: 0 0 0 15px rgba(203, 157, 118, 0);
  }
  100% {
    box-shadow: 0 0 0 0 rgba(203, 157, 118, 0);
  }
`;

const HeroContainer = styled.section`
  height: 100vh;
  min-height: 700px;
  position: relative;
  display: flex;
  align-items: center;
  color: var(--white);
  overflow: hidden;
  background-color: var(--secondary);

  @media (max-width: 768px) {
    min-height: 100vh;
    height: auto;
    padding: 100px 0 80px;
  }
`;

const BackgroundWrapper = styled.div`
  position: absolute;
  top: 0;
  right: 0;
  width: 60%;
  height: 100%;
  clip-path: polygon(15% 0, 100% 0, 100% 100%, 0% 100%);
  overflow: hidden;

  @media (max-width: 1024px) {
    width: 100%;
    clip-path: none;
    opacity: 0.15;
  }
`;

const SlideshowContainer = styled.div`
  width: 100%;
  height: 100%;
  position: relative;
`;

const BackgroundImage = styled.div<{ src: string; active: boolean }>`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: url(${props => props.src}) no-repeat center center/cover;
  transition: opacity 1.5s ease-in-out;
  opacity: ${props => props.active ? 1 : 0};
`;

const OverlayGradient = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    120deg,
    rgba(33, 37, 41, 0.8) 0%,
    rgba(33, 37, 41, 0.6) 50%,
    rgba(33, 37, 41, 0.4) 100%
  );
`;

const ContentContainer = styled.div`
  width: 100%;
  max-width: 1400px;
  margin: 0 auto;
  padding: 0 20px;
  display: flex;
  position: relative;
  z-index: 2;

  @media (max-width: 1024px) {
    flex-direction: column;
    align-items: center;
    text-align: center;
  }

  @media (max-width: 768px) {
    padding: 0 25px;
  }
`;

const Logo = styled.div`
  position: absolute;
  top: 30px;
  left: 50px;
  font-size: 28px;
  font-weight: 700;
  color: var(--white);
  z-index: 10;
  display: flex;
  align-items: center;

  span {
    color: var(--primary);
  }

  @media (max-width: 768px) {
    left: 20px;
    font-size: 24px;
    top: 20px;
  }
`;

const HeroContent = styled.div`
  width: 45%;
  padding-left: 50px;
  position: relative;
  z-index: 1;
  animation: ${fadeIn} 1s ease-out;

  @media (max-width: 1024px) {
    width: 100%;
    padding-left: 0;
    margin-bottom: 40px;
  }

  @media (max-width: 768px) {
    margin-top: 40px;
  }
`;

const MainHeading = styled.h1`
  margin-bottom: 20px;
  font-size: 3.5rem;
  font-weight: 800;
  line-height: 1.2;
  position: relative;

  &::after {
    content: '';
    position: absolute;
    left: 0;
    bottom: -15px;
    width: 80px;
    height: 5px;
    background: var(--primary);
    border-radius: 5px;
  }

  span {
    display: block;
    color: var(--primary);
  }

  @media (max-width: 1024px) {
    font-size: 2.8rem;

    &::after {
