const Hero = () => {
  const [currentImage, setCurrentImage] = useState(0);
  const [isVisible, setIsVisible] = useState(true);
  const slideshowRef = useRef<ReturnType<typeof setInterval> | null>(null);

  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: false,
  });

  useEffect(() => {
    setIsVisible(inView);
  }, [inView]);

  useEffect(() => {
    // Start slideshow
    slideshowRef.current = setInterval(() => {
      setCurrentImage(prev => (prev + 1) % imageUrls.length);
    }, 5000);

    return () => {
      if (slideshowRef.current) {
        clearInterval(slideshowRef.current);
      }
    };
  }, []);

  const scrollToSection = (sectionId: string) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <HeroContainer ref={ref}>
      <Logo>
        Home <span>Vision</span> Solutions
      </Logo>

      <BackgroundWrapper>
        <SlideshowContainer>
          {imageUrls.map((url, index) => (
            <BackgroundImage 
              key={index} 
              src={url} 
              active={index === currentImage} 
            />
          ))}
          <OverlayGradient />
        </SlideshowContainer>
      </BackgroundWrapper>

      <ContentContainer>
        <HeroContent className={isVisible ? 'fade-in' : ''}>
          <MainHeading>
            Your Home, <span>Reimagined</span>
          </MainHeading>

          <SubHeading>
            Transform your living space with cutting-edge smart home automation. 
            Professional design and installation in just 14 days.
          </SubHeading>

          <FeatureBadges>
            <FeatureBadge><span>✦</span> Premium Automation</FeatureBadge>
            <FeatureBadge><span>✦</span> Professional Install</FeatureBadge>
            <FeatureBadge><span>✦</span> 24/7 Support</FeatureBadge>
          </FeatureBadges>

          <ButtonContainer>
            <Button 
              primary 
              onClick={() => scrollToSection('contact')}
              style={{
                fontSize: '1.1rem',
                padding: '15px 30px'
              }}
            >
              Get Your Quote
            </Button>
            <Button 
              outline 
              onClick={() => scrollToSection('packages')}
            >
              Explore Packages
            </Button>
          </ButtonContainer>

          <UrgencyContainer>
            <PulseCircle>5</PulseCircle>
            <UrgencyText>
              <span>Limited availability:</span> Only 5 installation slots left for this month!
            </UrgencyText>
          </UrgencyContainer>
        </HeroContent>
      </ContentContainer>

      <ImageSlider>
        {imageUrls.map((_, index) => (
          <SliderDot 
            key={index} 
            active={index === currentImage}
            onClick={() => setCurrentImage(index)}
          />
        ))}
      </ImageSlider>

      <ScrollIndicator onClick={() => scrollToSection('about')}>
        <ScrollText>Discover More</ScrollText>
        <ScrollArrow />
      </ScrollIndicator>
    </HeroContainer>
  );
};

export default Hero;
