import React, { useEffect, useState, useRef } from 'react';
import styled, { keyframes } from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const HeroContainer = styled.section`
  height: 100vh;
  min-height: 700px;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--white);
  overflow: hidden;

  @media (max-width: 768px) {
    min-height: 100vh;
    height: auto;
  }
`;

const VideoBackground = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 0;

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(
      to right,
      rgba(0, 0, 0, 0.6) 0%,
      rgba(0, 0, 0, 0.4) 100%
    );
    z-index: 1;
  }

  video {
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
  }
`;

const ContentContainer = styled.div`
  width: 90%;
  max-width: 1200px;
  padding: 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  position: relative;
  z-index: 2;

  @media (max-width: 768px) {
    width: 85%;
    padding: 100px 0;
  }
`;

const Logo = styled.div`
  position: absolute;
  top: 40px;
  left: 50px;
  font-size: 28px;
  font-weight: 600;
  color: var(--white);
  z-index: 10;
  display: flex;
  align-items: center;
  letter-spacing: 1px;

  span {
    color: var(--primary);
  }

  @media (max-width: 768px) {
    left: 20px;
    font-size: 24px;
    top: 20px;
  }
`;

const MainHeading = styled.h1`
  margin-bottom: 30px;
  font-size: 5rem;
  font-weight: 200;
  line-height: 1.1;
  letter-spacing: 2px;
  text-transform: uppercase;

  span {
    display: block;
    font-weight: 600;
    color: var(--primary);
    margin-top: 10px;
  }

  @media (max-width: 1200px) {
    font-size: 4rem;
  }

  @media (max-width: 768px) {
    font-size: 3rem;
  }

  @media (max-width: 480px) {
    font-size: 2.5rem;
  }
`;

const Divider = styled.div`
  width: 70px;
  height: 3px;
  background-color: var(--primary);
  margin: 30px auto;
`;

const SubHeading = styled.div`
  font-size: 1.2rem;
  font-weight: 300;
  margin: 0 auto 50px;
  line-height: 1.8;
  max-width: 700px;

  @media (max-width: 768px) {
    font-size: 1.1rem;
  }

  @media (max-width: 480px) {
    font-size: 1rem;
  }
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 20px;
  margin-top: 10px;

  @media (max-width: 480px) {
    flex-direction: column;
    width: 100%;
    gap: 15px;
  }
`;

const ScrollDown = styled.div`
  position: absolute;
  bottom: 40px;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 8px;
  cursor: pointer;
  z-index: 5;
  font-size: 0.9rem;
  letter-spacing: 1.5px;
  text-transform: uppercase;
  opacity: 0.8;

  @media (max-width: 768px) {
    display: none;
  }
`;

const ArrowDown = styled.div`
  width: 30px;
  height: 30px;
  display: flex;
  justify-content: center;

  &::after {
    content: '';
    width: 10px;
    height: 10px;
    border-right: 1px solid var(--white);
    border-bottom: 1px solid var(--white);
    transform: rotate(45deg);
    animation: bounce 2s infinite;
  }

  @keyframes bounce {
    0%, 20%, 50%, 80%, 100% {
      transform: translateY(0) rotate(45deg);
    }
    40% {
      transform: translateY(-10px) rotate(45deg);
    }
    60% {
      transform: translateY(-5px) rotate(45deg);
    }
  }
`;

const Hero = () => {
  const [isVisible, setIsVisible] = useState(true);
  const videoRef = useRef<HTMLVideoElement>(null);

  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: false,
  });

  useEffect(() => {
    setIsVisible(inView);
  }, [inView]);

  // Play video when component mounts
  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.play().catch(error => {
        console.log("Video autoplay failed:", error);
      });
    }
  }, []);

  const scrollToSection = (sectionId: string) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <HeroContainer ref={ref}>
      <Logo>
        Home <span>Vision</span>
      </Logo>

      <VideoBackground>
        <video 
          ref={videoRef}
          autoPlay
          muted
          loop
          playsInline
        >
          <source src="/video2.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      </VideoBackground>

      <ContentContainer>
        <MainHeading className={isVisible ? 'fade-in' : ''}>
          Intelligent Living
          <span>Effortless Luxury</span>
        </MainHeading>

        <Divider />

        <SubHeading>
          Premium smart home integration that elevates your lifestyle.
          Professional design and installation with unparalleled attention to detail.
        </SubHeading>

        <ButtonContainer>
          <Button 
            primary 
            onClick={() => scrollToSection('contact')}
            style={{
              fontSize: '1rem',
              padding: '15px 35px',
              letterSpacing: '1px',
              textTransform: 'uppercase'
            }}
          >
            Request Consultation
          </Button>
          <Button 
            outline 
            onClick={() => scrollToSection('packages')}
            style={{
              letterSpacing: '1px',
              textTransform: 'uppercase'
            }}
          >
            Explore Services
          </Button>
        </ButtonContainer>
      </ContentContainer>

      <ScrollDown onClick={() => scrollToSection('about')}>
        Discover
        <ArrowDown />
      </ScrollDown>
    </HeroContainer>
  );
};

export default Hero;
