import React, { useState } from 'react';
import styled from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

const packages = [
  {
    id: 'bedroom1',
    title: '1 Bedroom Smart Package',
    price: '$17,000',
    image: '/1bed.png',
    features: [
      'Smart lighting (5 zones)',
      'Audio system (4 speakers)',
      '1 smart thermostat',
      '1 video doorbell',
      '1 smart touchscreen',
      '2 Wi-Fi access points',
      'Smart lock',
      'Full installation + network setup',
    ],
    color: '#f8d7bc',
  },
  {
    id: 'bedroom2',
    title: '2 Bedroom Smart Package',
    price: '$24,000',
    image: '/2bed.png',
    features: [
      'Smart lighting (7 zones)',
      'Audio (6 speakers)',
      '2 thermostats',
      'Video doorbell + touchscreen',
      'Smart lock',
      'Control4 automation hub',
      'Full install + tuning',
    ],
    featured: true,
    color: '#CB9D76',
  },
  {
    id: 'bedroom3',
    title: '3 Bedroom Smart Package',
    price: '$33,000',
    image: '/3bed.png',
    features: [
      'Smart lighting (10 zones)',
      'Audio (8–10 speakers)',
      '3 thermostats',
      'Touchscreen x2 + TV control',
      'Video doorbell + smart lock',
      'Full network + access points',
      'Intercom/gate options',
    ],
    color: '#b78a63',
  },
  {
    id: 'custom',
    title: 'Custom Luxury Homes',
    price: 'Starting at $50,000+',
    image: '/cusrombed.png',
    features: [
      'Full panelized lighting',
      'Multi-zone audio/video',
      'In-ceiling speakers',
      'Motorized shades',
      'Rack system & power conditioning',
      'Full-service engineering + install',
    ],
    color: '#8d6c4e',
  },
];

const PackagesSection = styled.section`
  padding: 100px 0;
  background-color: var(--white);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle at 90% 10%, var(--primary) 0%, transparent 30%);
    opacity: 0.03;
    z-index: 0;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
  position: relative;
  z-index: 1;
`;

const SectionHeader = styled.div`
  text-align: center;
  margin-bottom: 60px;

  h2 {
    color: var(--secondary);
    margin-bottom: 15px;
    font-size: 2.5rem;
    position: relative;
    display: inline-block;

    &::after {
      content: '';
      position: absolute;
      bottom: -10px;
      left: 50%;
      transform: translateX(-50%);
      width: 80px;
      height: 3px;
      background: var(--primary);
      border-radius: 3px;
    }
  }

  p {
    font-size: 1.1rem;
    max-width: 700px;
    margin: 0 auto;
    margin-top: 25px;
    color: var(--secondary);
  }
`;

const PackagesTabs = styled.div`
  display: flex;
  justify-content: center;
  margin-bottom: 40px;
  flex-wrap: wrap;
  gap: 10px;
`;

const PackageTab = styled.button<{ active: boolean; color: string }>`
  padding: 12px 25px;
  background: ${props => props.active ? props.color : 'transparent'};
  color: ${props => props.active ? 'white' : 'var(--secondary)'};
  border: 2px solid ${props => props.color};
  border-radius: 30px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  font-size: 0.95rem;

  &:hover {
    background: ${props => props.color};
    color: white;
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
  }
`;

const PackageContent = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  opacity: 0;
  transform: translateY(20px);
  animation: fadeInUp 0.6s forwards;

  @keyframes fadeInUp {
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
`;

const PackageImageWrapper = styled.div`
  position: relative;
  width: 100%;
  max-width: 700px;
  border-radius: 15px;
  overflow: hidden;
  margin-bottom: 40px;
  box-shadow: 0 20px 50px rgba(0, 0, 0, 0.15);

  &::before {
    content: '';
    display: block;
    padding-top: 56.25%; /* 16:9 aspect ratio */
  }

  img {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.5s ease;
  }

  &:hover img {
    transform: scale(1.05);
  }
`;

const PackageInfo = styled.div`
  display: flex;
  max-width: 900px;
  width: 100%;
  margin: 0 auto;
  box-shadow: 0 15px 40px rgba(0, 0, 0, 0.1);
  border-radius: 15px;
  overflow: hidden;

  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const PackageDetails = styled.div<{ color: string }>`
  flex: 1;
  padding: 40px;

  h3 {
    color: var(--secondary);
    font-size: 1.8rem;
    margin-bottom: 10px;
  }

  .price {
    font-size: 2.2rem;
    font-weight: 700;
    color: ${props => props.color};
    margin-bottom: 30px;
    display: block;
  }
`;

const PackageFeatures = styled.div`
  flex: 1;
  background: #f9f9f9;
  padding: 40px;

  h4 {
    font-size: 1.2rem;
    margin-bottom: 20px;
    color: var(--secondary);
    position: relative;
    display: inline-block;

    &::after {
      content: '';
      position: absolute;
      bottom: -8px;
      left: 0;
      width: 40px;
      height: 2px;
      background: var(--primary);
    }
  }

  ul {
    list-style: none;
    padding: 0;

    li {
      padding: 10px 0;
      border-bottom: 1px solid rgba(0, 0, 0, 0.05);
      display: flex;
      align-items: center;

      &:last-child {
        border-bottom: none;
      }

      &::before {
        content: '✓';
        color: var(--primary);
        margin-right: 10px;
        font-weight: bold;
      }
    }
  }
`;

const CTAButton = styled.div`
  margin-top: 40px;
  text-align: center;
`;

const Packages = () => {
  const [activePackage, setActivePackage] = useState(packages[1]);

  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  const scrollToSection = (sectionId: string) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <PackagesSection id="packages" ref={ref}>
      <Container>
        <SectionHeader className={inView ? 'fade-in' : ''}>
          <h2>Choose Your Smart Home Package</h2>
          <p>
            Select the perfect smart home solution for your space. Each package 
            includes professional design, installation, and programming. Custom 
            options available.
          </p>
        </SectionHeader>

        <PackagesTabs>
          {packages.map((pkg) => (
            <PackageTab 
              key={pkg.id}
              active={activePackage.id === pkg.id}
              color={pkg.color}
              onClick={() => setActivePackage(pkg)}
            >
              {pkg.title.split(' ')[0]} {pkg.title.split(' ')[1]}
            </PackageTab>
          ))}
        </PackagesTabs>

        <PackageContent>
          <PackageImageWrapper>
            <img src={activePackage.image} alt={activePackage.title} />
          </PackageImageWrapper>

          <PackageInfo>
            <PackageDetails color={activePackage.color}>
              <h3>{activePackage.title}</h3>
              <span className="price">{activePackage.price}</span>
              <CTAButton>
                <Button 
                  primary 
                  onClick={() => scrollToSection('contact')}
                  style={{ 
                    backgroundColor: activePackage.color, 
                    borderColor: activePackage.color 
                  }}
                >
                  Get Started
                </Button>
              </CTAButton>
            </PackageDetails>

            <PackageFeatures>
              <h4>What's Included</h4>
              <ul>
                {activePackage.features.map((feature, i) => (
                  <li key={i}>{feature}</li>
                ))}
              </ul>
            </PackageFeatures>
          </PackageInfo>
        </PackageContent>
      </Container>
    </PackagesSection>
  );
};

export default Packages;
