import React from 'react';
import styled, { keyframes } from 'styled-components';
import { useInView } from 'react-intersection-observer';

const trustIcons = [
  {
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <circle cx="12" cy="12" r="10"></circle>
        <polyline points="12 6 12 12 16 14"></polyline>
      </svg>
    ),
    title: '10+ Years',
    text: 'of Industry Experience',
  },
  {
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path>
        <circle cx="12" cy="7" r="4"></circle>
      </svg>
    ),
    title: '100+',
    text: 'Smart Homes Delivered',
  },
  {
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
        <polyline points="22 4 12 14.01 9 11.01"></polyline>
      </svg>
    ),
    title: 'Certified',
    text: 'Professional Technicians',
  },
  {
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <polyline points="20 6 9 17 4 12"></polyline>
      </svg>
    ),
    title: 'Full Warranty',
    text: 'on Parts & Labor',
  },
  {
    icon: (
      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
        <path d="M21 10c0 7-9 13-9 13s-9-6-9-13a9 9 0 0 1 18 0z"></path>
        <circle cx="12" cy="10" r="3"></circle>
      </svg>
    ),
    title: 'Local',
    text: 'Based in West Vancouver',
  },
];

const fadeUp = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const TrustSection = styled.section`
  padding: 80px 0;
  background-color: var(--background-light);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    width: 100%;
    height: 1px;
    background: linear-gradient(90deg, transparent, rgba(203, 157, 118, 0.2), transparent);
    top: 0;
    left: 0;
  }

  &::after {
    content: '';
    position: absolute;
    width: 100%;
    height: 1px;
    background: linear-gradient(90deg, transparent, rgba(203, 157, 118, 0.2), transparent);
    bottom: 0;
    left: 0;
  }
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
  position: relative;
`;

const SectionTitle = styled.h2<{ inView: boolean }>`
  font-size: 2rem;
  color: var(--secondary);
  margin-bottom: 50px;
  text-align: center;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '20px'});
  transition: all 0.5s ease-out;
  font-weight: 600;

  span {
    color: var(--primary);
    position: relative;

    &::after {
      content: '';
      position: absolute;
      bottom: 0;
      left: 0;
      width: 100%;
      height: 8px;
      background-color: rgba(203, 157, 118, 0.15);
      z-index: -1;
    }
  }
`;

const TrustGrid = styled.div<{ inView: boolean }>`
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  gap: 30px;
  opacity: ${props => props.inView ? 1 : 0};
  transform: translateY(${props => props.inView ? 0 : '20px'});
  transition: all 0.6s ease-out;
  transition-delay: 0.2s;

  @media (max-width: 992px) {
    grid-template-columns: repeat(3, 1fr);
  }

  @media (max-width: 576px) {
    grid-template-columns: repeat(2, 1fr);
  }
`;

const TrustItem = styled.div<{ delay: number }>`
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  background: white;
  padding: 25px 15px;
  border-radius: 8px;
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.03);
  transition: all 0.3s ease;
  animation: ${fadeUp} 0.5s ease-out forwards;
  animation-delay: ${props => props.delay * 0.1}s;
  border: 1px solid rgba(0, 0, 0, 0.03);

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 15px 35px rgba(0, 0, 0, 0.08);
  }

  svg {
    width: 45px;
    height: 45px;
    color: var(--primary);
    margin-bottom: 15px;
    transition: all 0.3s ease;
  }

  &:hover svg {
    transform: translateY(-3px);
  }

  h3 {
    font-size: 1.3rem;
    font-weight: 600;
    color: var(--secondary);
    margin: 0 0 5px 0;
  }

  p {
    font-size: 0.9rem;
    color: var(--text);
    margin-bottom: 0;
    line-height: 1.5;
  }
`;

const TrustIcons = () => {
  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  return (
    <TrustSection ref={ref}>
      <Container>
        <SectionTitle inView={inView}>
          Why Clients <span>Trust Us</span>
        </SectionTitle>

        <TrustGrid inView={inView}>
          {trustIcons.map((item, index) => (
            <TrustItem 
              key={index}
              delay={index}
            >
              {item.icon}
              <h3>{item.title}</h3>
              <p>{item.text}</p>
            </TrustItem>
          ))}
        </TrustGrid>
      </Container>
    </TrustSection>
  );
};

export default TrustIcons;
