import React, { useState } from 'react';
import styled from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

const packages = [
  {
    id: 'bedroom1',
    title: '1 Bedroom Smart Package',
    price: '$17,000',
    image: '/1bed.png',
    features: [
      'Smart lighting (5 zones)',
      'Audio system (4 speakers)',
      '1 smart thermostat',
      '1 video doorbell',
      '1 smart touchscreen',
      '2 Wi-Fi access points',
      'Smart lock',
      'Full installation + network setup',
    ],
    color: '#f8d7bc',
  },
  {
    id: 'bedroom2',
    title: '2 Bedroom Smart Package',
    price: '$24,000',
    image: '/2bed.png',
    features: [
      'Smart lighting (7 zones)',
      'Audio (6 speakers)',
      '2 thermostats',
      'Video doorbell + touchscreen',
      'Smart lock',
      'Control4 automation hub',
      'Full install + tuning',
    ],
    featured: true,
    color: '#CB9D76',
  },
  {
    id: 'bedroom3',
    title: '3 Bedroom Smart Package',
    price: '$33,000',
    image: '/3bed.png',
    features: [
      'Smart lighting (10 zones)',
      'Audio (8–10 speakers)',
      '3 thermostats',
      'Touchscreen x2 + TV control',
      'Video doorbell + smart lock',
      'Full network + access points',
      'Intercom/gate options',
    ],
    color: '#b78a63',
  },
  {
    id: 'custom',
    title: 'Custom Luxury Homes',
    price: 'Starting at $50,000+',
    image: '/cusrombed.png',
    features: [
      'Full panelized lighting',
      'Multi-zone audio/video',
      'In-ceiling speakers',
      'Motorized shades',
      'Rack system & power conditioning',
      'Full-service engineering + install',
    ],
    color: '#8d6c4e',
  },
];

const PackagesSection = styled.section`
  padding: 100px 0 120px;
  background-color: var(--white);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle at 90% 10%, var(--primary) 0%, transparent 30%);
    opacity: 0.03;
    z-index: 0;
  }
`;

const Container = styled.div`
  max-width: 1400px;
  margin: 0 auto;
  padding: 0 20px;
  position: relative;
  z-index: 1;
`;

const SectionHeader = styled.div`
  text-align: center;
  margin-bottom: 70px;

  h2 {
    color: var(--secondary);
    margin-bottom: 15px;
    font-size: 2.5rem;
    position: relative;
    display: inline-block;

    &::after {
      content: '';
      position: absolute;
      bottom: -10px;
      left: 50%;
      transform: translateX(-50%);
      width: 80px;
      height: 3px;
      background: var(--primary);
      border-radius: 3px;
    }
  }

  p {
    font-size: 1.1rem;
    max-width: 700px;
    margin: 0 auto;
    margin-top: 25px;
    color: var(--secondary);
  }
`;

const PackagesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 35px;

  @media (max-width: 1400px) {
    grid-template-columns: repeat(2, 1fr);
  }

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    max-width: 500px;
    margin: 0 auto;
  }
`;

const PackageCard = styled.div<{ color: string; featured?: boolean }>`
  background-color: white;
  border-radius: 20px;
  overflow: hidden;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.05);
  transition: all 0.4s ease;
  height: 100%;
  display: flex;
  flex-direction: column;
  border: ${props => props.featured ? `2px solid ${props.color}` : '1px solid rgba(0, 0, 0, 0.05)'};
  position: relative;
  z-index: ${props => props.featured ? '2' : '1'};

  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
  }
`;

const PackageImageContainer = styled.div`
  position: relative;
  width: 100%;
  padding-top: 60%; /* Maintain aspect ratio */
  overflow: hidden;

  img {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: contain; /* Show full image */
    transition: transform 0.5s ease;
    background-color: #f9f9f9;
  }

  &:hover img {
    transform: scale(1.03);
  }
`;

const ImageOverlay = styled.div<{ color: string }>`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    to bottom,
    transparent 70%,
    ${props => props.color}20 100%
  );
`;

const PopularTag = styled.div`
  position: absolute;
  top: 20px;
  right: 20px;
  background-color: var(--primary);
  color: var(--white);
  padding: 8px 20px;
  font-size: 0.8rem;
  font-weight: 600;
  border-radius: 30px;
  box-shadow: 0 5px 15px rgba(203, 157, 118, 0.3);
  z-index: 3;
`;

const BestValueTag = styled.div`
  position: absolute;
  top: -12px;
  left: 50%;
  transform: translateX(-50%);
  background-color: var(--primary);
  color: var(--white);
  padding: 8px 25px;
  font-size: 0.8rem;
  font-weight: 600;
  border-radius: 30px;
  box-shadow: 0 5px 15px rgba(203, 157, 118, 0.3);
  z-index: 3;
`;

const PackageContent = styled.div`
  padding: 30px;
  flex: 1;
  display: flex;
  flex-direction: column;
`;

const PackageHeader = styled.div`
  margin-bottom: 25px;
  position: relative;
`;

const PackageTitle = styled.h3`
  color: var(--secondary);
  font-size: 1.5rem;
  margin-bottom: 10px;
`;

const PackagePrice = styled.div<{ color: string }>`
  font-size: 1.8rem;
  font-weight: 700;
  color: ${props => props.color};
`;

const FeaturesContainer = styled.div`
  flex: 1;
  margin-bottom: 25px;
`;

const FeaturesList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;

  li {
    padding: 8px 0;
    border-bottom: 1px solid rgba(0, 0, 0, 0.05);
    display: flex;
    align-items: center;
    font-size: 0.95rem;

    &:last-child {
      border-bottom: none;
    }

    &::before {
      content: '✓';
      color: var(--primary);
      margin-right: 10px;
      font-weight: bold;
      flex-shrink: 0;
    }
  }
`;

const ToggleButton = styled.button<{ isOpen: boolean }>`
  background: none;
  border: none;
  color: var(--primary);
  padding: 5px 0;
  font-size: 0.9rem;
  font-weight: 600;
  cursor: pointer;
  display: flex;
  align-items: center;
  margin-top: 10px;

  &::after {
    content: '';
    display: inline-block;
    width: 0;
    height: 0;
    margin-left: 5px;
    border-left: 5px solid transparent;
    border-right: 5px solid transparent;
    border-top: ${props => props.isOpen ? '5px solid var(--primary)' : 'none'};
    border-bottom: ${props => !props.isOpen ? '5px solid var(--primary)' : 'none'};
    transition: transform 0.2s ease;
  }

  &:hover {
    text-decoration: underline;
  }
`;

const CTAButton = styled.div`
  margin-top: auto;
`;

const HiddenFeatures = styled.div<{ isOpen: boolean }>`
  height: ${props => props.isOpen ? 'auto' : '0'};
  overflow: hidden;
  opacity: ${props => props.isOpen ? '1' : '0'};
  transition: opacity 0.3s ease;
  margin-top: ${props => props.isOpen ? '10px' : '0'};
`;

const Packages = () => {
  const [expandedFeatures, setExpandedFeatures] = useState<{ [key: string]: boolean }>({
    bedroom1: false,
    bedroom2: false,
    bedroom3: false,
    custom: false
  });

  const toggleFeatures = (id: string) => {
    setExpandedFeatures(prev => ({
      ...prev,
      [id]: !prev[id]
    }));
  };

  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  const scrollToSection = (sectionId: string) => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <PackagesSection id="packages" ref={ref}>
      <Container>
        <SectionHeader className={inView ? 'fade-in' : ''}>
          <h2>Choose Your Smart Home Package</h2>
          <p>
            Select the perfect smart home solution for your space. Each package 
            includes professional design, installation, and programming. Custom 
            options available.
          </p>
        </SectionHeader>

        <PackagesGrid>
          {packages.map((pkg, index) => {
            const showAllFeatures = pkg.features.length <= 4 || expandedFeatures[pkg.id];
            const visibleFeatures = showAllFeatures ? pkg.features : pkg.features.slice(0, 3);
            const hiddenFeatures = pkg.features.slice(3);

            return (
              <PackageCard 
                key={pkg.id} 
                color={pkg.color}
                featured={pkg.featured}
                className={inView ? `slide-up delay-${index}` : ''}
              >
                {pkg.featured && (
                  <BestValueTag>BEST VALUE</BestValueTag>
                )}
                <PackageImageContainer>
                  <img src={pkg.image} alt={pkg.title} />
                  <ImageOverlay color={pkg.color} />
                </PackageImageContainer>

                <PackageContent>
                  <PackageHeader>
                    <PackageTitle>{pkg.title}</PackageTitle>
                    <PackagePrice color={pkg.color}>{pkg.price}</PackagePrice>
                  </PackageHeader>

                  <FeaturesContainer>
                    <FeaturesList>
                      {visibleFeatures.map((feature, i) => (
                        <li key={i}>{feature}</li>
                      ))}
                    </FeaturesList>

                    {hiddenFeatures.length > 0 && (
                      <>
                        <HiddenFeatures isOpen={expandedFeatures[pkg.id]}>
                          <FeaturesList>
                            {hiddenFeatures.map((feature, i) => (
                              <li key={i + 3}>{feature}</li>
                            ))}
                          </FeaturesList>
                        </HiddenFeatures>

                        <ToggleButton 
                          isOpen={expandedFeatures[pkg.id]}
                          onClick={() => toggleFeatures(pkg.id)}
                        >
                          {expandedFeatures[pkg.id] ? 'Show Less' : `Show ${hiddenFeatures.length} More`}
                        </ToggleButton>
                      </>
                    )}
                  </FeaturesContainer>

                  <CTAButton>
                    <Button 
                      onClick={() => scrollToSection('contact')}
                      fullWidth
                      style={{ 
                        backgroundColor: pkg.color, 
                        borderColor: pkg.color 
                      }}
                    >
                      Get {pkg.title.split(' ')[0]} Package
                    </Button>
                  </CTAButton>
                </PackageContent>
              </PackageCard>
            );
          })}
        </PackagesGrid>
      </Container>
    </PackagesSection>
  );
};

export default Packages;
