import React, { useEffect, useState, useRef } from 'react';
import styled, { keyframes, createGlobalStyle, css } from 'styled-components';
import Button from './Button';
import { useInView } from 'react-intersection-observer';

// Enhanced animations
const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const fadeInUp = keyframes`
  from {
    opacity: 0;
    transform: translateY(15px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const shimmer = keyframes`
  0% {
    background-position: -468px 0;
  }
  100% {
    background-position: 468px 0;
  }
`;

const float = keyframes`
  0% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
  100% {
    transform: translateY(0px);
  }
`;

const pulse = keyframes`
  0% {
    box-shadow: 0 0 0 0 rgba(203, 157, 118, 0.4);
  }
  70% {
    box-shadow: 0 0 0 15px rgba(203, 157, 118, 0);
  }
  100% {
    box-shadow: 0 0 0 0 rgba(203, 157, 118, 0);
  }
`;

const slideInRight = keyframes`
  from {
    transform: translateX(50px);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
`;

// Global styles for enhanced visuals
const GlobalStyle = createGlobalStyle`
  .hero-video {
    filter: contrast(1.1) brightness(0.7) saturate(0.9);
    will-change: transform;
    transition: all 1.5s ease-in-out;
  }

  .hero-video.in-view {
    filter: contrast(1.1) brightness(0.7) saturate(1);
    transform: scale(1.05);
  }

  @keyframes gradientShift {
    0% {
      background-position: 0% 50%;
    }
    50% {
      background-position: 100% 50%;
    }
    100% {
      background-position: 0% 50%;
    }
  }
`;

const HeroContainer = styled.section`
  position: relative;
  color: #fff;
  display: flex;
  height: 100vh;
  width: 100%;
  background-color: #0a0a0a;
  overflow: hidden;

  @media (max-width: 768px) {
    flex-direction: column-reverse;
  }
`;

const VideoSection = styled.div`
  flex: 0.6;
  position: relative;
  overflow: hidden;
  height: 100vh;
  transform-style: preserve-3d;
  perspective: 1000px;

  @media (max-width: 768px) {
    height: 50vh;
  }
`;

const VideoBackground = styled.div`
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 0;
  overflow: hidden;
  background-color: #0a0a0a;
  transform: translateZ(-50px) scale(1.15);

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(to left, rgba(10, 10, 10, 0.4), rgba(10, 10, 10, 0.8));
    z-index: 1;
  }

  &::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: radial-gradient(circle at 50% 50%, transparent 0%, rgba(10, 10, 10, 0.7) 100%);
    z-index: 2;
    opacity: 0.7;
  }

  video {
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: center;
    opacity: 0.75;
  }
`;

const ParticlesOverlay = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 2;
  pointer-events: none;
  overflow: hidden;
`;

const Particle = styled.div<{ size: number, posX: number, posY: number, speed: number }>`
  position: absolute;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  background: rgba(203, 157, 118, 0.2);
  border-radius: 50%;
  top: ${props => props.posY}%;
  left: ${props => props.posX}%;
  animation: ${float} ${props => props.speed}s ease-in-out infinite;
  opacity: 0.2;
`;

const ContentSection = styled.div`
  flex: 0.4;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0 50px;
  position: relative;
  z-index: 10;
  background-color: #0a0a0a;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle at 30% 50%, rgba(203, 157, 118, 0.05) 0%, rgba(0, 0, 0, 0) 50%);
    z-index: -1;
    animation: ${shimmer} 10s linear infinite;
  }

  @media (max-width: 1200px) {
    padding: 0 30px;
  }

  @media (max-width: 768px) {
    flex: 1;
    padding: 30px 25px;
  }
`;

const ContentInner = styled.div`
  max-width: 440px;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  text-align: left;
  position: relative;
`;

const Logo = styled.div`
  position: absolute;
  top: 40px;
  left: 50px;
  font-size: 24px;
  font-weight: 500;
  color: #fff;
  z-index: 20;
  display: flex;
  align-items: center;
  letter-spacing: 1px;

  span {
    color: rgba(203, 157, 118, 0.9);
    position: relative;

    &::after {
      content: '';
      position: absolute;
      bottom: 0;
      left: 0;
      width: 100%;
      height: 10px;
      background-color: rgba(203, 157, 118, 0.1);
      z-index: -1;
    }
  }

  svg {
    width: 20px;
    height: 20px;
    margin-right: 10px;
    fill: rgba(203, 157, 118, 0.9);
  }
`;

const MainHeading = styled.h1<{ inView: boolean }>`
  font-size: clamp(2.5rem, 5vw, 3.5rem);
  font-weight: 700;
  line-height: 1.2;
  margin-bottom: 25px;
  opacity: 0;
  transform: translateY(30px);
  transition: all 0.8s cubic-bezier(0.215, 0.61, 0.355, 1);
  transition-delay: 0.2s;

  ${props => props.inView && css`
    opacity: 1;
    transform: translateY(0);
  `}

  span {
    background: linear-gradient(135deg, rgba(203, 157, 118, 0.9), rgba(178, 132, 93, 0.9));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    position: relative;
    display: inline-block;

    &::after {
      content: '';
      position: absolute;
      bottom: 5px;
      left: 0;
      width: 100%;
      height: 10px;
      background-color: rgba(203, 157, 118, 0.1);
      z-index: -1;
      border-radius: 10px;
    }
  }
`;

const Subtitle = styled.p<{ inView: boolean }>`
  font-size: 1.1rem;
  line-height: 1.7;
  margin-bottom: 35px;
  color: rgba(255, 255, 255, 0.9);
  opacity: 0;
  transform: translateY(30px);
  transition: all 0.8s cubic-bezier(0.215, 0.61, 0.355, 1);
  transition-delay: 0.4s;

  ${props => props.inView && css`
    opacity: 1;
    transform: translateY(0);
  `}
`;

const ButtonWrapper = styled.div<{ inView: boolean }>`
  display: flex;
  gap: 15px;
  opacity: 0;
  transform: translateY(30px);
  transition: all 0.8s cubic-bezier(0.215, 0.61, 0.355, 1);
  transition-delay: 0.6s;

  ${props => props.inView && css`
    opacity: 1;
    transform: translateY(0);
  `}
`;

const PrimaryButton = styled(Button)`
  padding: 14px 28px;
  background: linear-gradient(135deg, rgba(203, 157, 118, 0.9), rgba(178, 132, 93, 0.9));
  color: #fff;
  border-radius: 12px;
  border: none;
  font-weight: 600;
  letter-spacing: 0.5px;
  text-transform: uppercase;
  font-size: 0.9rem;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);

