import React, { useState, useEffect, useRef } from 'react';
import styled, { keyframes, css } from 'styled-components';
import Button from './Button';

// Animations
const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
`;

const pulse = keyframes`
  0% { transform: scale(1); }
  50% { transform: scale(1.05); }
  100% { transform: scale(1); }
`;

const float = keyframes`
  0% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
  100% { transform: translateY(0px); }
`;

const glow = keyframes`
  0% { box-shadow: 0 0 5px rgba(65, 154, 255, 0.5); }
  50% { box-shadow: 0 0 20px rgba(65, 154, 255, 0.8); }
  100% { box-shadow: 0 0 5px rgba(65, 154, 255, 0.5); }
`;

// Styled Components
const Section = styled.section`
  padding: 120px 0;
  background: linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%);
  position: relative;
  overflow: hidden;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
  position: relative;
  z-index: 2;
`;

const SectionHeader = styled.div`
  text-align: center;
  margin-bottom: 60px;
`;

const SectionTitle = styled.h2`
  font-size: 2.8rem;
  font-weight: 700;
  color: var(--primary);
  margin-bottom: 20px;

  span {
    color: var(--accent);
    position: relative;

    &::after {
      content: '';
      position: absolute;
      bottom: -5px;
      left: 0;
      width: 100%;
      height: 3px;
      background: var(--accent);
      border-radius: 3px;
    }
  }
`;

const SectionDescription = styled.p`
  font-size: 1.2rem;
  color: var(--text);
  max-width: 700px;
  margin: 0 auto;
  line-height: 1.6;
`;

const GameContainer = styled.div`
  background: white;
  border-radius: 20px;
  box-shadow: 0 15px 50px rgba(0, 0, 0, 0.1);
  padding: 40px;
  margin-top: 40px;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: url('/home-automation.jpg') center/cover no-repeat;
    opacity: 0.05;
    z-index: 0;
  }
`;

const GameHeader = styled.div`
  text-align: center;
  margin-bottom: 30px;
  position: relative;
  z-index: 1;
`;

const GameTitle = styled.h3`
  font-size: 2rem;
  font-weight: 700;
  color: var(--primary);
  margin-bottom: 15px;
`;

const GameDescription = styled.p`
  font-size: 1.1rem;
  color: var(--text);
  max-width: 700px;
  margin: 0 auto;
`;

const ProgressContainer = styled.div`
  margin: 30px 0;
  position: relative;
  z-index: 1;
`;

const ProgressBar = styled.div`
  height: 10px;
  background: #e9ecef;
  border-radius: 10px;
  overflow: hidden;
  margin-bottom: 15px;
`;

const ProgressFill = styled.div<{ progress: number }>`
  height: 100%;
  width: ${props => props.progress}%;
  background: linear-gradient(90deg, var(--primary) 0%, var(--accent) 100%);
  border-radius: 10px;
  transition: width 0.5s ease;
`;

const ProgressStats = styled.div`
  display: flex;
  justify-content: space-between;
  color: var(--text);
  font-size: 1rem;
  font-weight: 500;
`;

const GameSteps = styled.div`
  position: relative;
  z-index: 1;
`;

const StepTabs = styled.div`
  display: flex;
  border-bottom: 2px solid #e9ecef;
  margin-bottom: 30px;
`;

const StepTab = styled.button<{ active: boolean }>`
  padding: 15px 20px;
  background: transparent;
  border: none;
  font-size: 1.1rem;
  font-weight: 600;
  color: ${props => props.active ? 'var(--primary)' : 'var(--text)'};
  cursor: pointer;
  position: relative;
  transition: all 0.3s ease;

  &::after {
    content: '';
    position: absolute;
    bottom: -2px;
    left: 0;
    width: 100%;
    height: 3px;
    background: ${props => props.active ? 'var(--primary)' : 'transparent'};
    transition: all 0.3s ease;
  }

  &:hover {
    color: var(--primary);
  }
`;

const StepContent = styled.div`
  min-height: 400px;
`;

const DeviceGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  gap: 25px;
  margin-bottom: 30px;
`;

const DeviceCard = styled.div<{ selected: boolean }>`
  background: ${props => props.selected ? 'rgba(65, 154, 255, 0.1)' : 'white'};
  border: 2px solid ${props => props.selected ? 'var(--primary)' : '#e9ecef'};
  border-radius: 15px;
  padding: 20px;
  text-align: center;
  cursor: pointer;
  transition: all 0.3s ease;
  position: relative;

  ${props => props.selected && css`
    animation: ${glow} 2s infinite;
  `}

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.05);
  }
`;

const DeviceIcon = styled.div`
  width: 60px;
  height: 60px;
  background: rgba(65, 154, 255, 0.1);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto 15px;
  font-size: 1.8rem;
  color: var(--primary);
`;

const DeviceName = styled.h4`
  font-size: 1.2rem;
  font-weight: 600;
  margin-bottom: 10px;
  color: var(--primary);
`;

const DeviceDescription = styled.p`
  font-size: 0.9rem;
  color: var(--text);
  margin-bottom: 15px;
`;

const DeviceCost = styled.span`
  display: inline-block;
  padding: 5px 10px;
  background: rgba(65, 154, 255, 0.1);
  color: var(--primary);
  font-weight: 600;
  border-radius: 20px;
  font-size: 0.9rem;
`;

const SelectedBadge = styled.div`
  position: absolute;
  top: -10px;
  right: -10px;
  background: var(--primary);
  color: white;
  border-radius: 50%;
  width: 30px;
  height: 30px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1rem;
  font-weight: bold;
`;

const SmartHomeDisplay = styled.div`
  background: white;
  border-radius: 15px;
  padding: 30px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.05);
  text-align: center;
  margin-top: 50px;
`;

const HomeImage = styled.div`
  position: relative;
  width: 100%;
  height: 300px;
  background: url('/home-automation.jpg') center/cover no-repeat;
  border-radius: 10px;
  margin-bottom: 20px;
  overflow: hidden;
`;

const DeviceMarker = styled.div<{ top: string; left: string }>`
  position: absolute;
  top: ${props => props.top};
  left: ${props => props.left};
  width: 20px;
  height: 20px;
  background: var(--primary);
  border-radius: 50%;
  border: 3px solid white;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.3);
  cursor: pointer;
  z-index: 5;
  animation: ${pulse} 2s infinite;
  transition: transform 0.3s ease;

  &:hover {
    transform: scale(1.2);
  }
`;

const DeviceTooltip = styled.div<{ visible: boolean }>`
  position: absolute;
  background: white;
  padding: 10px 15px;
  border-radius: 10px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
  z-index: 6;
  text-align: left;
  min-width: 200px;
  opacity: ${props => props.visible ? 1 : 0};
  visibility: ${props => props.visible ? 'visible' : 'hidden'};
  transition: all 0.3s ease;
  transform: ${props => props.visible ? 'translateY(0)' : 'translateY(10px)'};

  h4 {
    font-size: 1rem;
    font-weight: 600;
    color: var(--primary);
    margin-bottom: 5px;
  }

  p {
    font-size: 0.9rem;
    color: var(--text);
    margin: 0;
  }
`;

const HomeInfo = styled.div`
  display: flex;
  justify-content: space-around;
  margin-bottom: 20px;
`;

const InfoItem = styled.div`
  text-align: center;
`;

const InfoValue = styled.div`
  font-size: 1.8rem;
  font-weight: 700;
  color: var(--primary);
`;

const InfoLabel = styled.div`
  font-size: 0.9rem;
  color: var(--text);
`;

const ResultsContainer = styled.div`
  text-align: center;
  margin-top: 50px;
`;

const ResultTitle = styled.h3`
  font-size: 2rem;
  font-weight: 700;
  color: var(--primary);
  margin-bottom: 20px;
  animation: ${fadeIn} 0.5s ease;
`;

const ResultDescription = styled.p`
  font-size: 1.1rem;
  color: var(--text);
  max-width: 700px;
  margin: 0 auto 30px;
  animation: ${fadeIn} 0.5s ease 0.2s forwards;
  opacity: 0;
`;

const ActionButtons = styled.div`
  display: flex;
  justify-content: center;
  gap: 20px;
  margin-top: 30px;

  ${Button} {
    min-width: 180px;
  }
`;

const ResetButton = styled.button`
  background: transparent;
  border: 2px solid var(--primary);
  color: var(--primary);
  padding: 12px 24px;
  font-size: 1rem;
  font-weight: 600;
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: rgba(65, 154, 255, 0.1);
  }
`;

const devices = [
  {
    id: 'lighting',
    name: 'Smart Lighting',
    icon: 'üí°',
    description: 'Control lights from anywhere and set automated schedules',
    cost: 50,
    points: 15,
    markerPosition: { top: '25%', left: '40%' }
  },
  {
    id: 'thermostat',
    name: 'Smart Thermostat',
    icon: 'üå°Ô∏è',
    description: 'Optimize temperature and save on energy costs',
    cost: 150,
    points: 25,
    markerPosition: { top: '50%', left: '20%' }
  },
  {
    id: 'security',
    name: 'Security System',
    icon: 'üîí',
    description: 'Monitor your home and get real-time alerts',
    cost: 200,
    points: 30,
    markerPosition: { top: '70%', left: '60%' }
  },
  {
    id: 'speakers',
    name: 'Smart Speakers',
    icon: 'üîä',
    description: 'Voice control and multi-room audio',
    cost: 100,
    points: 20,
    markerPosition: { top: '40%', left: '70%' }
  },
  {
    id: 'doorlock',
    name: 'Smart Door Lock',
    icon: 'üîë',
    description: 'Keyless entry and remote access control',
    cost: 150,
    points: 25,
    markerPosition: { top: '60%', left: '35%' }
  },
  {
    id: 'blinds',
    name: 'Motorized Blinds',
    icon: 'ü™ü',
    description: 'Automated window treatments for convenience and energy efficiency',
    cost: 120,
    points: 20,
    markerPosition: { top: '30%', left: '55%' }
  },
  {
    id: 'cameras',
    name: 'Smart Cameras',
    icon: 'üìπ',
    description: 'Indoor and outdoor monitoring with intelligent alerts',
    cost: 180,
    points: 25,
    markerPosition: { top: '20%', left: '75%' }
  },
  {
    id: 'hub',
    name: 'Smart Hub',
    icon: 'üì°',
    description: 'Central control system for all your smart devices',
    cost: 120,
    points: 30,
    markerPosition: { top: '45%', left: '45%' }
  }
];

const HomeAutomation: React.FC = () => {
  const [currentStep, setCurrentStep] = useState('select');
  const [selectedDevices, setSelectedDevices] = useState<string[]>([]);
  const [budget, setBudget] = useState(500);
  const [activeMarker, setActiveMarker] = useState<string | null>(null);
  const [gameCompleted, setGameCompleted] = useState(false);
  const [energySavings, setEnergySavings] = useState(0);
  const [convenienceScore, setConvenienceScore] = useState(0);

  const progress = Math.min((selectedDevices.length / 5) * 100, 100);

  useEffect(() => {
    // Calculate metrics based on selected devices
    let energy = 0;
    let convenience = 0;

    selectedDevices.forEach(id => {
      const device = devices.find(d => d.id === id);
      if (device) {
        energy += device.id === 'thermostat' ? 30 : (device.id === 'lighting' ? 20 : 10);
        convenience += device.points;
      }
    });

    setEnergySavings(energy);
    setConvenienceScore(convenience);

    // Check if game is completed
    if (selectedDevices.length >= 3 && budget >= 0) {
      setGameCompleted(true);
    } else {
      setGameCompleted(false);
    }
  }, [selectedDevices, budget]);

  const toggleDevice = (id: string) => {
    const device = devices.find(d => d.id === id);
    if (!device) return;

    if (selectedDevices.includes(id)) {
      setSelectedDevices(selectedDevices.filter(deviceId => deviceId !== id));
      setBudget(prev => prev + device.cost);
    } else {
      if (budget >= device.cost) {
        setSelectedDevices([...selectedDevices, id]);
        setBudget(prev => prev - device.cost);
      } else {
        alert("You've exceeded your budget!");
      }
    }
  };

  const resetGame = () => {
    setSelectedDevices([]);
    setBudget(500);
    setCurrentStep('select');
    setGameCompleted(false);
  };

  return (
    <Section id="home-automation">
      <Container>
        <SectionHeader>
          <SectionTitle>Smart Home <span>Automation</span></SectionTitle>
          <SectionDescription>
            Transform your home with intelligent automation that enhances comfort, convenience, and security.
            Build your perfect smart home system with our interactive tool.
          </SectionDescription>
        </SectionHeader>

        <GameContainer>
          <GameHeader>
            <GameTitle>Build Your Smart Home Challenge</GameTitle>
            <GameDescription>
              You have a budget of $500. Select the smart devices that would create the most value
              for your home and see the impact they would make.
            </GameDescription>
          </GameHeader>

          <ProgressContainer>
            <ProgressBar>
              <ProgressFill progress={progress} />
            </ProgressBar>
            <ProgressStats>
              <div>Smart Home Progress: {Math.round(progress)}%</div>
              <div>Budget Remaining: ${budget}</div>
            </ProgressStats>
          </ProgressContainer>

          <GameSteps>
            <StepTabs>
              <StepTab 
                active={currentStep === 'select'} 
                onClick={() => setCurrentStep('select')}
              >
                1. Select Devices
              </StepTab>
              <StepTab 
                active={currentStep === 'visualize'} 
                onClick={() => setCurrentStep('visualize')}
              >
                2. Visualize Your Smart Home
              </StepTab>
              <StepTab 
                active={currentStep === 'results'}
                onClick={() => gameCompleted && setCurrentStep('results')}
                disabled={!gameCompleted}
              >
                3. See Results
              </StepTab>
            </StepTabs>

            <StepContent>
              {currentStep === 'select' && (
                <DeviceGrid>
                  {devices.map(device => (
                    <DeviceCard 
                      key={device.id} 
                      selected={selectedDevices.includes(device.id)}
                      onClick={() => toggleDevice(device.id)}
                    >
                      {selectedDevices.includes(device.id) && <SelectedBadge>‚úì</SelectedBadge>}
                      <DeviceIcon>{device.icon}</DeviceIcon>
                      <DeviceName>{device.name}</DeviceName>
                      <DeviceDescription>{device.description}</DeviceDescription>
                      <DeviceCost>${device.cost}</DeviceCost>
                    </DeviceCard>
                  ))}
                </DeviceGrid>
              )}

              {currentStep === 'visualize' && (
                <SmartHomeDisplay>
                  <HomeImage>
                    {selectedDevices.map(id => {
                      const device = devices.find(d => d.id === id);
                      if (!device) return null;

                      return (
                        <React.Fragment key={id}>
                          <DeviceMarker 
                            top={device.markerPosition.top} 
                            left={device.markerPosition.left}
                            onMouseEnter={() => setActiveMarker(id)}
                            onMouseLeave={() => setActiveMarker(null)}
                          />
                          <DeviceTooltip 
                            visible={activeMarker === id}
                            style={{
                              top: `calc(${device.markerPosition.top} + 20px)`,
                              left: `calc(${device.markerPosition.left} + 20px)`
                            }}
                          >
                            <h4>{device.name}</h4>
                            <p>{device.description}</p>
                          </DeviceTooltip>
                        </React.Fragment>
                      );
                    })}
                  </HomeImage>

                  <HomeInfo>
                    <InfoItem>
                      <InfoValue>{selectedDevices.length}</InfoValue>
                      <InfoLabel>Smart Devices</InfoLabel>
                    </InfoItem>
                    <InfoItem>
                      <InfoValue>{energySavings}%</InfoValue>
                      <InfoLabel>Energy Savings</InfoLabel>
                    </InfoItem>
                    <InfoItem>
                      <InfoValue>{convenienceScore}</InfoValue>
                      <InfoLabel>Convenience Score</InfoLabel>
                    </InfoItem>
                  </HomeInfo>

                  {!gameCompleted && selectedDevices.length > 0 && (
                    <p>Add at least {Math.max(3 - selectedDevices.length, 0)} more devices to complete your smart home system.</p>
                  )}

                  {gameCompleted && (
                    <Button onClick={() => setCurrentStep('results')}>
                      View Your Results
                    </Button>
                  )}
                </SmartHomeDisplay>
              )}

              {currentStep === 'results' && (
                <ResultsContainer>
                  <ResultTitle>Your Smart Home Blueprint</ResultTitle>
                  <ResultDescription>
                    Congratulations! You've created a smart home system that will save approximately 
                    {energySavings}% on energy costs and provide a convenience score of {convenienceScore}.
                    Here's what your smart home system includes:
                  </ResultDescription>

                  <DeviceGrid>
                    {selectedDevices.map(id => {
                      const device = devices.find(d => d.id === id);
                      if (!device) return null;

                      return (
                        <DeviceCard key={device.id} selected={true}>
                          <DeviceIcon>{device.icon}</DeviceIcon>
                          <DeviceName>{device.name}</DeviceName>
                          <DeviceDescription>{device.description}</DeviceDescription>
                        </DeviceCard>
                      );
                    })}
                  </DeviceGrid>

                  <ActionButtons>
                    <Button>Request a Custom Quote</Button>
                    <ResetButton onClick={resetGame}>Build Another System</ResetButton>
                  </ActionButtons>
                </ResultsContainer>
              )}
            </StepContent>
          </GameSteps>

          {currentStep !== 'results' && (
            <ActionButtons>
              {currentStep === 'select' && selectedDevices.length > 0 && (
                <Button onClick={() => setCurrentStep('visualize')}>
                  Visualize Your Smart Home
                </Button>
              )}

              {currentStep === 'visualize' && (
                <>
                  <Button onClick={() => setCurrentStep('select')}>
                    Back to Device Selection
                  </Button>
                  {gameCompleted && (
                    <Button onClick={() => setCurrentStep('results')}>
                      See Your Results
                    </Button>
                  )}
                </>
              )}
            </ActionButtons>
          )}
        </GameContainer>
      </Container>
    </Section>
  );
};

export default HomeAutomation;
