import React, { useEffect } from 'react';
import styled, { keyframes, css } from 'styled-components';
import { useInView } from 'react-intersection-observer';

// Modern team image
const teamImageUrl = 'https://images.unsplash.com/photo-1600880292203-757bb62b4baf?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1770&q=80';

// Animations
const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const shimmer = keyframes`
  0% {
    background-position: -468px 0;
  }
  100% {
    background-position: 468px 0;
  }
`;

const reveal = keyframes`
  from {
    transform: scaleX(0);
  }
  to {
    transform: scaleX(1);
  }
`;

const float = keyframes`
  0% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
  100% {
    transform: translateY(0px);
  }
`;

const outlineReveal = keyframes`
  0% {
    opacity: 0;
    transform: translate(20px, -20px);
  }
  100% {
    opacity: 1;
    transform: translate(0, 0);
  }
`;

// Main section container
const AboutSection = styled.section`
  padding: 120px 0;
  background-color: var(--white);
  color: var(--secondary);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    width: 500px;
    height: 500px;
    background: radial-gradient(circle, rgba(203, 157, 118, 0.1) 0%, rgba(255, 255, 255, 0) 70%);
    top: -200px;
    left: -200px;
    z-index: 0;
  }

  &::after {
    content: '';
    position: absolute;
    width: 400px;
    height: 400px;
    background: radial-gradient(circle, rgba(203, 157, 118, 0.08) 0%, rgba(255, 255, 255, 0) 70%);
    bottom: -150px;
    right: -150px;
    z-index: 0;
  }
`;

// Container for content alignment
const AboutContainer = styled.div`
  max-width: 1300px;
  margin: 0 auto;
  padding: 0 50px;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 80px;
  align-items: center;
  position: relative;
  z-index: 1;

  @media (max-width: 992px) {
    grid-template-columns: 1fr;
    gap: 60px;
    padding: 0 30px;
  }
`;

// Section title with animated underline
const SectionTitle = styled.h2`
  font-size: 0.9rem;
  text-transform: uppercase;
  letter-spacing: 3px;
  color: var(--primary);
  margin-bottom: 25px;
  position: relative;
  display: inline-block;
  font-weight: 600;

  &::after {
    content: '';
    position: absolute;
    bottom: -8px;
    left: 0;
    width: 40px;
    height: 2px;
    background-color: var(--primary);
    transform-origin: left;
    animation: ${reveal} 1.5s cubic-bezier(0.25, 0.46, 0.45, 0.94) forwards;
  }
`;

// Content container with staggered animation for children
const AboutContent = styled.div<{ inView: boolean }>`
  flex: 1;
  color: var(--secondary);

  h2 {
    font-size: 2.5rem;
    margin-bottom: 20px;
    opacity: 0;
    transform: translateY(20px);
    transition: all 0.7s cubic-bezier(0.215, 0.61, 0.355, 1);
    font-weight: 600;

    ${props => props.inView && css`
      opacity: 1;
      transform: translateY(0);
    `}

    span {
      color: var(--primary);
      position: relative;

      &::after {
        content: '';
        position: absolute;
        bottom: 2px;
        left: 0;
        width: 100%;
        height: 8px;
        background-color: rgba(203, 157, 118, 0.15);
        z-index: -1;
        transform-origin: left;
        animation: ${props => props.inView ? css`${reveal} 1.5s cubic-bezier(0.25, 0.46, 0.45, 0.94) forwards` : 'none'};
        animation-delay: 0.3s;
      }
    }
  }

  p {
    margin-bottom: 30px;
    line-height: 1.8;
    color: var(--text);
    opacity: 0;
    transform: translateY(20px);
    transition: all 0.7s cubic-bezier(0.215, 0.61, 0.355, 1);
    transition-delay: 0.2s;
    font-size: 1.05rem;

    ${props => props.inView && css`
      opacity: 1;
      transform: translateY(0);
    `}
  }
`;

// Image container with 3D depth effect
const AboutImage = styled.div<{ inView: boolean }>`
  flex: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
  transform: translateY(30px);
  opacity: 0;
  transition: all 0.8s cubic-bezier(0.215, 0.61, 0.355, 1);
  transition-delay: 0.3s;
  filter: grayscale(0%);

  ${props => props.inView && css`
    opacity: 1;
    transform: translateY(0);
  `}

  img {
    width: 100%;
    max-width: 500px;
    height: auto;
    border-radius: 5px;
    object-fit: cover;
    transition: all 0.5s ease;
    box-shadow: 0 15px 35px rgba(0, 0, 0, 0.1);

    &:hover {
      transform: perspective(1000px) rotateY(-5deg) rotateX(5deg) scale(1.02);
      box-shadow: 20px 20px 60px rgba(0, 0, 0, 0.1);
    }
  }

  &::before {
    content: '';
    position: absolute;
