import React from 'react';
import styled from 'styled-components';
import { useInView } from 'react-intersection-observer';

// Placeholder logos - replace with actual brand logos
const logoUrls = [
  'https://via.placeholder.com/150x50?text=Control4',
  'https://via.placeholder.com/150x50?text=Triad',
  'https://via.placeholder.com/150x50?text=Sonos',
  'https://via.placeholder.com/150x50?text=Lutron',
  'https://via.placeholder.com/150x50?text=Araknis',
  'https://via.placeholder.com/150x50?text=Samsung',
];

const LogosSection = styled.section`
  padding: 80px 0;
  background-color: var(--light-gray);
  text-align: center;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const SectionTitle = styled.h3`
  color: var(--secondary);
  margin-bottom: 40px;
  position: relative;
  display: inline-block;

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 50%;
    transform: translateX(-50%);
    width: 60px;
    height: 3px;
    background-color: var(--primary);
  }
`;

const LogosGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(6, 1fr);
  gap: 30px;
  align-items: center;
  justify-items: center;

  @media (max-width: 992px) {
    grid-template-columns: repeat(3, 1fr);
  }

  @media (max-width: 576px) {
    grid-template-columns: repeat(2, 1fr);
  }
`;

const Logo = styled.div`
  transition: var(--transition);
  filter: grayscale(100%);
  opacity: 0.6;

  &:hover {
    filter: grayscale(0);
    opacity: 1;
  }

  img {
    max-width: 100%;
    height: auto;
  }
`;

const BrandLogos = () => {
  const { ref, inView } = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  return (
    <LogosSection ref={ref}>
      <Container>
        <SectionTitle className={inView ? 'fade-in' : ''}>
          We Work With the Best
        </SectionTitle>

        <LogosGrid>
          {logoUrls.map((url, index) => (
            <Logo 
              key={index} 
              className={inView ? `fade-in delay-${index % 3}` : ''}
            >
              <img src={url} alt={`Partner brand ${index + 1}`} />
            </Logo>
          ))}
        </LogosGrid>
      </Container>
    </LogosSection>
  );
};

export default BrandLogos;
