import React from 'react';
import styled from 'styled-components';
import { useInView } from 'react-intersection-observer';

// Placeholder image for the about section
const teamImageUrl = 'https://images.unsplash.com/photo-1600880292203-757bb62b4baf?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1770&q=80';

const AboutSection = styled.section`
  padding: 100px 0;
  background-color: var(--white);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    width: 300px;
    height: 300px;
    background: var(--primary);
    opacity: 0.05;
    border-radius: 50%;
    top: -100px;
    left: -100px;
    z-index: 0;
  }

  &::after {
    content: '';
    position: absolute;
    width: 200px;
    height: 200px;
    background: var(--secondary);
    opacity: 0.05;
    border-radius: 50%;
    bottom: -50px;
    right: -50px;
    z-index: 0;
  }
`;

const AboutContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 60px;
  align-items: center;
  position: relative;
  z-index: 1;

  @media (max-width: 992px) {
    grid-template-columns: 1fr;
    gap: 40px;
  }
`;

const AboutContent = styled.div`
  h2 {
    margin-bottom: 30px;
    position: relative;
    display: inline-block;

    &::after {
      content: '';
      position: absolute;
      bottom: -10px;
      left: 0;
      width: 80px;
      height: 3px;
      background-color: var(--primary);
    }
  }

  p {
    margin-bottom: 25px;
    font-size: 1.1rem;
    line-height: 1.8;
    color: var(--secondary);
  }
`;

const AboutImage = styled.div`
  position: relative;
  height: 450px;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    border-radius: 10px;
    box-shadow: var(--box-shadow);
  }

  &::before {
    content: '';
    position: absolute;
    top: 20px;
    left: -20px;
    width: 100%;
    height: 100%;
    border: 3px solid var(--primary);
    border-radius: 10px;
    z-index: -1;
  }

  @media (max-width: 992px) {
    height: 400px;
  }

  @media (max-width: 576px) {
    height: 300px;
  }
`;

const FeaturesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 20px;
  margin-top: 30px;

  @media (max-width: 576px) {
    grid-template-columns: 1fr;
  }
`;

const Feature = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;

  h4 {
    margin: 15px 0 8px;
    color: var(--secondary);
    font-family: 'Montserrat', sans-serif;
    font-weight: 600;
  }

  p {
    font-size: 0.9rem;
    margin-bottom: 0;
  }

  svg {
    color: var(--primary);
    font-size: 28px;
  }
`;

const About = () => {
  const [ref, inView] = useInView({
    threshold: 0.1,
    triggerOnce: true,
  });

  return (
    <AboutSection id="about" ref={ref}>
      <AboutContainer>
        <AboutContent className={inView ? 'slide-up' : ''}>
          <h2>
            Trusted. Local. Experts.
          </h2>
          <p>
            Home Vision Solutions is Vancouver's go-to smart home integration team. 
            With 10+ years of experience, we design, install, and support custom automation 
            systems for homes, condos, and luxury estates.
          </p>
          <p>
            We're not box movers — we're experience creators. Our certified technicians 
            bring professional knowledge and white-glove service to every project, 
            ensuring your smart home vision is executed flawlessly.
          </p>

          <FeaturesGrid>
            <Feature>
              <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
                <polyline points="22 4 12 14.01 9 11.01"></polyline>
              </svg>
              <h4>Certified Control4 Integrators</h4>
              <p>Official partner with industry-leading brands</p>
            </Feature>

            <Feature>
              <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <circle cx="12" cy="12" r="3"></circle>
                <path d="M19.4 15a1.65 1.65 0 0 0 .33 1.82l.06.06a2 2 0 0 1 0 2.83 2 2 0 0 1-2.83 0l-.06-.06a1.65 1.65 0 0 0-1.82-.33 1.65 1.65 0 0 0-1 1.51V21a2 2 0 0 1-2 2 2 2 0 0 1-2-2v-.09A1.65 1.65 0 0 0 9 19.4a1.65 1.65 0 0 0-1.82.33l-.06.06a2 2 0 0 1-2.83 0 2 2 0 0 1 0-2.83l.06-.06a1.65 1.65 0 0 0 .33-1.82 1.65 1.65 0 0 0-1.51-1H3a2 2 0 0 1-2-2 2 2 0 0 1 2-2h.09A1.65 1.65 0 0 0 4.6 9a1.65 1.65 0 0 0-.33-1.82l-.06-.06a2 2 0 0 1 0-2.83 2 2 0 0 1 2.83 0l.06.06a1.65 1.65 0 0 0 1.82.33H9a1.65 1.65 0 0 0 1-1.51V3a2 2 0 0 1 2-2 2 2 0 0 1 2 2v.09a1.65 1.65 0 0 0 1 1.51 1.65 1.65 0 0 0 1.82-.33l.06-.06a2 2 0 0 1 2.83 0 2 2 0 0 1 0 2.83l-.06.06a1.65 1.65 0 0 0-.33 1.82V9a1.65 1.65 0 0 0 1.51 1H21a2 2 0 0 1 2 2 2 2 0 0 1-2 2h-.09a1.65 1.65 0 0 0-1.51 1z"></path>
              </svg>
              <h4>White-glove Installation</h4>
              <p>Professional, clean, and efficient service</p>
            </Feature>

            <Feature>
              <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
                <polygon points="12 2 15.09 8.26 22 9.27 17 14.14 18.18 21.02 12 17.77 5.82 21.02 7 14.14 2 9.27 8.91 8.26 12 2"></polygon>
              </svg>
              <h4>Smart Made Simple</h4>
              <p>Technology that works for you, not against you</p>
            </Feature>
          </FeaturesGrid>
        </AboutContent>

        <AboutImage className={inView ? 'slide-up delay-1' : ''}>
          <img src={teamImageUrl} alt="Home Vision Solutions team" />
        </AboutImage>
      </AboutContainer>
    </AboutSection>
  );
};

export default About;
